import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';
import './TestPage.css';

function TestPage() {
  const [mapUrl, setMapUrl] = useState(null);
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [selectedTask, setSelectedTask] = useState('');
  const [availableTasks, setAvailableTasks] = useState(['Task 1', 'Task 2', 'Task 3']);
  const canvasRef = useRef(null);
  const [mapResolution, setMapResolution] = useState(0.05);
  const [mapDimensions, setMapDimensions] = useState({ width: 0, height: 0 });
  const [expandedRobotId, setExpandedRobotId] = useState(null);

  const toggleExpand = (robotId) => {
    if (expandedRobotId === robotId) {
      setExpandedRobotId(null); // 로봇을 축소
    } else {
      setExpandedRobotId(robotId); // 로봇을 확장
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      await fetchMapMetadata();
      await fetchMonitoredMap();
      fetchRobots();
      fetchAllTasks();
    };
    fetchData();

    const socket = new WebSocket('ws://your-websocket-url');
    socket.onopen = () => {
      console.log('WebSocket 연결 성공');
    };
    socket.onmessage = (event) => {
      const updatedRobots = JSON.parse(event.data);
      setRobots(updatedRobots);
    };
    socket.onerror = (error) => {
      console.error('WebSocket 오류:', error);
    };
    socket.onclose = () => {
      console.log('WebSocket 연결 종료');
    };

    return () => {
      socket.close();
    };
  }, []);

  return (
    <div className="test-page">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <h1></h1>
      <div className="main-content">
        <div className="map-canvas-container">
          {mapUrl ? (
            <canvas ref={canvasRef} className="canvas"></canvas>
          ) : (
            <p>지도를 불러오는 중...</p>
          )}
        </div>

        <div className="robot-list">
          <h3>로봇 리스트</h3>
          <ul>
            {robots.length > 0 ? (
              robots.map((robot) => (
                <li key={robot._id} className="robot-item">
                  <div className="robot-item-header">
                    <span><strong>이름:</strong> {robot.name}</span>
                    <span><strong>상태:</strong> {robot.status?.state || 'Unknown'}</span>
                    <span><strong>배터리:</strong> {robot.status?.battery || 'N/A'}%</span>
                    <button
                      onClick={() => toggleExpand(robot._id)}
                      className="expand-btn"
                      aria-label={expandedRobotId === robot._id ? '접기' : '펼치기'}
                    >
                      {expandedRobotId === robot._id ? <FaChevronUp /> : <FaChevronDown />}
                    </button>
                  </div>
                  <div className={`robot-details ${expandedRobotId === robot._id ? 'expanded' : ''}`}>
                    <p><strong>IP:</strong> {robot.ip}</p>
                    <p><strong>위치:</strong> x: {robot.location.x}, y: {robot.location.y}</p>
                    <p><strong>속도:</strong> {robot.status?.speed || 'N/A'} m/s</p>
                    <p>
                      <strong>job</strong>{' '}
                      <button onClick={() => openPopup(robot)} className="job-button">+</button>
                    </p>
                  </div>
                </li>
              ))
            ) : (
              <p>연결된 로봇이 없습니다.</p>
            )}
          </ul>
        </div>
      </div>

      {isPopupOpen && selectedRobot && (
        <div className="popup">
          <h2>작업 선택 - {selectedRobot.name}</h2>
          <div>
            <label>
              작업을 선택하세요:{' '}
              <select value={selectedTask} onChange={handleTaskChange}>
                <option value="">선택하세요</option>
                {availableTasks.map((task, index) => (
                  <option key={index} value={task}>
                    {task}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <br />
          <button onClick={closePopup} className="close-button">닫기</button>
        </div>
      )}

      {isPopupOpen && (
        <div onClick={closePopup} className="popup-background"></div>
      )}

      <div className="task-log">
        <h3>작업 로그</h3>
        <div style={{ height: '80%', overflowY: 'scroll', border: '1px solid #ccc' }}>
          <table>
            <thead>
              <tr>
                <th>작업 이름</th>
                <th>로봇 IP</th>
                <th>작업 유형</th>
                <th>상태</th>
                <th>시작 시간</th>
                <th>종료 시간</th>
              </tr>
            </thead>
            <tbody>
              {tasks.length > 0 ? (
                tasks.map((task, index) => (
                  <tr key={index}>
                    <td>{task.name}</td>
                    <td>{task.robotIp}</td>
                    <td>{task.taskType}</td>
                    <td>{task.status}</td>
                    <td>{formatDate(task.startTime)}</td>
                    <td>{formatDate(task.endTime)}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="no-data">작업 로그가 없습니다.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TestPage;
