import React, { useState } from 'react';
import './TaskFlow.css'; // 스타일 파일 임포트

function TaskFlow() {
    const [taskFlows, setTaskFlows] = useState([]); // 추가된 작업 플로우 목록
    const [nodeName, setNodeName] = useState(''); // 노드 이름
    const [stepName, setStepName] = useState(''); // 단계 이름

    const handleAddTaskFlow = () => {
        if (nodeName.trim() === '' || stepName.trim() === '') return; // 빈 값일 경우 추가하지 않음
        setTaskFlows([...taskFlows, { node: nodeName, step: stepName }]); // 새로운 플로우 추가
        setNodeName(''); // 입력 필드 초기화
        setStepName(''); // 입력 필드 초기화
    };

    return (
        <div className="task-flow-container">
            <div className="task-flow-background">
                <div className="task-flow-list">
                    {taskFlows.map((flow, index) => (
                        <div key={index} className="task-flow-item">
                            <div><strong>노드:</strong> {flow.node}</div>
                            <div><strong>단계:</strong> {flow.step}</div>
                        </div>
                    ))}
                    {/* 마지막에 + 버튼이 있는 큰 둥근 사각형 */}
                    <div className="task-flow-add">
                        <input 
                            type="text" 
                            value={nodeName} 
                            onChange={(e) => setNodeName(e.target.value)} 
                            placeholder="노드 이름 입력"
                            className="task-flow-input"
                        />
                        <input 
                            type="text" 
                            value={stepName} 
                            onChange={(e) => setStepName(e.target.value)} 
                            placeholder="단계 이름 입력"
                            className="task-flow-input"
                        />
                        <button onClick={handleAddTaskFlow} className="task-flow-button">+</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TaskFlow;
