// components/NodeCreate.js
import React, { useState } from 'react';
import axios from 'axios';


function NodeCreate({ coordinates, mapId, onClose, onNodeCreated }) {
  const [nodeName, setNodeName] = useState('');

  const handleNodeCreation = async () => {
    const token = localStorage.getItem('token');
    try {
      await axios.post(
        'http://172.30.1.15:5557/map/node',
        {
          name: nodeName,
          x: coordinates.x,
          y: coordinates.y,
          mapId: mapId,
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('노드가 생성되었습니다.');
      onNodeCreated(); // 노드 생성 후 처리
      onClose(); // 팝업 닫기
    } catch (error) {
      console.error('노드 생성 중 오류:', error);
      alert('노드 생성에 실패했습니다.');
    }
  };

  return (
    <div className="node-create-popup">
      <h3>노드 이름 입력</h3>
      <input
        type="text"
        value={nodeName}
        onChange={(e) => setNodeName(e.target.value)}
        required
      />
      <button onClick={handleNodeCreation}>노드 생성</button>
      <button onClick={onClose}>취소</button>
    </div>
  );
}

export default NodeCreate;
