import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import './TestPage.css'; // CSS 파일을 불러옵니다

function TestPage() {
  const [mapUrl, setMapUrl] = useState(null);
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false); // 팝업 상태 관리
  const [selectedRobot, setSelectedRobot] = useState(null); // 선택된 로봇 정보 저장
  const [selectedTask, setSelectedTask] = useState(''); // 선택된 작업 저장
  const [availableTasks, setAvailableTasks] = useState(['Task 1', 'Task 2', 'Task 3']); // 드롭다운에 표시할 작업들
  const canvasRef = useRef(null);
  const [mapResolution, setMapResolution] = useState(0.05); // 기본 값, 실제 파일에서 가져올 예정
  const [mapDimensions, setMapDimensions] = useState({ width: 0, height: 0 });
  const [expandedRobotId, setExpandedRobotId] = useState(null); // 확장된 로봇 ID 저장

  useEffect(() => {
    const fetchData = async () => {
      await fetchMapMetadata();
      await fetchMonitoredMap();
      fetchRobots(); // 로봇 리스트 API 호출
      fetchAllTasks(); // 모든 작업 로그 API 호출
    };
    fetchData();

    // WebSocket 연결 설정
    const socket = new WebSocket('ws://your-websocket-url'); // WebSocket 주소를 설정하세요

    socket.onopen = () => {
      console.log('WebSocket 연결 성공');
    };

    socket.onmessage = (event) => {
      const updatedRobots = JSON.parse(event.data);
      setRobots(updatedRobots); // WebSocket으로 받은 데이터를 로봇 상태로 설정
    };

    socket.onerror = (error) => {
      console.error('WebSocket 오류:', error);
    };

    socket.onclose = () => {
      console.log('WebSocket 연결 종료');
    };

    return () => {
      socket.close(); // 컴포넌트가 언마운트되면 WebSocket 연결 종료
    };
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      window.location.href = '/login'; // 토큰이 없으면 로그인 페이지로 리디렉션
      return null;
    }
    return token;
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/file', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = URL.createObjectURL(response.data);
      setMapUrl(url);

      const img = new Image();
      img.src = url;
      img.onload = () => {
        setMapDimensions({ width: img.width, height: img.height });
        drawMapAndRobots(img.width, img.height); // 이미지 크기대로 캔버스 조정 및 그리기
      };
      img.onerror = (err) => {
        console.error('지도 이미지를 로드하는 중 오류 발생:', err);
      };
    } catch (error) {
      console.error('모니터링 지도 가져오기 오류:', error);
    }
  };

  const fetchMapMetadata = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/metadata', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const metadata = response.data;
      const resolution = parseFloat(metadata.match(/resolution:\s*(\d+.\d+)/)[1]);
      setMapResolution(resolution);
    } catch (error) {
      console.error('지도 메타데이터 가져오기 오류:', error);
    }
  };

  const fetchAllTasks = async () => {
    console.log('모든 작업 로그 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchAllTasks 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:8080/task/all', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTasks(response.data); // 모든 작업 데이터를 상태로 설정
    } catch (error) {
      console.error('작업 로그 가져오기 오류:', error);
    }
  };

  const fetchRobots = async () => {
    console.log('로봇 정보 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchRobots 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setRobots(response.data);
    } catch (error) {
      console.error('로봇 정보 가져오기 오류:', error);
    }
  };

  const toggleExpand = (robotId) => {
    if (expandedRobotId === robotId) {
      setExpandedRobotId(null);
    } else {
      setExpandedRobotId(robotId);
    }
  };

  const openPopup = (robot) => {
    setSelectedRobot(robot);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const handleTaskChange = (event) => {
    setSelectedTask(event.target.value); // 드롭다운에서 선택된 작업 저장
  };

  const drawMapAndRobots = (imgWidth, imgHeight) => {
    const canvas = canvasRef.current;
    if (!canvas) {
      console.error('캔버스 요소를 찾을 수 없습니다.');
      return;
    }

    const ctx = canvas.getContext('2d');
    const mapImage = new Image();
    mapImage.src = mapUrl;

    mapImage.onload = () => {
      canvas.width = imgWidth;
      canvas.height = imgHeight;
      ctx.drawImage(mapImage, 0, 0, imgWidth, imgHeight);

      robots.forEach((robot) => {
        const { x, y } = robot.location;
        const { mapX, mapY } = worldToMap(x, y, imgWidth, imgHeight, mapResolution);

        let robotColor = 'gray';
        if (robot.status?.state === 'Tasking') {
          robotColor = 'blue';
        } else if (robot.status?.state === 'Waiting') {
          robotColor = 'yellow';
        } else if (robot.status?.state === 'Error') {
          robotColor = 'red';
        }

        ctx.fillStyle = robotColor;
        ctx.beginPath();
        ctx.arc(mapX, mapY, 5, 0, 2 * Math.PI);
        ctx.fill();
      });
    };

    mapImage.onerror = (err) => {
      console.error('지도 이미지를 그리는 중 오류 발생:', err);
    };
  };

  const worldToMap = (x, y, mapWidth, mapHeight, resolution) => {
    const mapX = (x + (mapWidth * resolution) / 2) / resolution;
    const mapY = mapHeight - (y + (mapHeight * resolution) / 2) / resolution;
    return { mapX, mapY };
  };

  const formatDate = (date) => {
    const parsedDate = new Date(date);
    return isNaN(parsedDate) ? 'N/A' : parsedDate.toLocaleString();
  };

  useEffect(() => {
    if (mapUrl && mapDimensions.width && mapDimensions.height) {
      drawMapAndRobots(mapDimensions.width, mapDimensions.height);
    }
  }, [mapUrl, robots, mapDimensions]);

  return (
    <div className="test-page-container">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <div className="main-content">
        {/* 좌측 - 지도 캔버스 */}
        <div className="map-container">
          {mapUrl ? (
            <canvas ref={canvasRef} className="map-canvas"></canvas>
          ) : (
            <p>지도를 불러오는 중...</p>
          )}
        </div>

        {/* 우측 - 로봇 리스트 */}
        <div className="robot-list-container">
          <h3>로봇 리스트</h3>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {robots.length > 0 ? (
              robots.map((robot) => (
                <li key={robot._id} className="robot-list-item">
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <span>
                      <strong>이름:</strong> {robot.name}
                    </span>
                    <span>
                      <strong>상태:</strong> {robot.status?.state || 'Unknown'}
                    </span>
                    <span>
                      <strong>배터리:</strong> {robot.status?.battery || 'N/A'}%
                    </span>
                    <button
                      onClick={() => toggleExpand(robot._id)}
                      style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '16px' }}
                    >
                      {expandedRobotId === robot._id ? '^' : 'v'}
                    </button>
                  </div>
                  {expandedRobotId === robot._id && (
                    <div className="robot-details">
                      <p><strong>IP:</strong> {robot.ip}</p>
                      <p><strong>위치:</strong> x: {robot.location.x}, y: {robot.location.y}</p>
                      <p><strong>속도:</strong> {robot.status?.speed || 'N/A'} m/s</p>
                      <p>
                        <strong>job</strong>
                        <button onClick={() => openPopup(robot)} style={{ background: 'none', border: '1px solid #ccc', cursor: 'pointer', fontSize: '16px' }}>+</button>
                      </p>
                    </div>
                  )}
                </li>
              ))
            ) : (
              <p>연결된 로봇이 없습니다.</p>
            )}
          </ul>
        </div>
      </div>

      {/* 팝업 창 */}
      {isPopupOpen && selectedRobot && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup-container" onClick={(e) => e.stopPropagation()}>
            <h2>작업 선택 - {selectedRobot.name}</h2>
            <div>
              <label>
                작업을 선택하세요:
                <select value={selectedTask} onChange={handleTaskChange}>
                  <option value="">선택하세요</option>
                  {availableTasks.map((task, index) => (
                    <option key={index} value={task}>
                      {task}
                    </option>
                  ))}
                </select>
              </label>
            </div>
            <br />
            <button className="popup-close-button" onClick={closePopup}>닫기</button>
          </div>
        </div>
      )}

      {/* 작업 로그 섹션 */}
      <div className="task-log-container">
        <h3>작업 로그</h3>
        <div style={{ height: '80%', overflowY: 'scroll', border: '1px solid #ccc' }}>
          <table className="task-log-table">
            <thead>
              <tr>
                <th>작업 이름</th>
                <th>로봇 IP</th>
                <th>작업 유형</th>
                <th>상태</th>
                <th>시작 시간</th>
                <th>종료 시간</th>
              </tr>
            </thead>
            <tbody>
              {tasks.length > 0 ? (
                tasks.map((task, index) => (
                  <tr key={index}>
                    <td>{task.name}</td>
                    <td>{task.robotIp}</td>
                    <td>{task.taskType}</td>
                    <td>{task.status}</td>
                    <td>{formatDate(task.startTime)}</td>
                    <td>{formatDate(task.endTime)}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center', padding: '10px' }}>작업 로그가 없습니다.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TestPage;
