import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import MapComponent from './MapComponent'; // MapComponent 임포트
import RobotListComponent from './RobotListComponent'; // RobotListComponent 임포트
import TaskLogComponent from './TaskLogComponent'; // TaskLogComponent 임포트
import './TestPage.css';

function TestPage() {
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [mapUrl, setMapUrl] = useState(null); // 지도 URL 상태
  const [isWebSocketConnected, setIsWebSocketConnected] = useState(false); // WebSocket 연결 상태 추가

  useEffect(() => {
    const fetchData = async () => {
      await fetchMonitoredMap(); // 지도 불러오기
      fetchRobots(); // 로봇 리스트 API 호출
      fetchAllTasks(); // 모든 작업 로그 API 호출
    };
    fetchData();

    // WebSocket 연결 설정
    const socket = new WebSocket('ws://172.30.1.28:5050'); // WebSocket 주소를 설정하세요

    socket.onopen = () => {
      console.log('WebSocket 연결 성공');
      setIsWebSocketConnected(true); // WebSocket 연결 상태 업데이트
    };

    socket.onmessage = (event) => {
      const updatedRobots = JSON.parse(event.data);
      setRobots(updatedRobots); // WebSocket으로 받은 데이터를 로봇 상태로 설정
    };

    socket.onerror = (error) => {
      console.error('WebSocket 오류:', error);
    };

    socket.onclose = () => {
      console.log('WebSocket 연결 종료');
      setIsWebSocketConnected(false); // WebSocket 연결 종료 시 상태 업데이트
    };

    return () => {
      socket.close(); // 컴포넌트가 언마운트되면 WebSocket 연결 종료
    };
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      window.location.href = '/login'; // 토큰이 없으면 로그인 페이지로 리디렉션
      return null;
    }
    return token;
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/file', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = URL.createObjectURL(response.data);
      setMapUrl(url); // 지도 URL 설정
    } catch (error) {
      console.error('모니터링 지도 가져오기 오류:', error);
    }
  };

  const fetchRobots = async () => {
    console.log('로봇 정보 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchRobots 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRobots(response.data); // 로봇 데이터를 상태에 저장
    } catch (error) {
      console.error('로봇 정보 가져오기 오류:', error);
    }
  };

  const fetchAllTasks = async () => {
    console.log('모든 작업 로그 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchAllTasks 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:8080/task/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(response.data); // 작업 로그를 상태로 설정
    } catch (error) {
      console.error('작업 로그 가져오기 오류:', error);
    }
  };

  return (
    <div className="test-page">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <h1>{isWebSocketConnected ? "WebSocket 연결됨" : "WebSocket 연결 끊김"}</h1> {/* 연결 상태 표시 */}
      <div className="main-content">
        {/* MapComponent에 robots, mapUrl, isWebSocketConnected 전달 */}
        <MapComponent robots={robots} mapUrl={mapUrl} isWebSocketConnected={isWebSocketConnected} />
        <RobotListComponent robots={robots} /> {/* 로봇 리스트 컴포넌트 */}
      </div>
      {/* 작업 로그 컴포넌트 */}
      <TaskLogComponent tasks={tasks} />
    </div>
  );
}

export default TestPage;
