// utils/coordinateUtils.js

// 해상도(픽셀당 미터)와 원점 좌표 설정
const mapResolution = 0.05;
const mapOrigin = [-10.0, -10.0]; // (x, y)

// 월드 좌표를 맵 좌표로 변환
export const worldToMap = (x, y, mapWidth, mapHeight) => {
  const mapX = (x - mapOrigin[0]) / mapResolution;
  const mapY = mapHeight - (y - mapOrigin[1]) / mapResolution; // Y 축 반전
  return { mapX, mapY };
};

// 맵 좌표를 월드 좌표로 변환 (필요한 경우)
export const mapToWorld = (mapX, mapY, mapWidth, mapHeight) => {
  const x = mapX * mapResolution + mapOrigin[0];
  const y = (mapHeight - mapY) * mapResolution + mapOrigin[1];
  return { x, y };
};
