// src/components/MapManage/Mapmanage.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faPlus, faTrash } from '@fortawesome/free-solid-svg-icons'; // faTrash 아이콘 추가
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css';
import { worldToMap, mapToWorld } from '../../utils/coordinateUtils';
import NodeList from './NodeList'; // NodeList 컴포넌트 임포트
import NodeDelete from './NodeDelete'; // NodeDelete 컴포넌트 임포트

function Mapmanage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null); // 선택된 맵 상태
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [isMonitored, setIsMonitored] = useState(false); // 모니터링 설정 체크박스 상태
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false); // 업로드 팝업 상태
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 수정 팝업 상태
  const [isCreatingNode, setIsCreatingNode] = useState(false); // 노드 생성 모드
  const [isDeletingNode, setIsDeletingNode] = useState(false); // 노드 삭제 모드
  const [nodeX, setNodeX] = useState(null); // X 좌표
  const [nodeY, setNodeY] = useState(null); // Y 좌표
  const [nodeName, setNodeName] = useState(''); // 노드 이름
  const [isNodePopupOpen, setIsNodePopupOpen] = useState(false); // 노드 팝업 상태
  const canvasRef = useRef(null); // 캔버스 참조
  const mapImageRef = useRef(null); // 지도 이미지 참조
  const [nodes, setNodes] = useState([]); // 노드 목록 저장

  const [imageNaturalWidth, setImageNaturalWidth] = useState(null);
  const [imageNaturalHeight, setImageNaturalHeight] = useState(null);

  // Hover 상태 관리
  const [hoveredNode, setHoveredNode] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    fetchMaps();
  }, []);

  // 맵 목록을 서버에서 가져오는 함수
  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
      alert('Failed to fetch maps.');
    }
  };

  // 맵을 선택했을 때 호출되는 함수
  const handleSelectMap = async (map) => {
    setSelectedMap(map); // 맵 선택 상태 업데이트
    setEditName(map.name);
    setEditDescription(map.description);
    setIsMonitored(map.isMonitored || false); // 모니터링 지도 상태 설정

    // 맵 선택 후에 노드를 비동기로 가져오기
    await fetchNodes(map._id);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.15:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl); // 이미지 설정
    } catch (error) {
      console.error('Error fetching map image:', error);
      alert('Failed to fetch map image.');
    }
  };

  // 노드 목록을 서버에서 가져오는 함수
  const fetchNodes = async (mapId) => {
    try {
      const response = await axios.get(`http://172.30.1.15:5557/map/nodes/${mapId}`);
      if (response.status === 200) {
        setNodes(response.data); // 노드 리스트 상태 업데이트
      } else {
        setNodes([]); // 실패 시 빈 배열로 초기화
      }
    } catch (error) {
      console.error('Error fetching nodes:', error);
      setNodes([]); // 오류 발생 시에도 빈 배열로 초기화
      alert('Failed to fetch nodes.');
    }
  };

  // 이미지 로드 시 호출되는 함수 (지도 이미지를 캔버스에 그리기)
  const loadMapImage = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.src = mapImage;
    mapImageRef.current = img;

    img.onload = () => {
      const imgWidth = img.width;
      const imgHeight = img.height;
      setImageNaturalWidth(imgWidth);
      setImageNaturalHeight(imgHeight);

      // 캔버스 크기 조정
      canvas.width = imgWidth;
      canvas.height = imgHeight;

      // 지도 이미지를 캔버스에 그리기
      ctx.drawImage(img, 0, 0, imgWidth, imgHeight);
      drawNodes(); // 노드 그리기
    };
  };

  // 노드를 캔버스에 그리는 함수
  const drawNodes = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!canvas || !imageNaturalWidth || !imageNaturalHeight) return;

    nodes.forEach((node) => {
      const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);

      // 노드를 빨간 정사각형으로 그리기
      const size = 10; // 정사각형의 크기
      ctx.fillStyle = 'red';
      ctx.fillRect(x - size / 2, y - size / 2, size, size); // 중심을 기준으로 그리기
    });
  };

  // 이미지 로드 후 캔버스에 지도 그리기
  useEffect(() => {
    if (mapImage) {
      loadMapImage();
    }
  }, [mapImage]);

  // 노드 변경 시마다 다시 그리기
  useEffect(() => {
    if (canvasRef.current && mapImageRef.current) {
      const ctx = canvasRef.current.getContext('2d');
      ctx.clearRect(0, 0, canvasRef.current.width, canvasRef.current.height);
      ctx.drawImage(mapImageRef.current, 0, 0, imageNaturalWidth, imageNaturalHeight);
      drawNodes();
    }
  }, [nodes, imageNaturalWidth, imageNaturalHeight]);

  // 마우스가 캔버스 위를 움직일 때 노드 호버 상태 업데이트
  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    let foundNode = null;

    for (let node of nodes) {
      const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
      const distance = Math.sqrt((mouseX - x) ** 2 + (mouseY - y) ** 2);
      if (distance < 5) { // 노드 반경 5px 내에 있을 때
        foundNode = node;
        break;
      }
    }

    if (foundNode) {
      setHoveredNode(foundNode);
      setTooltipPosition({ x: e.clientX, y: e.clientY });
    } else {
      setHoveredNode(null);
    }
  };

  // 마우스가 캔버스를 벗어났을 때 호버 상태 초기화
  const handleMouseLeave = () => {
    setHoveredNode(null);
  };

  // 맵 이미지 클릭 시 노드 생성
  const handleMapClick = (e) => {
    if (!isCreatingNode || !selectedMap) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const clickY = e.clientY - rect.top;

    // 화면 좌표를 월드 좌표로 변환
    if (!imageNaturalWidth || !imageNaturalHeight) {
      console.error('Image natural size not available.');
      alert('이미지 로드 중입니다. 잠시 후 다시 시도해주세요.');
      return;
    }

    const { x, y } = mapToWorld(clickX, clickY, imageNaturalWidth, imageNaturalHeight);

    console.log(`Click Coordinates: (${clickX}, ${clickY})`);
    console.log(`World Coordinates: (${x.toFixed(2)}, ${y.toFixed(2)})`);

    setNodeX(x);
    setNodeY(y);
    setIsCreatingNode(false);
    setIsNodePopupOpen(true); // 팝업 열기
    document.body.style.cursor = 'default'; // 커서 복구
  };

  // 노드 생성을 제출하는 함수
  const handleSubmitNode = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    if (nodeName.trim().length === 0) {
      alert('Node name cannot be empty.');
      return;
    }

    try {
      await axios.post('http://172.30.1.15:5557/map/nodes', {
        name: nodeName,
        x: nodeX,
        y: nodeY,
        mapId: selectedMap._id,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Node created successfully.');
      setIsNodePopupOpen(false);
      setNodeName(''); // 입력 필드 초기화
      setNodeX(null);
      setNodeY(null);
      fetchNodes(selectedMap._id); // 노드 목록 갱신
    } catch (error) {
      console.error('Error creating node:', error);
      alert('Failed to create node.');
    }
  };

  // 맵 삭제하는 함수
  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;

    try {
      await axios.put(`http://172.30.1.15:5557/map/delete/${mapId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) {
        setSelectedMap(null); // 선택된 맵을 삭제한 경우 초기화
        setMapImage(null); // 맵 이미지 초기화
        setNodes([]); // 노드 목록 초기화
      }
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  // 맵 파일 변경 시 호출되는 함수
  const handleFileChange = (e) => {
    setMapFile(e.target.files[0]);
  };

  // YAML 파일 변경 시 호출되는 함수
  const handleYamlFileChange = (e) => {
    setYamlFile(e.target.files[0]);
  };

  // 맵 업데이트를 처리하는 함수
  const handleUpdateMap = async (e) => {
    e.preventDefault();

    if (editName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.15:5557/map/update/${selectedMap._id}`, {
        name: editName,
        description: editDescription,
        isMonitored // 모니터링 지도 상태 포함
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Map updated successfully');
      setIsEditPopupOpen(false); // 수정 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
      alert(`Error updating map: ${error.response?.data?.message || error.message}`);
    }
  };

  // 맵 업로드를 처리하는 함수
  const handleUploadMap = async (e) => {
    e.preventDefault();

    if (uploadName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const formData = new FormData();
    formData.append('name', uploadName);
    formData.append('description', uploadDescription);
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.15:5557/map/upload', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Map uploaded successfully');
      setIsUploadPopupOpen(false); // 업로드 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
      alert('Error uploading map.');
    }
  };

  // 맵 수정 팝업을 여는 함수
  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsMonitored(map.isMonitored || false); // 모니터링 상태 설정
    setIsEditPopupOpen(true); // 수정 팝업 열기
  };

  // 노드 생성 모드를 활성화하는 함수
  const handleCreateNodeMode = () => {
    if (!selectedMap) {
      alert('Please select a map first.');
      return;
    }
    setIsCreatingNode(true);
    document.body.style.cursor = 'crosshair'; // 마우스 커서 십자가 모양 변경
  };

  // 노드 삭제 핸들러 함수
  const handleDeleteNode = async (nodeId) => {
    const token = localStorage.getItem('token');
    try {
      await axios.put(`http://172.30.1.15:5557/node/${nodeId}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Node deleted successfully.');
      fetchNodes(selectedMap._id); // 삭제 후 노드 목록 갱신
    } catch (error) {
      console.error('Error deleting node:', error);
      alert('Failed to delete node.');
    }
  };

  // 삭제 모드 토글 함수
  const toggleDeleteNodeMode = () => {
    setIsDeletingNode(!isDeletingNode); // 삭제 모드 활성화/비활성화
  };

  return (
    <div className="map-management-page">
      {/* 상단 네비게이션 바 */}
      <header className="map-management-header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      {/* 툴바: 네비게이션 아래에 위치 */}
      <div className="toolbar">
        <button onClick={handleCreateNodeMode} className="create-node-button">
          <FontAwesomeIcon icon={faPlus} /> Create Node
        </button>
        <button onClick={toggleDeleteNodeMode} className="delete-node-button">
          <FontAwesomeIcon icon={faTrash} /> {isDeletingNode ? 'Cancel Delete' : 'Delete Node'}
        </button>
      </div>

      {/* 맵 리스트와 이미지 컨텐츠 */}
      <div className="map-management-content">
        {/* 맵 리스트 컨테이너 */}
        <div className="map-list-container">
          <h2 className="map-list-header">
            Map List
            <button
              onClick={() => setIsUploadPopupOpen(true)}
              className="add-map-button"
            >
              <FontAwesomeIcon icon={faPlus} />
            </button>
          </h2>
          <ul className="map-list">
            {maps.map((map) => (
              <li
                key={map._id}
                className={`map-list-item ${selectedMap?._id === map._id ? 'selected-map' : ''}`}
              >
                <span
                  onClick={() => handleSelectMap(map)}
                  className="map-list-item-name"
                >
                  {map.name}
                </span>
                <button
                  onClick={() => openEditPopup(map)}
                  className="edit-map-button"
                >
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button
                  onClick={() => handleDeleteMap(map._id)}
                  className="delete-map-button"
                >
                  &times;
                </button>
              </li>
            ))}
          </ul>

          {selectedMap && (
            <div className="map-description">
              <h3>Map Description</h3>
              <p>{selectedMap.description}</p>
            </div>
          )}
        </div>

        {/* 지도 이미지와 노드 렌더링 및 노드 리스트 영역 */}
        <div className="map-image-and-node-list">
          {/* 지도 이미지와 노드 렌더링 영역 */}
          <div
            className="map-image-container"
            style={{ position: 'relative' }}
            onClick={handleMapClick}
          >
            {mapImage ? (
              <>
                <canvas
                  ref={canvasRef}
                  className="map-canvas"
                  style={{ cursor: isCreatingNode ? 'crosshair' : 'default' }}
                  onMouseMove={handleMouseMove}
                  onMouseLeave={handleMouseLeave}
                />
                {/* 노드 삭제 모드가 활성화된 경우, 각 노드에 NodeDelete 버튼 표시 */}
                {isDeletingNode && nodes.map((node) => {
                  const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
                  return (
                    <NodeDelete
                      key={node._id}
                      onDelete={() => handleDeleteNode(node._id)}
                      style={{
                        left: `${x + 5}px`, // 노드 위치 기준으로 조정
                        top: `${y - 15}px`, // 노드 위치 기준으로 조정
                      }}
                    />
                  );
                })}
              </>
            ) : (
              <p>Select a map to view its image</p>
            )}
          </div>

          {/* 오른쪽에 노드 목록 표시 */}
          <NodeList nodes={nodes} /> {/* NodeList 컴포넌트 사용 */}
        </div>
      </div>

      {/* Node 생성 팝업 */}
      {isNodePopupOpen && (
        <div className="popup-container">
          <h3>Create Node</h3>
          <p>X: {nodeX.toFixed(2)}, Y: {nodeY.toFixed(2)}</p>
          <input
            type="text"
            value={nodeName}
            onChange={(e) => setNodeName(e.target.value)}
            placeholder="Enter node name"
            className="popup-input"
          />
          <div className="popup-button-container">
            <button onClick={handleSubmitNode} className="popup-submit-button">
              Create
            </button>
            <button
              onClick={() => setIsNodePopupOpen(false)}
              className="popup-cancel-button"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Map Upload Popup */}
      {isUploadPopupOpen && (
        <div className="popup-container">
          <h3>Upload Map</h3>
          <form onSubmit={handleUploadMap}>
            <input
              type="text"
              value={uploadName}
              onChange={(e) => setUploadName(e.target.value)}
              placeholder="Map Name"
              required
              className="popup-input"
            />
            <textarea
              value={uploadDescription}
              onChange={(e) => setUploadDescription(e.target.value)}
              placeholder="Map Description"
              required
              className="popup-textarea"
            />
            <label>Map File (.pgm, .png, .jpg)</label>
            <input
              type="file"
              accept=".pgm,.png,.jpg"
              onChange={handleFileChange}
              required
              className="popup-file-input"
            />
            <label>YAML File (.yaml)</label>
            <input
              type="file"
              accept=".yaml"
              onChange={handleYamlFileChange}
              required
              className="popup-file-input"
            />
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                Upload
              </button>
              <button
                type="button"
                onClick={() => setIsUploadPopupOpen(false)}
                className="popup-cancel-button"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Edit Map Popup */}
      {isEditPopupOpen && (
        <div className="popup-container">
          <h3>Edit Map</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="Map Name"
              required
              className="popup-input"
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="Map Description"
              required
              className="popup-textarea"
            />
            {/* 모니터링 지도 설정 체크박스 추가 */}
            <label style={{ marginTop: '10px', color: '#333' }}>
              <input
                type="checkbox"
                checked={isMonitored}
                onChange={(e) => setIsMonitored(e.target.checked)}
                style={{ marginRight: '10px' }}
              />
              Set as Monitoring Map
            </label>
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                Update
              </button>
              <button
                type="button"
                onClick={() => setIsEditPopupOpen(false)}
                className="popup-cancel-button"
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Hover Tooltip */}
      {hoveredNode && (
        <div
          className="tooltip"
          style={{
            position: 'fixed',
            top: `${tooltipPosition.y + 10}px`,
            left: `${tooltipPosition.x + 10}px`,
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
            color: 'white',
            padding: '5px 10px',
            borderRadius: '4px',
            pointerEvents: 'none',
            zIndex: 1000,
            fontSize: '12px',
          }}
        >
          {hoveredNode.name}
        </div>
      )}
    </div>
  );
}

export default Mapmanage;
