import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null); // 선택된 맵 상태
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false); // 업로드 팝업 상태
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 수정 팝업 상태

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map); // 맵 선택 상태 업데이트
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.28:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;

    try {
      await axios.put(`http://172.30.1.28:5557/map/delete/${mapId}`, {}, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) {
        setSelectedMap(null); // 선택된 맵을 삭제한 경우 초기화
      }
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleFileChange = (e) => {
    setMapFile(e.target.files[0]);
  };

  const handleYamlFileChange = (e) => {
    setYamlFile(e.target.files[0]);
  };

  const handleUpdateMap = async (e) => {
    e.preventDefault();

    if (editName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.28:5557/map/update/${selectedMap._id}`, {
        name: editName,
        description: editDescription,
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Map updated successfully');
      setIsEditPopupOpen(false); // 수정 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
    }
  };

  const handleUploadMap = async (e) => {
    e.preventDefault();

    if (uploadName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const formData = new FormData();
    formData.append('name', uploadName);
    formData.append('description', uploadDescription);
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.28:5557/map/upload', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Map uploaded successfully');
      setIsUploadPopupOpen(false); // 업로드 후 팝업 닫기
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsEditPopupOpen(true); // 수정 팝업 열기
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', height: '100vh' }}>
      {/* 상단 네비게이션 바 */}
      <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      <div style={{ display: 'flex', height: 'calc(100% - 100px)', padding: '20px' }}>
        {/* Left side: Map List */}
        <div style={{ flex: '0.5', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
          <h2 style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            Map List
            <button
              onClick={() => setIsUploadPopupOpen(true)} // 팝업 열기
              style={{
                backgroundColor: '#4CAF50',
                color: 'white',
                border: 'none',
                borderRadius: '50%',
                width: '30px',
                height: '30px',
                fontSize: '20px',
                cursor: 'pointer'
              }}
            >
              +
            </button>
          </h2>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {maps.map((map) => (
              <li
                key={map._id}
                style={{
                  padding: '10px',
                  cursor: 'pointer',
                  borderBottom: '1px solid #ccc',
                  display: 'flex',
                  justifyContent: 'space-between',
                  backgroundColor: selectedMap && selectedMap._id === map._id ? '#e0e0e0' : 'transparent', // 선택된 맵일 경우 회색
                }}
              >
                <span onClick={() => handleSelectMap(map)} style={{ flexGrow: 1 }}>{map.name}</span>
                <button
                  onClick={() => openEditPopup(map)}
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    cursor: 'pointer',
                    fontSize: '16px',
                    marginRight: '10px'
                  }}
                >
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button
                  onClick={() => handleDeleteMap(map._id)}
                  style={{
                    backgroundColor: 'transparent',
                    border: 'none',
                    color: 'red',
                    cursor: 'pointer',
                    fontSize: '16px'
                  }}
                >
                  &times;
                </button>
              </li>
            ))}
          </ul>

          {/* 선택된 맵의 설명을 보여주는 부분 */}
          {selectedMap && (
            <div style={{ marginTop: '30px', padding: '10px', borderTop: '1px solid #ccc' }}>
              <h3>Map Description</h3>
              <p>{selectedMap.description}</p>
            </div>
          )}
        </div>

        {/* Center: Map Image */}
        <div style={{ flex: '2', padding: '0 20px', textAlign: 'center', marginTop: '20px' }}>
          {mapImage ? (
            <img src={mapImage} alt="Selected Map" style={{ maxWidth: '100%', height: 'auto' }} />
          ) : (
            <p>Select a map to view its image</p>
          )}
        </div>
      </div>

      {/* Map Upload Popup */}
      {isUploadPopupOpen && (
        <div style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: 'white',
          padding: '20px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          zIndex: 1000,
          width: '400px',
          borderRadius: '10px',
          boxSizing: 'border-box' // 박스 사이징 추가
        }}>
          <h3>Upload Map</h3>
          <form onSubmit={handleUploadMap}>
            <input
              type="text"
              value={uploadName}
              onChange={(e) => setUploadName(e.target.value)}
              placeholder="Map Name"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%', boxSizing: 'border-box' }} // 박스 사이징 추가
            />
            <textarea
              value={uploadDescription}
              onChange={(e) => setUploadDescription(e.target.value)}
              placeholder="Map Description"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%', boxSizing: 'border-box' }} // 박스 사이징 추가
            />
            <label>Map File (.pgm, .png, .jpg)</label>
            <input type="file" accept=".pgm,.png,.jpg" onChange={handleFileChange} required style={{ display: 'block', marginBottom: '10px' }} />
            <label>YAML File (.yaml)</label>
            <input type="file" accept=".yaml" onChange={handleYamlFileChange} required style={{ display: 'block', marginBottom: '10px' }} />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
                Upload
              </button>
              <button
                type="button"
                onClick={() => setIsUploadPopupOpen(false)}
                style={{ padding: '10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer' }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Edit Map Popup */}
      {isEditPopupOpen && (
        <div style={{
          position: 'fixed',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          backgroundColor: 'white',
          padding: '20px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          zIndex: 1000,
          width: '400px',
          borderRadius: '10px',
          boxSizing: 'border-box' // 박스 사이징 추가
        }}>
          <h3>Edit Map</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="Map Name"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%', boxSizing: 'border-box' }} // 박스 사이징 추가
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="Map Description"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%', boxSizing: 'border-box' }} // 박스 사이징 추가
            />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
                Update
              </button>
              <button
                type="button"
                onClick={() => setIsEditPopupOpen(false)}
                style={{ padding: '10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer' }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default MapManagementPage;
