import React, { useEffect, useRef } from 'react';

function MapComponent({ robots, mapUrl }) {
  const canvasRef = useRef(null);
  const mapResolution = 0.05; // 기본 해상도 값

  const drawMapAndRobots = () => {
    const canvas = canvasRef.current;
    if (!canvas) {
      console.error('캔버스 요소를 찾을 수 없습니다.');
      return;
    }

    const ctx = canvas.getContext('2d');
    const mapImage = new Image();
    mapImage.src = mapUrl;

    mapImage.onload = () => {
      // 캔버스 크기를 CSS 크기에 맞춥니다.
      const canvasWidth = canvas.clientWidth;
      const canvasHeight = canvas.clientHeight;
      canvas.width = canvasWidth;
      canvas.height = canvasHeight;

      // 이미지의 원본 크기로 그립니다.
      ctx.drawImage(mapImage, 0, 0);

      robots.forEach((robot) => {
        const { x, y } = robot.location;
        const { mapX, mapY } = worldToMap(x, y, mapResolution);

        let robotColor = 'gray';
        if (robot.status?.state === 'Tasking') {
          robotColor = 'blue';
        } else if (robot.status?.state === 'Waiting') {
          robotColor = 'yellow';
        } else if (robot.status?.state === 'Error') {
          robotColor = 'red';
        }

        ctx.fillStyle = robotColor;
        ctx.beginPath();
        ctx.arc(mapX, mapY, 5, 0, 2 * Math.PI);
        ctx.fill();
      });
    };

    mapImage.onerror = (err) => {
      console.error('지도 이미지를 그리는 중 오류 발생:', err);
    };
  };

  const worldToMap = (x, y, resolution) => {
    // 지도 이미지의 원본 크기를 기준으로 좌표 변환
    const mapX = x / resolution;
    const mapY = (mapImage.height - y / resolution);
    return { mapX, mapY };
  };

  useEffect(() => {
    if (mapUrl && robots.length > 0) {
      drawMapAndRobots(); // 지도 이미지와 로봇 데이터로 맵을 그림
    }
  }, [mapUrl, robots]);

  return (
    <div className="map-canvas-container">
      {mapUrl ? (
        <canvas ref={canvasRef} className="canvas"></canvas>
      ) : (
        <p>지도를 불러오는 중...</p>
      )}
    </div>
  );
}

export default MapComponent;
