import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './NodeManager.css'; // 스타일 임포트

function NodeManager() {
  const [nodes, setNodes] = useState([]); // 노드 목록 상태
  const [name, setName] = useState(''); // 노드 이름 입력 상태
  const [x, setX] = useState(0); // X 좌표 입력 상태
  const [y, setY] = useState(0); // Y 좌표 입력 상태
  const [task, setTask] = useState(''); // 작업 입력 상태

  // 서버에서 노드 목록 가져오기
  useEffect(() => {
    fetchNodes();
  }, []);

  const fetchNodes = async () => {
    try {
      const response = await axios.get('http://172.30.1.15:5557/map/nodes');
      setNodes(response.data);
    } catch (error) {
      console.error('Error fetching nodes:', error);
    }
  };

  const handleCreateNode = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://172.30.1.15:5557/map/nodes', {
        name,
        x,
        y,
        task,
      });

      alert('Node created successfully!');
      setNodes([...nodes, response.data.node]); // 노드 목록 업데이트
      setName(''); setX(0); setY(0); setTask(''); // 입력 필드 초기화
    } catch (error) {
      console.error('Error creating node:', error);
      alert('Error creating node');
    }
  };

  return (
    <div className="node-manager-container">
      <h2>Add Map Node</h2>
      <form onSubmit={handleCreateNode} className="node-form">
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Node Name"
          required
        />
        <input
          type="number"
          value={x}
          onChange={(e) => setX(e.target.value)}
          placeholder="X Coordinate"
          required
        />
        <input
          type="number"
          value={y}
          onChange={(e) => setY(e.target.value)}
          placeholder="Y Coordinate"
          required
        />
        <input
          type="text"
          value={task}
          onChange={(e) => setTask(e.target.value)}
          placeholder="Task"
        />
        <button type="submit">Add Node</button>
      </form>

      <h2>Map Nodes</h2>
      <ul className="node-list">
        {nodes.map((node) => (
          <li key={node._id}>
            <span>{node.name}</span> - ({node.x}, {node.y}) - Task: {node.task || 'None'}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default NodeManager;
