import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css'; // CSS 파일 임포트

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false);
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false);
  const [nodeName, setNodeName] = useState('');
  const [nodeX, setNodeX] = useState(0);
  const [nodeY, setNodeY] = useState(0);

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    try {
      const response = await axios.get('http://172.30.1.15:5557/map/maps', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;
    try {
      await axios.put(`http://172.30.1.15:5557/map/delete/${mapId}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) setSelectedMap(null);
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleUpdateMap = async (e) => {
    e.preventDefault();
    if (editName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    try {
      await axios.put(`http://172.30.1.15:5557/map/update/${selectedMap._id}`, {
        name: editName,
        description: editDescription,
      }, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'application/json' },
      });
      alert('Map updated successfully');
      setIsEditPopupOpen(false);
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
    }
  };

  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsEditPopupOpen(true);
  };

  const handleAddNode = async (e) => {
    e.preventDefault();
    if (!selectedMap) return;

    try {
      const response = await axios.post('http://172.30.1.15:5557/map/nodes', {
        name: nodeName,
        x: nodeX,
        y: nodeY,
        mapId: selectedMap._id,
      });

      alert('Node added successfully!');
      setNodeName('');
      setNodeX(0);
      setNodeY(0);
    } catch (error) {
      console.error('Error adding node:', error);
      alert('Error adding node');
    }
  };

  return (
    <div className="map-management-container">
      <header className="map-management-header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      <div className="map-management-content">
        <div className="map-list-container">
          <h2 className="map-list-header">
            Map List
            <button onClick={() => setIsUploadPopupOpen(true)} className="upload-button">+</button>
          </h2>
          <ul className="map-list">
            {maps.map((map) => (
              <li key={map._id} className={`map-list-item ${selectedMap && selectedMap._id === map._id ? 'selected' : ''}`}>
                <span onClick={() => setSelectedMap(map)}>{map.name}</span>
                <button onClick={() => openEditPopup(map)} className="edit-button">
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button onClick={() => handleDeleteMap(map._id)} className="delete-button">&times;</button>
              </li>
            ))}
          </ul>
        </div>

        <div className="map-image-container">
          {mapImage ? <img src={mapImage} alt="Selected Map" className="map-image" /> : <p>Select a map to view its image</p>}
        </div>
      </div>

      {isEditPopupOpen && (
        <div className="popup">
          <h3>Edit Map</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="Map Name"
              required
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="Map Description"
              required
            />
            <h3>Add Node</h3>
            <input
              type="text"
              value={nodeName}
              onChange={(e) => setNodeName(e.target.value)}
              placeholder="Node Name"
              required
            />
            <input
              type="number"
              value={nodeX}
              onChange={(e) => setNodeX(e.target.value)}
              placeholder="X Coordinate"
              required
            />
            <input
              type="number"
              value={nodeY}
              onChange={(e) => setNodeY(e.target.value)}
              placeholder="Y Coordinate"
              required
            />
            <div className="popup-buttons">
              <button type="submit">Update Map</button>
              <button type="button" onClick={() => setIsEditPopupOpen(false)}>Cancel</button>
              <button type="button" onClick={handleAddNode}>Add Node</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default MapManagementPage;
