// Mapmanage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css'; // CSS 파일 import

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false);
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false);
  const [isAddingNode, setIsAddingNode] = useState(false);
  const [nodeCoordinates, setNodeCoordinates] = useState({ x: 0, y: 0 });

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5557/map/maps', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(
        `http://172.30.1.15:5557/map/file/${map._id}`,
        { headers: { Authorization: `Bearer ${token}` }, responseType: 'blob' }
      );
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const openUploadPopup = () => {
    setIsUploadPopupOpen(true);
    setUploadName('');
    setUploadDescription('');
    setMapFile(null);
    setYamlFile(null);
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;

    try {
      await axios.put(`http://172.30.1.15:5557/map/delete/${mapId}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) {
        setSelectedMap(null);
      }
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleUploadMap = async (e) => {
    e.preventDefault();

    if (uploadName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }

    const formData = new FormData();
    formData.append('name', uploadName);
    formData.append('description', uploadDescription);
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.15:5557/map/upload', formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Map uploaded successfully');
      setIsUploadPopupOpen(false);
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  const handleMapClick = (e) => {
    if (!isAddingNode) return;

    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    setNodeCoordinates({ x, y });
    alert(`좌표가 설정되었습니다: (${x}, ${y})`);
    setIsAddingNode(false);
  };

  const handleNodeAdd = async () => {
    const { x, y } = nodeCoordinates;
    if (x === 0 && y === 0) {
      alert('맵을 클릭하여 좌표를 선택해주세요.');
      return;
    }

    const nodeName = prompt('노드 이름을 입력하세요:');
    if (!nodeName) return;

    try {
      const response = await axios.post('http://172.30.1.15:5557/map/nodes', {
        name: nodeName,
        x,
        y,
        mapId,
      });

      alert('Node added successfully!');
      console.log(response.data);
    } catch (error) {
      console.error('Error adding node:', error);
      alert('Error adding node');
    }
  };

  return (
    <div className="map-management">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      <div className="content">
        <div className="map-list">
          <h2>
            Map List
            <button className="add-map-button" onClick={openUploadPopup}>
              +
            </button>
          </h2>
          <ul>
            {maps.map((map) => (
              <li
                key={map._id}
                className={selectedMap && selectedMap._id === map._id ? 'selected' : ''}
              >
                <span onClick={() => handleSelectMap(map)}>{map.name}</span>
                <button onClick={() => setIsEditPopupOpen(true)} className="edit-button">
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button onClick={() => handleDeleteMap(map._id)} className="delete-button">
                  &times;
                </button>
              </li>
            ))}
          </ul>
        </div>

        <div className="map-container">
          <div className="toolbar">
            <button className="node-add-button" onClick={() => setIsAddingNode(true)}>
              노드 추가
            </button>
            <button className="confirm-node-button" onClick={handleNodeAdd}>
              노드 저장
            </button>
          </div>

          <div className="map-view" onClick={handleMapClick}>
            {mapImage ? (
              <img src={mapImage} alt="Selected Map" />
            ) : (
              <p>Select a map to view its image</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default MapManagementPage;
