import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RobotListPage.css'; // CSS 분리

function RobotListPage() {
  const [robots, setRobots] = useState([]);

  useEffect(() => {
    fetchRobots();
  }, []);

  const fetchRobots = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setRobots(response.data);
    } catch (error) {
      console.error('Error fetching robots:', error);
    }
  };

  return (
    <div className="robot-list-page">
      <div className="robot-list-container">
        <h3>Your Robots</h3>
        <ul className="robot-list">
          {robots.map((robot) => (
            <li key={robot._id} className="robot-list-item">
              {robot.name} - {robot.ip}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default RobotListPage;
