import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';

import axios from 'axios';
import MapComponent from './MapComponent'; // MapComponent 임포트
import RobotListComponent from './RobotListComponent'; // RobotListComponent 임포트
import TaskLogComponent from './TaskLogComponent'; // TaskLogComponent 임포트
import './TestPage.css';

function TestPage() {
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [mapUrl, setMapUrl] = useState(null); // 지도 URL 상태
  const [nodes, setNodes] = useState([]); // 노드 상태

  useEffect(() => {
    const fetchData = async () => {
      await fetchMonitoredMap(); // 지도 불러오기
      fetchAllTasks(); // 모든 작업 로그 API 호출
      fetchNodes(); // 노드 데이터 불러오기
    };
    fetchData();

    // 2초마다 로봇 데이터를 가져오는 타이머 설정
    const intervalId = setInterval(() => {
      fetchRobots(); // 로봇 리스트 API 호출
    }, 2000);

    // 컴포넌트가 언마운트될 때 타이머 정리
    return () => clearInterval(intervalId);
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      window.location.href = '/login'; // 토큰이 없으면 로그인 페이지로 리디렉션
      return null;
    }
    return token;
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.15:5557/map/monitored', {
        headers: { Authorization: `Bearer ${token}` },
      });

      const monitoredMap = response.data;
      setMapUrl(monitoredMap.fileUrl); // 지도 URL 설정
      fetchNodes(monitoredMap._id); // mapId를 기반으로 노드 불러오기
    } catch (error) {
      console.error('모니터링 지도 가져오기 오류:', error);
    }
  };

  const fetchRobots = async () => {
    console.log('로봇 정보 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchRobots 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5559/robot/robots', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRobots(response.data); // 로봇 데이터를 상태에 저장
    } catch (error) {
      console.error('로봇 정보 가져오기 오류:', error);
    }
  };

  const fetchAllTasks = async () => {
    console.log('모든 작업 로그 가져오는 중');
    const token = getToken();
    if (!token) {
      console.log('토큰이 없어 fetchAllTasks 종료');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:8080/task/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(response.data); // 작업 로그를 상태로 설정
    } catch (error) {
      console.error('작업 로그 가져오기 오류:', error);
    }
  };

  const fetchNodes = async (mapId) => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get(`http://172.30.1.15:5557/map/nodes/${mapId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNodes(response.data); // 해당 맵의 노드 데이터를 상태에 저장
    } catch (error) {
      console.error('노드 정보 가져오기 오류:', error);
    }
  };

  return (
    <div className="test-page">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <h1></h1>
      <div className="main-content">
        {/* MapComponent에 robots, mapUrl, nodes 전달 */}
        <MapComponent robots={robots} mapUrl={mapUrl} nodes={nodes} />
        <RobotListComponent robots={robots} /> {/* 로봇 리스트 컴포넌트 */}
      </div>
      {/* 작업 로그 컴포넌트 */}
      <TaskLogComponent tasks={tasks} />
    </div>
  );
}

export default TestPage;
