import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';

function RobotManagePage() {
  console.log("1: RobotManagePage component rendered");

  const [tasks, setTasks] = useState([]);
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [message, setMessage] = useState('');
  const [showTaskModal, setShowTaskModal] = useState(false);  // Modal state
  const [taskName, setTaskName] = useState('');
  const [taskType, setTaskType] = useState('');

  // Get token from localStorage
  const getToken = () => {
    const token = localStorage.getItem('token');
    console.log("2: Token retrieved:", token);
    if (!token) {
      alert('No token found, please log in again.');
      window.location.href = '/login'; // Redirect to login if token is missing
      return null;
    }
    return token;
  };

  // Fetch all robots from the API
  const fetchRobots = async () => {
    console.log("Fetching robots");
    const token = getToken();
    if (!token) {
      console.log("No token found, exiting fetchRobots");
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setRobots(response.data);
    } catch (error) {
      console.error('Error fetching robots:', error);
    }
  };

  // Fetch tasks for the selected robot by IP
  const fetchTasksForRobot = async (robotIp) => {
    console.log(`Fetching tasks for robot IP: ${robotIp}`);
    const token = getToken();
    if (!token) {
      console.log("No token found, exiting fetchTasksForRobot");
      return;
    }

    try {
      const response = await axios.get(`http://172.30.1.28:8080/task/robot/ip/${robotIp}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setTasks(response.data);
      if (response.data.length === 0) {
        setMessage('No tasks found for this robot.');
      } else {
        setMessage('');
      }
    } catch (error) {
      console.log("Error fetching tasks for robot:", error);
      setMessage('Failed to retrieve tasks.');
    }
  };

  // Handle robot selection
  const handleSelectRobot = (robot) => {
    setSelectedRobot(robot);
    fetchTasksForRobot(robot.ip);  // Fetch tasks for the selected robot by IP
  };

  // Handle task creation
  const handleCreateTask = async () => {
    // 필수 필드 검증
    if (!taskName) {
      alert('Task name is required.');
      return;
    }

    if (!taskType) {
      alert('Task type is required.');
      return;
    }

    if (!selectedRobot || !selectedRobot.ip) {
      alert('A robot must be selected.');
      return;
    }

    const token = getToken();

    // 로깅을 통해 확인할 수 있음
    console.log('Creating Task with the following data:', {
      name: taskName,
      taskType: taskType,
      robotIp: selectedRobot.ip,  // 필수 필드: robot IP 사용
      status: 'Pending'
    });

    try {
      const newTask = {
        name: taskName,
        taskType: taskType,
        robotIp: selectedRobot.ip,  // 필수 필드: robot IP 사용
        status: 'Pending',          // 상태는 기본적으로 'Pending'
      };

      const response = await axios.post('http://172.30.1.28:8080/task/create', newTask, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      console.log('Task created successfully:', response.data);
      alert('Task added successfully!');
      setShowTaskModal(false); // Close the modal after successful task creation
      fetchTasksForRobot(selectedRobot.ip); // Refresh task list
    } catch (error) {
      console.error('Error adding task:', error);

      if (error.response) {
        alert(`Failed to add task: ${error.response.data?.message || error.response.status}`);
      } else if (error.request) {
        alert('Failed to add task: No response from the server. Please check your network.');
      } else {
        alert(`Failed to add task: ${error.message}`);
      }
    }
  };


  // Fetch robots on component mount
  useEffect(() => {
    fetchRobots();
  }, []);

  // Helper function to format date or show "N/A" if date is invalid
  const formatDate = (date) => {
    return date ? new Date(date).toLocaleString() : 'N/A';
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <div style={{ display: 'flex' }}>
        <Navbar />
      </div>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Robot and Task Management</h2>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* Left side - Robot list */}
        <div style={{ flex: '1', padding: '20px', borderRight: '1px solid #ccc' }}>
          <h3>Robot List</h3>
          {robots.length === 0 && <p>No robots available.</p>}
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {robots.map((robot) => (
              <li
                key={robot._id}
                onClick={() => handleSelectRobot(robot)}
                style={{
                  padding: '10px',
                  borderBottom: '1px solid #ccc',
                  marginBottom: '10px',
                  cursor: 'pointer',
                  backgroundColor: selectedRobot && selectedRobot._id === robot._id ? '#f0f0f0' : 'white', // Highlight selected robot
                }}
              >
                <strong>Robot Name:</strong> {robot.name} <br />
                <strong>Model:</strong> {robot.model} <br />
                <strong>IP:</strong> {robot.ip} <br />
                <strong>Location:</strong> ({robot.location.x}, {robot.location.y}) <br />
                <button onClick={() => setShowTaskModal(true)}>Add Task</button> {/* Add Task button */}
              </li>
            ))}
          </ul>
        </div>
        {/* Right side - Task list */}
        <div style={{ flex: '1', padding: '20px' }}>
          <h3>{selectedRobot ? `Tasks for ${selectedRobot.name}` : 'Tasks for Selected Robot'}</h3>
          {message && <p>{message}</p>}
          {tasks.length > 0 ? (
            <ul style={{ listStyleType: 'none', padding: 0 }}>
              {tasks.map((task) => (
                <li key={task._id} style={{ padding: '10px', borderBottom: '1px solid #ccc', marginBottom: '10px' }}>
                  <strong>Task Name:</strong> {task.name} <br />
                  <strong>Task Type:</strong> {task.taskType} <br />
                  <strong>Status:</strong> {task.status} <br />
                  <strong>Start Time:</strong> {formatDate(task.startTime)} <br />
                  {task.endTime && (
                    <>
                      <strong>End Time:</strong> {formatDate(task.endTime)} <br />
                    </>
                  )}
                </li>
              ))}
            </ul>
          ) : (
            <p>No tasks available for this robot.</p>
          )}
        </div>
      </div>

      {/* Task creation modal */}
      {showTaskModal && (
        <div
          style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'white',
            padding: '20px',
            boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
          }}
        >
          <h3>Create Task for {selectedRobot.name}</h3>
          <input
            type="text"
            placeholder="Task Name"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
            style={{ padding: '10px', marginBottom: '10px', width: '100%' }}
          />
          <input
            type="text"
            placeholder="Task Type"
            value={taskType}
            onChange={(e) => setTaskType(e.target.value)}
            style={{ padding: '10px', marginBottom: '10px', width: '100%' }}
          />
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <button onClick={handleCreateTask} style={{ padding: '10px', backgroundColor: 'green', color: 'white', border: 'none', cursor: 'pointer' }}>Add Task</button>
            <button onClick={() => setShowTaskModal(false)} style={{ padding: '10px', backgroundColor: 'red', color: 'white', border: 'none', cursor: 'pointer' }}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default RobotManagePage;
