import React, { useEffect, useState } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';

function MonitoringPage() {
  const [mapUrl, setMapUrl] = useState('');
  const [robots, setRobots] = useState([]);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    fetchMap();

    const token = localStorage.getItem('token');
    const ws = new WebSocket(`ws://172.30.1.40:5559?token=${token}`);
    ws.onmessage = (event) => {
      const updatedRobots = JSON.parse(event.data);
      setRobots(updatedRobots);
    };
    setWs(ws);

    return () => {
      if (ws) ws.close();
    };
  }, []);

  const fetchMap = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.40:5557/map/monitored/file', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const blob = new Blob([response.data], { type: 'image/png' });
      const url = window.URL.createObjectURL(blob);
      setMapUrl(url);
    } catch (error) {
      console.error('Error fetching map:', error);
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <div style={{ display: 'flex' }}>
        <Navbar />
      </div>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Monitoring</h2>
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        {mapUrl && (
          <div style={{ position: 'relative' }}>
            <img src={mapUrl} alt="Map" style={{ maxWidth: '100%' }} />
            {robots.map(robot => (
              <div
                key={robot._id}
                style={{
                  position: 'absolute',
                  left: `${robot.location.x}px`,
                  top: `${robot.location.y}px`,
                  width: '10px',
                  height: '10px',
                  backgroundColor: 'red',
                  borderRadius: '50%'
                }}
              ></div>
            ))}
          </div>
        )}
      </div>
      <div>
        <h3>Robots</h3>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {robots.map((robot) => (
            <li key={robot._id} style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>
              {robot.name} - {robot.location.x}, {robot.location.y}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default MonitoringPage;
