import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import './TestPage.css';
import '@fortawesome/fontawesome-free/css/all.min.css';

function TestPage() {
  const [mapUrl, setMapUrl] = useState(null);
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [selectedTask, setSelectedTask] = useState('');
  const [availableTasks, setAvailableTasks] = useState(['Task 1', 'Task 2', 'Task 3']);
  const mapCanvasRef = useRef(null); // 지도 캔버스 레퍼런스
  const robotsCanvasRef = useRef(null); // 로봇 캔버스 레퍼런스
  const [mapImage, setMapImage] = useState(null);
  const [mapResolution, setMapResolution] = useState(0.05);
  const [mapDimensions, setMapDimensions] = useState({ width: 0, height: 0 });
  const [dragging, setDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });
  const [lastMousePos, setLastMousePos] = useState({ x: 0, y: 0 });
  const [expandedRobotId, setExpandedRobotId] = useState(null);

  // 추가: 최신 로봇 데이터를 저장할 상태
  const latestRobotsRef = useRef([]);

  // 데이터 초기화 및 WebSocket 설정
  useEffect(() => {
    const fetchData = async () => {
      await fetchMapMetadata();
      await fetchMonitoredMap();
      fetchRobots();
      fetchAllTasks();
    };
    fetchData();

    const socket = new WebSocket('ws://your-websocket-url'); // 실제 WebSocket URL로 변경

    socket.onopen = () => {
      console.log('WebSocket 연결 성공');
    };

    socket.onmessage = (event) => {
      console.log('WebSocket 메시지 수신:', event.data); // 디버깅용
      try {
        const updatedRobots = JSON.parse(event.data);
        latestRobotsRef.current = updatedRobots; // 최신 로봇 데이터 저장
      } catch (err) {
        console.error('로봇 데이터 파싱 오류:', err);
      }
    };

    socket.onerror = (error) => {
      console.error('WebSocket 오류:', error);
    };

    socket.onclose = () => {
      console.log('WebSocket 연결 종료');
    };

    // 1.5초 간격으로 로봇 데이터 갱신
    const intervalId = setInterval(() => {
      if (latestRobotsRef.current.length > 0) {
        setRobots([...latestRobotsRef.current]); // 상태 업데이트
        latestRobotsRef.current = []; // 최신 데이터 초기화
      }
    }, 1500); // 1500ms = 1.5초

    return () => {
      socket.close();
      clearInterval(intervalId); // 클린업
    };
  }, []);

  // 지도 그리기 useEffect
  useEffect(() => {
    drawMap();
  }, [mapUrl, mapDimensions, dragOffset]);

  // 로봇 그리기 useEffect
  useEffect(() => {
    drawRobots();
  }, [robots, dragOffset]);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      window.location.href = '/login';
      return null;
    }
    return token;
  };

  const fetchMapMetadata = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/metadata', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const metadata = response.data;
      console.log('지도 메타데이터:', metadata); // 디버깅용

      // metadata가 객체인지 문자열인지 확인
      let resolution;
      if (typeof metadata === 'object' && metadata.resolution) {
        resolution = parseFloat(metadata.resolution);
      } else if (typeof metadata === 'string') {
        const match = metadata.match(/resolution:\s*(\d+(\.\d+)?)/);
        if (match && match[1]) {
          resolution = parseFloat(match[1]);
        }
      }

      if (resolution) {
        console.log('지도 해상도:', resolution); // 디버깅용
        setMapResolution(resolution);
      } else {
        console.error('지도 해상도 정보를 찾을 수 없습니다.');
      }
    } catch (error) {
      console.error('지도 메타데이터 가져오기 오류:', error);
    }
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5557/map/monitored/file', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const url = URL.createObjectURL(response.data);
      setMapUrl(url);
      console.log('지도 URL:', url); // 디버깅용

      const img = new Image();
      img.src = url;
      img.onload = () => {
        console.log('지도 이미지 로드 완료'); // 디버깅용
        setMapImage(img);
        setMapDimensions({ width: img.width, height: img.height });
        drawMap(); // 지도 캔버스만 그리기
        drawRobots(); // 초기 로봇 위치 그리기
      };
      img.onerror = (err) => {
        console.error('지도 이미지를 로드하는 중 오류 발생:', err);
      };
    } catch (error) {
      console.error('모니터링 지도 가져오기 오류:', error);
    }
  };

  const fetchAllTasks = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:8080/task/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(response.data);
    } catch (error) {
      console.error('작업 로그 가져오기 오류:', error);
    }
  };

  const fetchRobots = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRobots(response.data);
      // drawRobots(); // 이미 useEffect에서 호출됨
    } catch (error) {
      console.error('로봇 정보 가져오기 오류:', error);
    }
  };

  const toggleExpand = (robotId) => {
    if (expandedRobotId === robotId) {
      setExpandedRobotId(null);
    } else {
      setExpandedRobotId(robotId);
    }
  };

  const openPopup = (robot) => {
    setSelectedRobot(robot);
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    setIsPopupOpen(false);
  };

  const handleTaskChange = (event) => {
    setSelectedTask(event.target.value);
  };

  const formatDate = (date) => {
    const parsedDate = new Date(date);
    return isNaN(parsedDate) ? 'N/A' : parsedDate.toLocaleString();
  };

  const drawMap = () => {
    const mapCanvas = mapCanvasRef.current;
    if (!mapCanvas) {
      console.error('mapCanvasRef가 참조되지 않습니다.');
      return;
    }
    const ctx = mapCanvas.getContext('2d');
    ctx.clearRect(0, 0, mapCanvas.width, mapCanvas.height);

    if (mapImage) {
      ctx.drawImage(mapImage, dragOffset.x, dragOffset.y, mapImage.width, mapImage.height);
      console.log('지도 그리기 완료'); // 디버깅용
    } else {
      console.warn('mapImage가 존재하지 않습니다.');
    }
  };

  const drawRobots = () => {
    const robotsCanvas = robotsCanvasRef.current;
    if (!robotsCanvas) {
      console.error('robotsCanvasRef가 참조되지 않습니다.');
      return;
    }
    const ctx = robotsCanvas.getContext('2d');
    ctx.clearRect(0, 0, robotsCanvas.width, robotsCanvas.height);

    robots.forEach((robot) => {
      const { x, y } = robot.location;
      const { mapX, mapY } = worldToMap(
        x,
        y,
        mapCanvasRef.current.width,
        mapCanvasRef.current.height,
        dragOffset.x,
        dragOffset.y,
        mapImage.width,
        mapImage.height,
        mapResolution
      );

      if (isNaN(mapX) || isNaN(mapY)) {
        console.warn(`로봇 ${robot.name}의 좌표가 유효하지 않습니다: x=${x}, y=${y}`);
        return;
      }

      let robotColor = 'gray';
      if (robot.status?.state === 'Tasking') {
        robotColor = 'blue';
      } else if (robot.status?.state === 'Waiting') {
        robotColor = 'yellow';
      } else if (robot.status?.state === 'Error') {
        robotColor = 'red';
      }

      ctx.fillStyle = robotColor;
      ctx.beginPath();
      ctx.arc(mapX, mapY, 5, 0, 2 * Math.PI);
      ctx.fill();
    });

    console.log('로봇 그리기 완료'); // 디버깅용
  };

  const worldToMap = (x, y, mapWidth, mapHeight, offsetX, offsetY, imgWidth, imgHeight, resolution) => {
    console.log('좌표 변환:', { x, y, resolution }); // 디버깅용
    const mapX = (x / resolution) + mapWidth / 2 + offsetX;
    const mapY = mapHeight - ((y / resolution) + mapHeight / 2) + offsetY;
    console.log('변환된 좌표:', { mapX, mapY }); // 디버깅용
    return { mapX, mapY };
  };

  const handleMouseDown = (e) => {
    setDragging(true);
    setLastMousePos({ x: e.clientX, y: e.clientY });
  };

  const handleMouseMove = (e) => {
    if (dragging && mapImage) {
      const deltaX = e.clientX - lastMousePos.x;
      const deltaY = e.clientY - lastMousePos.y;
      let newOffsetX = dragOffset.x + deltaX;
      let newOffsetY = dragOffset.y + deltaY;

      // 지도 이동 제한
      newOffsetX = Math.min(0, Math.max(mapCanvasRef.current.width - mapImage.width, newOffsetX));
      newOffsetY = Math.min(0, Math.max(mapCanvasRef.current.height - mapImage.height, newOffsetY));

      setDragOffset({ x: newOffsetX, y: newOffsetY });
      setLastMousePos({ x: e.clientX, y: e.clientY });
      // 지도와 로봇을 다시 그립니다.
      drawMap();
      drawRobots();
    }
  };

  const handleMouseUp = () => setDragging(false);
  const handleMouseLeave = () => setDragging(false);

  return (
    <div className="test-page-container">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <div className="main-content">
        <div className="map-container">
          {mapUrl ? (
            <>
              {/* 지도 캔버스 */}
              <canvas
                ref={mapCanvasRef}
                width="800"
                height="600"
                className="map-canvas"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseLeave}
              ></canvas>
              {/* 로봇 캔버스 */}
              <canvas
                ref={robotsCanvasRef}
                width="800"
                height="600"
                className="robots-canvas"
              ></canvas>
            </>
          ) : (
            <p>지도를 불러오는 중...</p>
          )}
        </div>

        <div className="robot-list-container">
          <h3>로봇 리스트</h3>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {robots.length > 0 ? (
              robots.map((robot) => (
                <li key={robot._id} className="robot-list-item">
                  <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                    <span><strong>이름:</strong> {robot.name}</span>
                    <span><strong>상태:</strong> {robot.status?.state || 'Unknown'}</span>
                    <span><strong>배터리:</strong> {robot.status?.battery || 'N/A'}%</span>
                    <button
                      onClick={() => toggleExpand(robot._id)}
                      style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '16px' }}
                    >
                      {expandedRobotId === robot._id ? <i className="fas fa-chevron-up"></i> : <i className="fas fa-chevron-down"></i>}
                    </button>
                  </div>
                  <div className={`robot-details ${expandedRobotId === robot._id ? 'expanded' : ''}`}>
                    <p><strong>IP:</strong> {robot.ip}</p>
                    <p><strong>위치:</strong> x: {robot.location.x}, y: {robot.location.y}</p>
                    <p><strong>속도:</strong> {robot.status?.speed || 'N/A'} m/s</p>
                    <p>
                      <strong>job</strong>
                      <button onClick={() => openPopup(robot)} style={{ background: 'none', border: '1px solid #ccc', cursor: 'pointer', fontSize: '16px' }}>+</button>
                    </p>
                  </div>
                </li>
              ))
            ) : (
              <p>연결된 로봇이 없습니다.</p>
            )}
          </ul>
        </div>
      </div>

      {isPopupOpen && selectedRobot && (
        <div className="popup-overlay" onClick={closePopup}>
          <div className="popup-container" onClick={(e) => e.stopPropagation()}>
            <h2>작업 선택 - {selectedRobot.name}</h2>
            <div>
              <label>
                작업을 선택하세요:
                <select value={selectedTask} onChange={handleTaskChange}>
                  <option value="">선택하세요</option>
                  {availableTasks.map((task, index) => (
                    <option key={index} value={task}>{task}</option>
                  ))}
                </select>
              </label>
            </div>
            <button className="popup-close-button" onClick={closePopup}>닫기</button>
          </div>
        </div>
      )}

      <div className="task-log-container">
        <h3>작업 로그</h3>
        <div className="task-log-table-wrapper">
          <table className="task-log-table">
            <thead>
              <tr>
                <th>작업 이름</th>
                <th>로봇 IP</th>
                <th>작업 유형</th>
                <th>상태</th>
                <th>시작 시간</th>
                <th>종료 시간</th>
              </tr>
            </thead>
            <tbody>
              {tasks.length > 0 ? (
                tasks.map((task, index) => (
                  <tr key={index}>
                    <td>{task.name}</td>
                    <td>{task.robotIp}</td>
                    <td>{task.taskType}</td>
                    <td>{task.status}</td>
                    <td>{formatDate(task.startTime)}</td>
                    <td>{formatDate(task.endTime)}</td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" style={{ textAlign: 'center', padding: '10px' }}>작업 로그가 없습니다.</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default TestPage;
