import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import './RobotEditPage.css'; // CSS 파일 적용

function RobotEditPage() {
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [name, setName] = useState(''); // 로봇 이름 상태 추가
  const [description, setDescription] = useState(''); // 로봇 설명 상태 추가

  useEffect(() => {
    fetchRobots();
  }, []);

  const fetchRobots = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.15:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setRobots(response.data);

      // 첫 번째 로봇을 기본 선택
      if (response.data.length > 0) {
        setSelectedRobot(response.data[0]);
        setName(response.data[0].name); // 첫 로봇 이름 가져오기
        setDescription(response.data[0].description); // 첫 로봇 설명 가져오기
      }
    } catch (error) {
      console.error('Error fetching robots:', error);
    }
  };

  const handleSelectRobot = (robot) => {
    setSelectedRobot(robot);
    setName(robot.name); // 선택된 로봇 이름 설정
    setDescription(robot.description); // 선택된 로봇 설명 설정
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.15:5559/robot/update/${selectedRobot._id}`, {
        name,
        description
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Robot updated successfully');
      fetchRobots(); // 업데이트 후 로봇 목록 다시 가져오기
    } catch (error) {
      console.error('Error updating robot:', error);
      alert(`Error updating robot: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleDelete = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    if (window.confirm('Are you sure you want to delete this robot?')) {
      try {
        await axios.put(`http://172.30.1.15:5559/robot/unregister/${selectedRobot._id}`, {}, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        alert('Robot removed successfully');
        setSelectedRobot(null); // 선택된 로봇 초기화
        fetchRobots(); // 목록 새로고침
      } catch (error) {
        console.error('Error removing robot:', error);
        alert(`Error removing robot: ${error.response?.data?.message || error.message}`);
      }
    }
  };

  return (
    <div className="robot-edit-page">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar /> {/* 상단 네비게이션 바 */}
      <div className="main-content">
        <div className="robot-list-container">
          <h3>Your Robots</h3>
          <ul className="robot-list">
            {robots.map((robot) => (
              <li
                key={robot._id}
                onClick={() => handleSelectRobot(robot)}
                className={selectedRobot && selectedRobot._id === robot._id ? 'selected' : ''}
              >
                {robot.name} - {robot.ip} - Battery: {robot.status.battery}%
              </li>
            ))}
          </ul>
        </div>
        <div className="robot-details-container">
          {selectedRobot ? (
            <form className="robot-form">
              <div className="robot-field">
                <label>Robot Name:</label>
                <input
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  className="input-field"
                  placeholder="Enter robot name"
                />
              </div>
              <div className="robot-field">
                <label>Robot Description:</label>
                <textarea
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows="4"
                  className="input-field"
                  placeholder="Enter robot description"
                />
              </div>

              {/* 배터리 상태 표시 */}
              <div className="robot-field">
                <label>Battery Status:</label>
                <input
                  type="text"
                  value={`${selectedRobot.status.battery}%`}
                  readOnly
                  className="input-field"
                />
              </div>

              {/* 읽기 전용 IP 주소, 모델, 상태, 등록 시간 */}
              <div className="robot-info">
                <div>
                  <p><strong>IP Address:</strong></p>
                  <p>{selectedRobot.ip}</p>
                </div>
                <div>
                  <p><strong>Model:</strong></p>
                  <p>{selectedRobot.model}</p>
                </div>
                <div>
                  <p><strong>Status:</strong></p>
                  <p>{selectedRobot.status.state}</p>
                </div>
                <div>
                  <p><strong>Registration Time:</strong></p>
                  <p>{new Date(selectedRobot.createdAt).toLocaleString()}</p>
                </div>
              </div>
            </form>
          ) : (
            <p>Please select a robot to view and edit details</p>
          )}
        </div>
      </div>

      {/* 오른쪽 하단에 고정된 플로팅 버튼 */}
      <div className="floating-buttons">
        <button className="floating-delete-button" onClick={handleDelete}>
          DELETE
        </button>
        <button className="floating-save-button" onClick={handleUpdate}>
          SAVE
        </button>
      </div>
    </div>
  );
}

export default RobotEditPage;
