import React, { useState } from 'react';
import './Dashboard.css';

function Dashboard() {
    const [activeTabs, setActiveTabs] = useState([]);

    // 좌측 네비게이션 바 항목
    const sidebarItems = [
        '종합 대시보드',
        '개별 대시보드',
        '최대수요전력 관리',
        '검침 데이터 조회',
        '에너지 사용 요약',
        '에너지 사용량 조회',
        '공정별 에너지 사용량 비교'
    ];

    // 탭 열기 함수
    const handleSidebarClick = (tabName) => {
        if (!activeTabs.includes(tabName)) {
            setActiveTabs([...activeTabs, tabName]);
        }
    };

    // 탭 닫기 함수
    const handleTabClose = (tabName) => {
        setActiveTabs(activeTabs.filter((tab) => tab !== tabName));
    };

    return (
        <div className="dashboard-layout">
            {/* 좌측 네비게이션 바 */}
            <div className="sidebar">
                <div className="sidebar-header">
                    <h2>FEMS</h2>
                    <p>개발자_관리자</p>
                </div>
                <ul className="menu">
                    {sidebarItems.map((item, index) => (
                        <li
                            key={index}
                            className="menu-item"
                            onClick={() => handleSidebarClick(item)}
                        >
                            {item}
                        </li>
                    ))}
                </ul>
            </div>

            {/* 메인 콘텐츠 */}
            <div className="main-container">
                {/* 동적 탭 바 */}
                <div className="tab-bar">
                    {activeTabs.map((tab, index) => (
                        <div key={index} className="tab">
                            <span>{tab}</span>
                            <button
                                className="close-button"
                                onClick={() => handleTabClose(tab)}
                            >
                                ✕
                            </button>
                        </div>
                    ))}
                </div>

                <div className="dashboard-content">
                    {activeTabs.length > 0 ? (
                        activeTabs.map((tab, index) => (
                            <div key={index} className="tab-content">
                                <h2>{tab}</h2>
                                <p>이곳에 {tab} 콘텐츠가 표시됩니다.</p>
                            </div>
                        ))
                    ) : (
                        <div className="empty-content">
                            <h2>좌측 네비게이션 바에서 항목을 선택하세요.</h2>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default Dashboard;
