// src/components/MapManage/NodeConnectionPopup.js

import React, { useState } from 'react';
import './NodeConnectionPopup.css';

function NodeConnectionPopup({ onClose, onCreate, connectionData, setSelectionMode }) {
  const [errorMessage, setErrorMessage] = useState('');

  // 연결 생성 핸들러
  const handleCreate = (e) => {
    e.stopPropagation(); // 이벤트 전파 중단
    if (!connectionData.node1 || !connectionData.node2) {
      setErrorMessage('노드 1과 노드 2를 모두 선택해주세요.');
      return;
    }
    setErrorMessage('');
    onCreate(connectionData);
  };

  // 노드 1 설정 핸들러
  const handleSetNode1 = (e) => {
    e.stopPropagation(); // 이벤트 전파 중단
    setSelectionMode('selectingNode1');
  };

  // 노드 2 설정 핸들러
  const handleSetNode2 = (e) => {
    e.stopPropagation(); // 이벤트 전파 중단
    setSelectionMode('selectingNode2');
  };

  // 웨이포인트 추가 핸들러
  const handleAddWaypoint = (e) => {
    e.stopPropagation(); // 이벤트 전파 중단
    setSelectionMode('selectingWaypoint');
  };

  // 웨이포인트 제거 핸들러
  const handleRemoveWaypoint = (e, index) => {
    e.stopPropagation(); // 이벤트 전파 중단
    const updatedWaypoints = [...connectionData.waypoints];
    updatedWaypoints.splice(index, 1);
    onCreate({ ...connectionData, waypoints: updatedWaypoints });
  };

  // 웨이포인트 설정 핸들러 (필요 시 사용)
  const handleSetWaypoint = (e, index) => {
    e.stopPropagation(); // 이벤트 전파 중단
    setSelectionMode(`selectingWaypoint_${index}`);
  };

  return (
    <div className="slide-overlay" onClick={onClose}>
      <div className="node-connection-slide-panel" onClick={(e) => e.stopPropagation()}>
        <h3>노드 연결 설정</h3>

        {/* 선택 단계에 대한 안내 메시지 추가 */}
        <div className="selection-instructions">
          {setSelectionMode === 'selectingNode1' && <p>노드 1을 선택하세요.</p>}
          {setSelectionMode === 'selectingNode2' && <p>노드 2를 선택하세요.</p>}
          {setSelectionMode.startsWith('selectingWaypoint') && <p>웨이포인트를 선택하세요.</p>}
        </div>

        {/* 에러 메시지 표시 */}
        {errorMessage && <div className="error-message">{errorMessage}</div>}

        <div className="input-group">
          <label>노드 1</label>
          <div className="coordinate-input">
            <input
              type="text"
              readOnly
              value={connectionData.node1 ? connectionData.node1.name : '선택하세요'}
            />
            <button onClick={handleSetNode1}>set</button>
          </div>
        </div>

        <div className="input-group">
          <label>노드 2</label>
          <div className="coordinate-input">
            <input
              type="text"
              readOnly
              value={connectionData.node2 ? connectionData.node2.name : '선택하세요'}
            />
            <button onClick={handleSetNode2}>set</button>
          </div>
        </div>

        <h4>경유지 (웨이포인트)</h4>
        {connectionData.waypoints.map((wp, index) => (
          <div key={index} className="waypoint-input-group">
            <label>웨이포인트 {index + 1}</label>
            <div className="coordinate-input">
              <input
                type="text"
                readOnly
                value={
                  wp.x !== null && wp.y !== null
                    ? `X: ${wp.x.toFixed(2)}, Y: ${wp.y.toFixed(2)}`
                    : '선택하세요'
                }
              />
              {/* 웨이포인트를 설정하는 버튼을 추가하려면 아래 버튼을 사용할 수 있습니다.
              <button onClick={(e) => handleSetWaypoint(e, index)}>set</button> 
              */}
            </div>
            {/* 마이너스 버튼 클릭 시 이벤트 전파 중단 */}
            <button
              className="remove-btn"
              onClick={(e) => handleRemoveWaypoint(e, index)}
            >
              -
            </button>
          </div>
        ))}
        <button
          className="add-btn"
          onClick={handleAddWaypoint}
        >
          +
        </button>

        {/* 생성 및 취소 버튼 */}
        <div className="button-group">
          <button className="create-btn" onClick={handleCreate}>
            생성
          </button>
          <button
            className="cancel-btn"
            onClick={onClose}
          >
            취소
          </button>
        </div>
      </div>
    </div>
  );
}

export default NodeConnectionPopup;
