import React, { useState, useEffect, useRef } from 'react';
import './TestPage.css'; // CSS 파일 임포트
import axios from 'axios';
import RobotListComponent from './RobotListComponent'; // 로봇 리스트 컴포넌트
import TaskLogComponent from './TaskLogComponent'; // 작업 로그 컴포넌트

function TestPage() {
  const [mapUrl, setMapUrl] = useState(null);
  const canvasRef = useRef(null);
  const [robots, setRobots] = useState([]);
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetchMonitoredMap();
    fetchRobots();
    fetchAllTasks();
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      window.location.href = '/login'; // 토큰이 없으면 로그인 페이지로 리디렉션
      return null;
    }
    return token;
  };

  const fetchMonitoredMap = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://localhost:5557/map/monitored/file', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });

      const url = URL.createObjectURL(response.data);
      setMapUrl(url);

      const img = new Image();
      img.src = url;
      img.onload = () => {
        const canvas = canvasRef.current;
        if (canvas) {
          const ctx = canvas.getContext('2d');
          canvas.width = img.width;
          canvas.height = img.height;
          ctx.drawImage(img, 0, 0, img.width, img.height);
        }
      };
    } catch (error) {
      console.error('모니터링 지도 가져오기 오류:', error);
    }
  };

  const fetchRobots = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://localhost:5559/robot/robots', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setRobots(response.data);
    } catch (error) {
      console.error('로봇 정보 가져오기 오류:', error);
    }
  };

  const fetchAllTasks = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://localhost:8080/task/all', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(response.data);
    } catch (error) {
      console.error('작업 로그 가져오기 오류:', error);
    }
  };

  return (
    <div className="test-page">
      <header className="header">
        <h1>로봇 제어 시스템</h1>
      </header>

      <div className="main-content">
        {/* 지도 영역 */}
        <div className="map-canvas-container">
          {mapUrl ? (
            <canvas ref={canvasRef} className="canvas"></canvas>
          ) : (
            <p>지도를 불러오는 중...</p>
          )}
        </div>

        {/* 로봇 리스트 영역 */}
        <RobotListComponent robots={robots} />
      </div>

      {/* 작업 로그 슬라이드 패널 */}
      <TaskLogComponent tasks={tasks} />
    </div>
  );
}

export default TestPage;
