import React, { useState, useEffect } from 'react';
import './TaskFlow.css'; // 스타일 파일 임포트
import CreateTaskFlow from './CreateTaskFlow';

function TaskFlow({ taskFlows: initialTaskFlows }) {
    const [taskFlows, setTaskFlows] = useState(initialTaskFlows || []); // 전달된 작업 플로우 목록 사용
    const [isPopupOpen, setIsPopupOpen] = useState(false); // 팝업 상태 관리

    useEffect(() => {
        // 전달된 작업 플로우 목록을 업데이트
        setTaskFlows(initialTaskFlows || []);
    }, [initialTaskFlows]);

    // 팝업 열기/닫기 토글
    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    // CreateTaskFlow 컴포넌트로부터 새로운 작업 플로우를 추가하는 함수
    const handleTaskFlowAdd = (nodeName, stepName) => {
        if (nodeName.trim() === '' || stepName.trim() === '') return; // 빈 값일 경우 추가하지 않음
        setTaskFlows([...taskFlows, { node: nodeName, step: stepName }]); // 새로운 플로우 추가
        togglePopup(); // 팝업 닫기
    };

    return (
        <div className="task-flow-container">
            <div className="task-flow-background">
                <div className="task-flow-list">
                    {taskFlows.map((flow, index) => (
                        <div key={index} className="task-flow-item-box"> {/* 네모 상자 */}
                            <div className="task-flow-item-content">
                                <div><strong>노드:</strong> {flow.node}</div>
                                <div><strong>단계:</strong> {flow.step}</div>
                            </div>
                        </div>
                    ))}
                    {/* + 버튼 클릭 시 팝업 열기 */}
                    <div className="task-flow-add">
                        <button onClick={togglePopup} className="task-flow-button">+</button>
                    </div>
                </div>
            </div>
            {/* 팝업 창 - CreateTaskFlow */}
            {isPopupOpen && (
                <div className="popup-overlay">
                    <div className="popup-content">
                        <CreateTaskFlow onClose={togglePopup} onTaskFlowAdd={handleTaskFlowAdd} />
                    </div>
                </div>
            )}
        </div>
    );
}

export default TaskFlow;
