import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './CreateTaskFlow.css'; // 스타일 파일 임포트

// 월드 좌표를 맵 좌표로 변환하는 함수 (배율 및 원점을 고려)
const mapResolution = 0.05; // 미터 당 픽셀 (meters per pixel)
const mapOrigin = [-10.0, -10.0]; // 맵의 원점 좌표 (x, y)

const worldToMap = (worldX, worldY, imageNaturalWidth, imageNaturalHeight) => {
    const mapX = (worldX - mapOrigin[0]) / mapResolution;
    const mapY = imageNaturalHeight - (worldY - mapOrigin[1]) / mapResolution; // Y 좌표 반전
    return { x: mapX, y: mapY };
};

function CreateTaskFlow({ onClose, onTaskFlowAdd }) {
    const [nodeName, setNodeName] = useState('');
    const [stepName, setStepName] = useState('');
    const [nodes, setNodes] = useState([]); // 노드 목록 상태
    const [mapImage, setMapImage] = useState(null); // 지도 이미지 상태
    const canvasRef = useRef(null); // 캔버스 참조
    const mapImageRef = useRef(null); // 이미지 참조
    const [imageNaturalWidth, setImageNaturalWidth] = useState(0);
    const [imageNaturalHeight, setImageNaturalHeight] = useState(0);
    const [monitoredMap, setMonitoredMap] = useState(null); // 모니터링 중인 맵 정보

    // 모니터링 중인 맵 정보를 가져오는 함수
    const fetchMonitoredMap = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://172.30.1.30:5557/map/monitored', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setMonitoredMap(response.data); // 모니터링 중인 맵 정보 설정
            fetchNodes(response.data._id); // 해당 맵의 노드 가져오기
            loadMapImage(response.data._id); // 해당 맵의 이미지 가져오기
        } catch (error) {
            console.error('모니터링 중인 맵을 가져오는 중 오류 발생:', error);
            alert('모니터링 중인 맵을 가져오는 데 실패했습니다.');
        }
    };

    // 노드를 가져오는 함수
    const fetchNodes = async (mapId) => {
        try {
            const response = await axios.get(`http://172.30.1.30:5557/map/nodes/${mapId}`);
            if (response.status === 200) {
                setNodes(response.data); // 노드 리스트 상태 업데이트
            } else {
                setNodes([]); // 실패 시 빈 배열로 초기화
            }
        } catch (error) {
            console.error('노드를 가져오는 중 오류 발생:', error);
            setNodes([]); // 오류 발생 시에도 빈 배열로 초기화
            alert('노드를 가져오는 데 실패했습니다.');
        }
    };

    // 맵 이미지 로드 함수
    const loadMapImage = async (mapId) => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const token = localStorage.getItem('token');
        try {
            const response = await axios.get(`http://172.30.1.30:5557/map/file/${mapId}`, {
                headers: { Authorization: `Bearer ${token}` },
                responseType: 'blob',
            });
            const imageUrl = URL.createObjectURL(response.data);
            setMapImage(imageUrl); // 지도 이미지 설정
        } catch (error) {
            console.error('맵 이미지를 가져오는 중 오류 발생:', error);
            alert('맵 이미지를 가져오는 데 실패했습니다.');
        }
    };

    // 이미지가 로드될 때 캔버스에 그리는 함수
    const loadCanvasWithImage = () => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        mapImageRef.current = new Image();
        mapImageRef.current.src = mapImage;

        mapImageRef.current.onload = () => {
            const imgWidth = mapImageRef.current.width;
            const imgHeight = mapImageRef.current.height;
            setImageNaturalWidth(imgWidth);
            setImageNaturalHeight(imgHeight);

            // 캔버스 크기 조정 및 기존 내용 클리어
            canvas.width = imgWidth;
            canvas.height = imgHeight;
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // 지도 이미지를 캔버스에 그리기
            ctx.drawImage(mapImageRef.current, 0, 0, imgWidth, imgHeight);
            drawNodes(); // 노드 그리기
        };
    };

    // 노드 그리기 함수
    const drawNodes = () => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');

        if (!canvas || !imageNaturalWidth || !imageNaturalHeight) return;

        // 기존 노드 그리기 전에 캔버스를 클리어
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // 지도 이미지 다시 그리기
        ctx.drawImage(mapImageRef.current, 0, 0, imageNaturalWidth, imageNaturalHeight);

        // 노드 그리기
        nodes.forEach((node) => {
            const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);

            // 노드를 빨간 정사각형으로 그리기
            const size = 10; // 정사각형의 크기
            ctx.fillStyle = 'red';
            ctx.fillRect(x - size / 2, y - size / 2, size, size); // 중심을 기준으로 그리기
        });
    };

    // 컴포넌트가 마운트될 때 모니터링 중인 맵 정보 가져오기
    useEffect(() => {
        fetchMonitoredMap();
    }, []);

    // 지도 이미지가 변경될 때마다 캔버스에 그리기
    useEffect(() => {
        if (mapImage) {
            loadCanvasWithImage();
        }
    }, [mapImage]);

    // 작업 플로우 추가 처리
    const handleSubmit = () => {
        onTaskFlowAdd(nodeName, stepName); // 부모 컴포넌트에 작업 플로우 추가 요청
    };

    return (
        <div className="create-task-flow-container">
            {/* 좌측: 지도와 노드를 표시 */}
            <div className="map-preview">
                <canvas ref={canvasRef} style={{ border: '1px solid #ccc', width: '100%', height: '100%' }}></canvas>
            </div>

            {/* 우측: 작업 플로우 추가 폼 */}
            <div className="task-flow-form">
                <h3>작업 플로우 추가</h3>
                <input 
                    type="text" 
                    value={nodeName} 
                    onChange={(e) => setNodeName(e.target.value)} 
                    placeholder="노드 이름 입력"
                />
                <input 
                    type="text" 
                    value={stepName} 
                    onChange={(e) => setStepName(e.target.value)} 
                    placeholder="단계 이름 입력"
                />
                <div className="buttons">
                    <button onClick={handleSubmit}>추가</button>
                    <button onClick={onClose}>취소</button>
                </div>
            </div>
        </div>
    );
}

export default CreateTaskFlow;
