import React, { useState, forwardRef, useImperativeHandle } from 'react';
import './TaskFlow.css';
import CreateTaskFlow from './CreateTaskFlow';

const TaskFlow = forwardRef(({ workflow, onTemporaryFlowAdd, onTemporaryFlowDelete }, ref) => {
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [temporaryWorkflow, setTemporaryWorkflow] = useState([]);

    // 팝업 열기/닫기 토글
    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    // CreateTaskFlow 컴포넌트로부터 새로운 작업 플로우를 추가하는 함수
    const handleTaskFlowAdd = (nodeName, stepName) => {
        if (nodeName.trim() === '' || stepName.trim() === '') return;
        const newFlow = { node: nodeName, step: stepName };
        const updatedTemporaryWorkflow = [...temporaryWorkflow, newFlow];
        setTemporaryWorkflow(updatedTemporaryWorkflow);
        onTemporaryFlowAdd(newFlow);
        togglePopup();
    };

    // 개별 워크플로우 항목을 삭제하는 함수
    const handleRemoveFlow = (index) => {
        const removedFlow = temporaryWorkflow[index];
        const updatedWorkflow = temporaryWorkflow.filter((_, i) => i !== index);
        setTemporaryWorkflow(updatedWorkflow);
        onTemporaryFlowDelete(removedFlow); // 삭제된 항목을 TaskPage.js에 전달
    };

    // 임시 플로우 초기화 함수
    useImperativeHandle(ref, () => ({
        clearTemporaryWorkflow() {
            setTemporaryWorkflow([]);
        }
    }));

    return (
        <div className="task-flow-container">
            <div className="task-flow-background">
                <div className="task-flow-list">
                    {/* 저장된 workflow와 임시 저장된 temporaryWorkflow 병합하여 표시 */}
                    {workflow.concat(temporaryWorkflow).map((flow, index) => (
                        <div key={index} className="task-flow-item">
                            <div>
                                <strong></strong> {flow.node}
                            </div>
                            <div>
                                <strong></strong> {flow.step}
                            </div>
                            {/* 삭제 버튼 추가 */}
                            {index >= workflow.length && (
                                <button
                                    className="task-flow-remove-btn"
                                    onClick={() => handleRemoveFlow(index - workflow.length)}
                                >
                                    ×
                                </button>
                            )}
                        </div>
                    ))}
                    <div className="task-flow-add">
                        <button onClick={togglePopup} className="task-flow-button">+</button>
                    </div>
                </div>
            </div>
            {isPopupOpen && (
                <div className="popup-overlay">
                    <div className="popup-content">
                        <CreateTaskFlow onClose={togglePopup} onTaskFlowAdd={handleTaskFlowAdd} />
                    </div>
                </div>
            )}
        </div>
    );
});

export default TaskFlow;
