import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';

function RobotManagePage() {
  console.log("1: RobotManagePage component rendered");

  const [tasks, setTasks] = useState([]);
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [message, setMessage] = useState('');

  // Get token from localStorage
  const getToken = () => {
    const token = localStorage.getItem('token');
    console.log("2: Token retrieved:", token);
    if (!token) {
      alert('No token found, please log in again.');
      window.location.href = '/login'; // Redirect to login if token is missing
      return null;
    }
    return token;
  };

  // Fetch all tasks from the API
  const fetchTasks = async () => {
    console.log("3: fetchTasks function called");
    const token = getToken();
    if (!token) {
      console.log("4: No token found, exiting fetchTasks");
      return; // Exit if no token is found
    }

    try {
      console.log("5: Sending API request to fetch tasks");
      const response = await axios.get('http://172.30.1.28:8080/task/all', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      console.log("6: API response received:", response.data);
      setTasks(response.data);
    } catch (error) {
      console.log("7: Error occurred during API request:", error);
      if (error.response) {
        setMessage(`Failed to retrieve tasks: ${error.response.status} - ${error.response.data?.message || error.message}`);
      } else if (error.request) {
        setMessage('No response from server. Please check your network or server status.');
        console.error('No response:', error.request);
      } else {
        setMessage('Error: Failed to retrieve tasks.');
        console.error('Error:', error.message);
      }
    }
  };

  // Fetch all robots from the API
  const fetchRobots = async () => {
    console.log("Fetching robots");
    const token = getToken();
    if (!token) {
      console.log("No token found, exiting fetchRobots");
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setRobots(response.data);
    } catch (error) {
      console.error('Error fetching robots:', error);
    }
  };

  // Handle robot selection
  const handleSelectRobot = (robot) => {
    setSelectedRobot(robot);
  };

  // Fetch tasks and robots on component mount
  useEffect(() => {
    console.log("8: useEffect called");
    fetchTasks();
    fetchRobots();
  }, []);

  // Helper function to format date or show "N/A" if date is invalid
  const formatDate = (date) => {
    return date ? new Date(date).toLocaleString() : 'N/A';
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <div style={{ display: 'flex' }}>
        <Navbar />
      </div>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Robot and Task Management</h2>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        {/* Left side - Robot list */}
        <div style={{ flex: '1', padding: '20px', borderRight: '1px solid #ccc' }}>
          <h3>Robot List</h3>
          {robots.length === 0 && <p>No robots available.</p>}
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {robots.map((robot) => (
              <li
                key={robot._id}
                onClick={() => handleSelectRobot(robot)}
                style={{ padding: '10px', borderBottom: '1px solid #ccc', marginBottom: '10px', cursor: 'pointer' }}
              >
                <strong>Robot Name:</strong> {robot.name} <br />
                <strong>Model:</strong> {robot.model} <br />
                <strong>IP:</strong> {robot.ip} <br />
                <strong>Location:</strong> ({robot.location.x}, {robot.location.y})
              </li>
            ))}
          </ul>
          {selectedRobot && (
            <div style={{ marginTop: '20px', padding: '20px', border: '1px solid #ccc' }}>
              <h4>Selected Robot Details</h4>
              <p><strong>Name:</strong> {selectedRobot.name}</p>
              <p><strong>Model:</strong> {selectedRobot.model}</p>
              <p><strong>IP:</strong> {selectedRobot.ip}</p>
              <p><strong>Location:</strong> ({selectedRobot.location.x}, {selectedRobot.location.y})</p>
              <p><strong>Battery:</strong> {selectedRobot.status.battery}%</p>
              <p><strong>Status:</strong> {selectedRobot.status.state}</p>
            </div>
          )}
        </div>
        {/* Right side - Task list */}
        <div style={{ flex: '1', padding: '20px' }}>
          <h3>All Tasks</h3>
          {message && <p>{message}</p>}
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {tasks.map((task) => (
              <li key={task._id} style={{ padding: '10px', borderBottom: '1px solid #ccc', marginBottom: '10px' }}>
                <strong>Task Name:</strong> {task.name} <br />
                <strong>Task Type:</strong> {task.taskType} <br />
                <strong>Status:</strong> {task.status} <br />
                <strong>Start Time:</strong> {formatDate(task.startTime)} <br />
                {task.endTime && (
                  <>
                    <strong>End Time:</strong> {formatDate(task.endTime)} <br />
                  </>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default RobotManagePage;
