import React, { useEffect } from 'react';
import ROSLIB from 'roslib';
import { exec } from 'child_process';
import './TeleopControl.css';

function TeleopControl({ robotName, onClose }) {
  // ROS와 연결 설정
  const ros = new ROSLIB.Ros({
    url: 'ws://localhost:9090', // rosbridge WebSocket 주소
  });

  // roslaunch 명령어 실행
  let rosbridgeProcess = exec('roslaunch rosbridge_server rosbridge_websocket.launch', (error, stdout, stderr) => {
    if (error) {
      console.error('Failed to start rosbridge websocket:', error);
    }
    if (stdout) console.log(`rosbridge stdout: ${stdout}`);
    if (stderr) console.warn(`rosbridge stderr: ${stderr}`);
  });

  // cmd_vel 토픽 설정, 로봇의 이름을 포함하여 동적으로 설정
  const cmdVel = new ROSLIB.Topic({
    ros: ros,
    name: `/${robotName}/cmd_vel`, // 로봇 이름을 포함한 토픽 경로
    messageType: 'geometry_msgs/Twist',
  });

  // 로봇에 명령을 보내는 함수
  const sendCommand = (linear, angular) => {
    const twist = new ROSLIB.Message({
      linear: { x: linear, y: 0.0, z: 0.0 },
      angular: { x: 0.0, y: 0.0, z: angular },
    });
    cmdVel.publish(twist);
  };

  // 키보드 이벤트 핸들러
  const handleKeyDown = (event) => {
    switch (event.key) {
      case 'ArrowUp':
        sendCommand(0.5, 0); // 전진
        break;
      case 'ArrowDown':
        sendCommand(-0.5, 0); // 후진
        break;
      case 'ArrowLeft':
        sendCommand(0, 0.5); // 좌회전
        break;
      case 'ArrowRight':
        sendCommand(0, -0.5); // 우회전
        break;
      case ' ': // 스페이스바
        sendCommand(0, 0); // 정지
        break;
      default:
        break;
    }
  };

  // 버튼 클릭 이벤트 핸들러
  const handleButtonClick = (direction) => {
    switch (direction) {
      case 'forward':
        sendCommand(0.5, 0); // 전진
        break;
      case 'backward':
        sendCommand(-0.5, 0); // 후진
        break;
      case 'left':
        sendCommand(0, 0.5); // 좌회전
        break;
      case 'right':
        sendCommand(0, -0.5); // 우회전
        break;
      case 'stop':
        sendCommand(0, 0); // 정지
        break;
      default:
        break;
    }
  };

  // 키보드 이벤트 리스너 추가 및 제거
  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);

    // 컴포넌트가 언마운트될 때 rosbridge 서버와 이벤트 리스너 종료
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      if (rosbridgeProcess) {
        rosbridgeProcess.kill(); // rosbridge 프로세스 종료
        console.log('rosbridge websocket stopped');
      }
      ros.close();
      console.log('ROS connection closed');
    };
  }, []);

  return (
    <div className="popup-overlay">
      <div className="popup">
        <h3>{robotName} Teleop Control</h3>
        <div className="teleop-controls">
          <p>Use arrow keys or buttons to control the robot. Press space to stop.</p>
          <div className="button-controls">
            <button onClick={() => handleButtonClick('forward')}>▲</button>
            <div>
              <button onClick={() => handleButtonClick('left')}>◀</button>
              <button onClick={() => handleButtonClick('stop')}>■</button>
              <button onClick={() => handleButtonClick('right')}>▶</button>
            </div>
            <button onClick={() => handleButtonClick('backward')}>▼</button>
          </div>
        </div>
        <button
          onClick={() => {
            if (onClose) onClose();
            if (rosbridgeProcess) {
              rosbridgeProcess.kill(); // Close 버튼으로 rosbridge 종료
              console.log('rosbridge websocket stopped by Close button');
            }
          }}
          className="close-btn"
        >
          Close
        </button>
      </div>
    </div>
  );
}

export default TeleopControl;
