import React, { useState } from 'react';
import './TaskFlow.css'; // 스타일 파일 임포트

function TaskFlow() {
    const [taskFlows, setTaskFlows] = useState([ // 예시 데이터
        { node: '노드 1', step: '단계 1' },
        { node: '노드 2', step: '단계 2' }
    ]); // 기본 작업 플로우 목록

    return (
        <div className="task-flow-container">
            <div className="task-flow-background">
                <div className="task-flow-list">
                    {taskFlows.map((flow, index) => (
                        <div key={index} className="task-flow-item">
                            <div><strong>노드:</strong> {flow.node}</div>
                            <div><strong>단계:</strong> {flow.step}</div>
                        </div>
                    ))}
                    {/* 마지막에 + 버튼이 있는 큰 둥근 사각형 */}
                    <div className="task-flow-add">
                        <button className="task-flow-button">+</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default TaskFlow;
