// src/components/MapManage/Mapmanage.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faPlus, faLink, faUnlink } from '@fortawesome/free-solid-svg-icons';

import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css';
import { worldToMap, mapToWorld } from '../../utils/coordinateUtils';
import NodeList from './NodeList'; // NodeList 컴포넌트 임포트
import NodeDisconnect from './NodeDisconnect'; // NodeDisconnect 컴포넌트 임포트

function Mapmanage() {
  // ... 기존 상태 변수 정의 ...

  // 연결 해제 성공 시 처리 함수
  const handleDisconnectSuccess = (isDeleted) => {
    if (isDeleted) {
      alert('노드 간의 연결이 성공적으로 해제되었습니다.');
      setDisconnectFirstNodeId(null);
      setDisconnectSecondNodeId(null);
      fetchNodes(selectedMap._id);
      drawAll();
    } else {
      alert('노드 간의 연결 해제에 실패했습니다.');
    }
  };

  return (
    <div className="map-management-page">
      {/* 상단 네비게이션 바 */}
      <header className="map-management-header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      {/* 툴바 */}
      <div className="toolbar">
        <button onClick={handleCreateNodeMode} className="create-node-button">
          <FontAwesomeIcon icon={faPlus} /> 노드 생성
        </button>
        <button onClick={handleDeleteNodeMode} className="delete-node-button">
          <FontAwesomeIcon icon={faEdit} /> 노드 삭제
        </button>
        <button onClick={handleConnectNodeMode} className="connect-node-button">
          <FontAwesomeIcon icon={faLink} /> 노드 연결
        </button>
        <button onClick={handleDisconnectNodeMode} className="disconnect-node-button">
          <FontAwesomeIcon icon={faUnlink} /> 연결 해제
        </button>
        <button onClick={handleCreateNoGoZone} className="create-nogozone-button">
          금지 구역 생성
        </button>
        <button onClick={handleDeleteNoGoZoneMode} className="delete-nogozone-button">
          금지 구역 해제
        </button>
      </div>

      {/* 맵 리스트와 이미지 컨텐츠 */}
      <div className="map-management-content">
        {/* 맵 리스트 컨테이너 */}
        <div className="map-list-container">
          <h2 className="map-list-header">
            맵 리스트
            <button onClick={() => setIsUploadPopupOpen(true)} className="add-map-button">
              <FontAwesomeIcon icon={faPlus} />
            </button>
          </h2>
          <ul className="map-list">
            {maps.map((map) => (
              <li
                key={map._id}
                className={`map-list-item ${selectedMap?._id === map._id ? 'selected-map' : ''}`}
              >
                <span onClick={() => handleSelectMap(map)} className="map-list-item-name">
                  {map.name}
                </span>
                <button onClick={() => openEditPopup(map)} className="edit-map-button">
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button onClick={() => handleDeleteMap(map._id)} className="delete-map-button">
                  &times;
                </button>
              </li>
            ))}
          </ul>

          {selectedMap && (
            <div className="map-description">
              <h3>맵 설명</h3>
              <p>{selectedMap.description}</p>
            </div>
          )}
        </div>

        {/* 지도 이미지와 노드 렌더링 및 노드 리스트 영역 */}
        <div className="map-image-and-node-list">
          {/* 지도 이미지와 노드 렌더링 영역 */}
          <div className="map-image-container" style={{ position: 'relative' }}>
            {mapImage ? (
              <canvas
                ref={canvasRef}
                className="map-canvas"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseLeave}
                onClick={handleMapClick} // 맵 클릭 이벤트 추가
                style={{
                  cursor:
                    isCreatingNode
                      ? 'crosshair'
                      : isCreatingNoGoZone
                      ? 'crosshair'
                      : isDeletingNoGoZone
                      ? 'not-allowed'
                      : connectionStep !== 'idle' || disconnectStep !== 'idle'
                      ? 'pointer'
                      : 'default',
                }} // 커서 스타일 수정
              />
            ) : (
              <p>맵을 선택하여 이미지를 확인하세요.</p>
            )}

            {/* 플로팅 Send to Robots 버튼과 Map Patch 버튼 추가 */}
            {selectedMap && (
              <div className="floating-buttons">
                <button className="send-to-robots-button" onClick={handleSendMap}>
                  로봇에게 전송
                </button>
                <button className="map-patch-button" onClick={handlePatchMap} disabled={isPatchingMap}>
                  {isPatchingMap ? '맵 패치 중...' : '맵 패치'}
                </button>
              </div>
            )}
          </div>

          {/* 오른쪽에 노드 목록 표시 */}
          <NodeList mapId={selectedMap?._id} /> {/* NodeList 컴포넌트 사용 */}
        </div>
      </div>

      {/* Node 생성 팝업 */}
      {isNodePopupOpen && (
        <div className="popup-container">
          <h3>노드 생성</h3>
          <p>X: {nodeX.toFixed(2)}, Y: {nodeY.toFixed(2)}</p>
          <input
            type="text"
            value={nodeName}
            onChange={(e) => setNodeName(e.target.value)}
            placeholder="노드 이름 입력"
            className="popup-input"
          />
          <div className="popup-button-container">
            <button onClick={handleSubmitNode} className="popup-submit-button">
              생성
            </button>
            <button onClick={() => setIsNodePopupOpen(false)} className="popup-cancel-button">
              취소
            </button>
          </div>
        </div>
      )}

      {/* Map Upload Popup */}
      {isUploadPopupOpen && (
        <div className="popup-container">
          <h3>맵 업로드</h3>
          <form onSubmit={handleUploadMap}>
            <input
              type="text"
              value={uploadName}
              onChange={(e) => setUploadName(e.target.value)}
              placeholder="맵 이름"
              required
              className="popup-input"
            />
            <textarea
              value={uploadDescription}
              onChange={(e) => setUploadDescription(e.target.value)}
              placeholder="맵 설명"
              required
              className="popup-textarea"
            />
            <label>맵 파일 (.pgm, .png, .jpg)</label>
            <input
              type="file"
              accept=".pgm,.png,.jpg"
              onChange={handleFileChange}
              required
              className="popup-file-input"
            />
            <label>YAML 파일 (.yaml)</label>
            <input
              type="file"
              accept=".yaml"
              onChange={handleYamlFileChange}
              required
              className="popup-file-input"
            />
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                업로드
              </button>
              <button type="button" onClick={() => setIsUploadPopupOpen(false)} className="popup-cancel-button">
                취소
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Edit Map Popup */}
      {isEditPopupOpen && (
        <div className="popup-container">
          <h3>맵 수정</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="맵 이름"
              required
              className="popup-input"
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="맵 설명"
              required
              className="popup-textarea"
            />
            {/* 모니터링 지도 설정 체크박스 추가 */}
            <label style={{ marginTop: '10px', color: '#333' }}>
              <input
                type="checkbox"
                checked={isMonitored}
                onChange={(e) => setIsMonitored(e.target.checked)}
                style={{ marginRight: '10px' }}
              />
              모니터링 맵으로 설정
            </label>
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                업데이트
              </button>
              <button type="button" onClick={() => setIsEditPopupOpen(false)} className="popup-cancel-button">
                취소
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Hover Tooltip */}
      {hoveredNode && (
        <div
          className="tooltip"
          style={{
            position: 'fixed',
            top: `${tooltipPosition.y + 10}px`,
            left: `${tooltipPosition.x + 10}px`,
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
            color: 'white',
            padding: '5px 10px',
            borderRadius: '4px',
            pointerEvents: 'none',
            zIndex: 1000,
            fontSize: '12px',
          }}
        >
          {hoveredNode.name}
        </div>
      )}

      {/* NodeDisconnect 컴포넌트를 조건부로 렌더링 */}
      {disconnectFirstNodeId && disconnectSecondNodeId && (
        <NodeDisconnect
          firstNodeId={disconnectFirstNodeId}
          secondNodeId={disconnectSecondNodeId}
          onDisconnectSuccess={handleDisconnectSuccess}
        />
      )}

      {/* 로딩 인디케이터 추가 */}
      {isPatchingMap && (
        <div className="loading-overlay">
          <div className="spinner"></div>
        </div>
      )}
    </div>
  );
}

export default Mapmanage;
