import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import CreateTask from './CreateTask';
import TaskFlow from './TaskFlow';
import './TaskPage.css';

function TaskPage() {
    const [tasks, setTasks] = useState([]);
    const [selectedTask, setSelectedTask] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [monitoredMap, setMonitoredMap] = useState(null);
    const [temporaryWorkflow, setTemporaryWorkflow] = useState([]);
    const [deletedWorkflowNodes, setDeletedWorkflowNodes] = useState([]); // 삭제된 노드들

    const [taskName, setTaskName] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [taskType, setTaskType] = useState('A');

    const taskFlowRef = useRef();
    const token = localStorage.getItem('token');

    useEffect(() => {
        async function fetchTasks() {
            try {
                const mapResponse = await axios.get('http://172.30.1.30:5557/map/monitored', {
                    headers: { Authorization: `Bearer ${token}` },
                });

                const monitoredMap = mapResponse.data;
                setMonitoredMap(monitoredMap);

                if (!monitoredMap || !monitoredMap._id) {
                    console.error('모니터링 중인 맵 정보가 없습니다.');
                    return;
                }

                const taskResponse = await axios.get(`http://172.30.1.30:8080/task/tasks?mapId=${monitoredMap._id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setTasks(taskResponse.data);

                if (taskResponse.data.length > 0) {
                    handleTaskClick(taskResponse.data[0]);
                }
            } catch (error) {
                console.error('작업 리스트를 가져오는 중 오류가 발생했습니다.', error);
            }
        }
        fetchTasks();
    }, [token]);

    const handleTaskClick = (task) => {
        setSelectedTask(task);
        setTaskName(task.name);
        setTaskDescription(task.description || '');
        setTaskType(task.taskType || 'A');
        setTemporaryWorkflow([]);
        setDeletedWorkflowNodes([]);
    };

    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    const handleTaskCreated = (newTask) => {
        setTasks([...tasks, newTask]);
        handleTaskClick(newTask);
    };

    const handleSaveTask = async () => {
        if (selectedTask) {
            try {
                const updatedTask = {
                    ...selectedTask,
                    workflow: [...selectedTask.workflow, ...temporaryWorkflow.filter(flow => !flow.isDeleted)],
                    name: taskName,
                    description: taskDescription,
                    taskType: taskType,
                };

                // 서버에 작업 업데이트 요청
                await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, updatedTask, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                // 삭제된 워크플로우 노드들을 서버에 반영
                for (const nodeId of deletedWorkflowNodes) {
                    await axios.put(
                        `http://172.30.1.30:8080/task/tasks/${selectedTask._id}/workflow/${nodeId}`,
                        null,
                        { headers: { Authorization: `Bearer ${token}` } }
                    );
                }

                // 작업 업데이트 후 상태 새로고침
                const response = await axios.get(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                const updatedSelectedTask = response.data;
                setSelectedTask(updatedSelectedTask);

                setTasks((prevTasks) =>
                    prevTasks.map((task) => (task._id === updatedSelectedTask._id ? updatedSelectedTask : task))
                );

                taskFlowRef.current.clearTemporaryWorkflow();
                setDeletedWorkflowNodes([]);

                alert('작업이 성공적으로 저장되었습니다.');
            } catch (error) {
                console.error('작업 저장 중 오류가 발생했습니다.', error);
                alert('작업 저장 중 오류가 발생했습니다.');
            }
        } else {
            alert('저장할 작업을 선택하세요.');
        }
    };

    const handleTemporaryFlowAdd = (newFlow) => {
        setTemporaryWorkflow([...temporaryWorkflow, newFlow]);
    };

    const handleTemporaryFlowDelete = (deletedFlowId) => {
        setDeletedWorkflowNodes([...deletedWorkflowNodes, deletedFlowId]);
    };

    const handleDeleteTask = async () => {
        if (selectedTask) {
            try {
                await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}/delete`, null, {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setTasks((prevTasks) => {
                    const updatedTasks = prevTasks.filter((task) => task._id !== selectedTask._id);
                    const nextTask = updatedTasks[0] || null;
                    if (nextTask) {
                        handleTaskClick(nextTask);
                    } else {
                        setSelectedTask(null);
                        setTaskName('');
                        setTaskDescription('');
                        setTaskType('A');
                        setTemporaryWorkflow([]);
                    }
                    return updatedTasks;
                });

                alert('작업이 성공적으로 삭제되었습니다.');
            } catch (error) {
                console.error('작업 삭제 중 오류가 발생했습니다.', error);
                alert('작업 삭제 중 오류가 발생했습니다.');
            }
        } else {
            alert('삭제할 작업을 선택하세요.');
        }
    };

    return (
        <div className="page-container">
            <header className="header">
                <UserInfo />
                <LogoutButton />
            </header>
            <Navbar className="navbar" />
            <div className="main-content">
                <div className="task-list">
                    <div className="task-list-header" style={{ display: 'inline-flex', alignItems: 'center', width: '100%' }}>
                        <h3 style={{ margin: 0 }}>작업 리스트</h3>
                        {monitoredMap && (
                            <span style={{ color: 'gray', marginLeft: '10px', fontSize: '14px' }}>
                                - {monitoredMap.name}
                            </span>
                        )}
                        <button className="add-task-btn" onClick={togglePopup} style={{ marginLeft: 'auto' }}>+</button>
                    </div>
                    <ul>
                        {tasks.map((task) => (
                            <li
                                key={task._id}
                                onClick={() => handleTaskClick(task)}
                                className={selectedTask && selectedTask._id === task._id ? 'selected' : ''}
                            >
                                {task.name}
                            </li>
                        ))}
                    </ul>
                </div>

                <div className="task-details">
                    {selectedTask ? (
                        <div>
                            <h3>작업 상세 정보</h3>
                            <label>
                                <strong>작업 이름:</strong>
                                <input
                                    type="text"
                                    className="input-field"
                                    value={taskName}
                                    onChange={(e) => setTaskName(e.target.value)}
                                />
                            </label>
                            <br />
                            <label>
                                <strong>작업 설명:</strong>
                                <textarea
                                    className="textarea-field"
                                    value={taskDescription}
                                    onChange={(e) => setTaskDescription(e.target.value)}
                                />
                            </label>
                            <br />
                            <div className="task-row">
                                <label>
                                    <strong>작업 유형:</strong>
                                    <select
                                        className="select-field"
                                        value={taskType}
                                        onChange={(e) => setTaskType(e.target.value)}
                                    >
                                        <option value="A">A</option>
                                        <option value="B">B</option>
                                        <option value="C">C</option>
                                    </select>
                                </label>
                                <label className="created-date">
                                    <strong>생성일:</strong> {new Date(selectedTask.createdAt).toLocaleDateString()}
                                </label>
                            </div>

                            <h4>작업 플로우</h4>
                            <div className="task-flow-section">
                                <TaskFlow
                                    ref={taskFlowRef}
                                    workflow={selectedTask.workflow}
                                    onTemporaryFlowAdd={handleTemporaryFlowAdd}
                                    onTemporaryFlowDelete={handleTemporaryFlowDelete} // 삭제 시 호출
                                />
                            </div>
                        </div>
                    ) : (
                        <p>작업을 선택하세요.</p>
                    )}
                </div>

                {isPopupOpen && (
                    <CreateTask onClose={togglePopup} onTaskCreated={handleTaskCreated} />
                )}

                <div className="floating-buttons">
                    <button className="assign-task-btn" onClick={() => alert('작업 할당')}>작업 할당</button>
                    <button className="save-task-btn" onClick={handleSaveTask}>작업 저장</button>
                    <button className="delete-task-btn" onClick={handleDeleteTask}>작업 삭제</button>
                </div>
            </div>
        </div>
    );
}

export default TaskPage;
