import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [editName, setEditName] = useState(''); // 맵 수정용 상태
  const [editDescription, setEditDescription] = useState(''); // 맵 수정용 상태
  const [uploadName, setUploadName] = useState(''); // 맵 업로드용 상태
  const [uploadDescription, setUploadDescription] = useState(''); // 맵 업로드용 상태
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.26:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.26:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const handleFileChange = (e) => {
    setMapFile(e.target.files[0]);
  };

  const handleYamlFileChange = (e) => {
    setYamlFile(e.target.files[0]);
  };

  const handleUpdateMap = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.26:5557/map/update/${selectedMap._id}`, {
        name: editName, // 수정된 맵 이름
        description: editDescription, // 수정된 맵 설명
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Map updated successfully');
      fetchMaps();
    } catch (error) {
      console.error('Error updating map:', error);
    }
  };

  const handleUploadMap = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', uploadName); // 업로드용 맵 이름
    formData.append('description', uploadDescription); // 업로드용 맵 설명
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.26:5557/map/upload', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });
      alert('Map uploaded successfully');
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', height: '100vh' }}>
      {/* 상단 네비게이션 바 */}
      <header style={{ display: 'flex', justifyContent: 'space-between', padding: '20px', borderBottom: '1px solid #ccc' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      <div style={{ display: 'flex', height: 'calc(100% - 100px)', padding: '20px' }}>
        {/* Left side: Map List and Map Edit */}
        <div style={{ flex: '1', borderRight: '1px solid #ccc', paddingRight: '20px' }}>
          <h2>Map List</h2>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {maps.map((map) => (
              <li
                key={map._id}
                onClick={() => handleSelectMap(map)}
                style={{ padding: '10px', cursor: 'pointer', borderBottom: '1px solid #ccc' }}
              >
                {map.name}
              </li>
            ))}
          </ul>
          {selectedMap && (
            <div style={{ marginTop: '20px' }}>
              <h3>Edit Map</h3>
              <form onSubmit={handleUpdateMap}>
                <input
                  type="text"
                  value={editName} // 수정용 이름 상태
                  onChange={(e) => setEditName(e.target.value)}
                  placeholder="Map Name"
                  required
                  style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
                />
                <textarea
                  value={editDescription} // 수정용 설명 상태
                  onChange={(e) => setEditDescription(e.target.value)}
                  placeholder="Map Description"
                  required
                  style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
                />
                <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
                  Update Map
                </button>
              </form>
            </div>
          )}
        </div>

        {/* Center: Map Image */}
        <div style={{ flex: '2', padding: '20px', textAlign: 'center' }}>
          <h2>Selected Map</h2>
          {mapImage ? (
            <img src={mapImage} alt="Selected Map" style={{ maxWidth: '100%', height: 'auto' }} />
          ) : (
            <p>Select a map to view its image</p>
          )}
        </div>

        {/* Right side: Map Upload */}
        <div style={{ flex: '1', borderLeft: '1px solid #ccc', paddingLeft: '20px' }}>
          <h2>Upload Map</h2>
          <form onSubmit={handleUploadMap}>
            <input
              type="text"
              value={uploadName} // 업로드용 이름 상태
              onChange={(e) => setUploadName(e.target.value)}
              placeholder="Map Name"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
            />
            <textarea
              value={uploadDescription} // 업로드용 설명 상태
              onChange={(e) => setUploadDescription(e.target.value)}
              placeholder="Map Description"
              required
              style={{ display: 'block', marginBottom: '10px', padding: '10px', width: '100%' }}
            />
            <label>Map File (.pgm, .png, .jpg)</label>
            <input type="file" accept=".pgm,.png,.jpg" onChange={handleFileChange} required style={{ display: 'block', marginBottom: '10px' }} />
            <label>YAML File (.yaml)</label>
            <input type="file" accept=".yaml" onChange={handleYamlFileChange} required style={{ display: 'block', marginBottom: '10px' }} />
            <button type="submit" style={{ padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', cursor: 'pointer' }}>
              Upload Map
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default MapManagementPage;
