// src/components/MapManage/NodeConnectionPopup.js

import React from 'react';
import './NodeConnectionPopup.css';

function NodeConnectionPopup({ onClose, onCreate, connectionData, setSelectionMode }) {
  const handleCreate = () => {
    if (!connectionData.node1 || !connectionData.node2) {
      alert('노드 1과 노드 2를 모두 선택해주세요.');
      return;
    }
    onCreate(connectionData);
  };

  const handleSetNode1 = () => {
    setSelectionMode('selectingNode1');
  };

  const handleSetNode2 = () => {
    setSelectionMode('selectingNode2');
  };

  const handleAddWaypoint = () => {
    setSelectionMode('selectingWaypoint');
  };

  return (
    <div className="popup-container" onClick={onClose}>
      <div className="node-connection-popup" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>
          &times;
        </button>
        <h3>노드 연결 설정</h3>

        <div className="input-group">
          <label>노드 1</label>
          <div className="coordinate-input">
            <input type="text" readOnly value={connectionData.node1 ? connectionData.node1.name : '선택하세요'} />
            <button onClick={handleSetNode1}>셋</button>
          </div>
        </div>

        <div className="input-group">
          <label>노드 2</label>
          <div className="coordinate-input">
            <input type="text" readOnly value={connectionData.node2 ? connectionData.node2.name : '선택하세요'} />
            <button onClick={handleSetNode2}>셋</button>
          </div>
        </div>

        <h4>경유지 (웨이포인트)</h4>
        {connectionData.waypoints.map((wp, index) => (
          <div key={index} className="waypoint-input-group">
            <label>웨이포인트 {index + 1}</label>
            <div className="coordinate-input">
              <input
                type="text"
                readOnly
                value={`X: ${wp.x.toFixed(2)}, Y: ${wp.y.toFixed(2)}`}
              />
              {/* 웨이포인트는 임의의 좌표이므로 '셋' 버튼 대신 삭제 버튼만 제공 */}
            </div>
            <button className="remove-btn" onClick={() => {
              // 웨이포인트 삭제 로직
              const updatedWaypoints = [...connectionData.waypoints];
              updatedWaypoints.splice(index, 1);
              connectionData.waypoints = updatedWaypoints;
              onCreate({ ...connectionData });
            }}>
              -
            </button>
          </div>
        ))}
        <button className="add-btn" onClick={handleAddWaypoint}>+</button>

        <div className="button-group">
          <button className="create-btn" onClick={handleCreate}>생성</button>
          <button className="cancel-btn" onClick={onClose}>취소</button>
        </div>
      </div>
    </div>
  );
}

export default NodeConnectionPopup;
