import React, { useState } from 'react';
import axios from 'axios';

function RobotRegisterPage({ onClose }) { // onClose prop 추가
  const [name, setName] = useState('');
  const [ip, setIp] = useState('');
  const [model, setModel] = useState('');
  const [sshUser, setSshUser] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (name.length < 2) {
      alert('Robot name must be at least 2 characters long');
      return;
    }

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.post('http://172.30.1.30:5559/robot/register', { name, ip, model, sshUser }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Robot registered successfully');
      setName('');
      setIp('');
      setModel('');
      setSshUser('');
    } catch (error) {
      console.error('Error registering robot:', error);
      alert('Failed to register robot');
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px', position: 'relative' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        {onClose && (
          <button 
            onClick={onClose} 
            style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              backgroundColor: 'transparent',
              border: 'none',
              fontSize: '16px',
              cursor: 'pointer'
            }}>
            &times; {/* 닫기 아이콘 */}
          </button>
        )}
      </header>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Robot Register</h2>
      <form onSubmit={handleRegister} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <input
          type="text"
          placeholder="Robot Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <input
          type="text"
          placeholder="Robot IP"
          value={ip}
          onChange={(e) => setIp(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <input
          type="text"
          placeholder="Robot Model"
          value={model}
          onChange={(e) => setModel(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <input
          type="text"
          placeholder="SSH Username"
          value={sshUser}
          onChange={(e) => setSshUser(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <button type="submit" style={{ padding: '10px 20px', margin: '20px 0', borderRadius: '5px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', cursor: 'pointer' }}>
          Register Robot
        </button>
      </form>
    </div>
  );
}

export default RobotRegisterPage;
