import React, { useState } from 'react';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';

function RobotListComponent({ robots }) {
  const [expandedRobotId, setExpandedRobotId] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [selectedJob, setSelectedJob] = useState('');
  const [showTeleopPopup, setShowTeleopPopup] = useState(false); // Teleop 팝업 상태 추가

  // 미리 정의된 작업 리스트
  const jobList = ['청소', '경비', '운반'];

  // 로봇 확장/축소 토글
  const toggleExpand = (robotId) => {
    if (expandedRobotId === robotId) {
      setExpandedRobotId(null); // 이미 확장된 로봇이면 축소
    } else {
      setExpandedRobotId(robotId); // 클릭된 로봇을 확장
    }
  };

  // 작업 추가 팝업 열기
  const handleAddJob = (robot) => {
    setSelectedRobot(robot); // 선택된 로봇 설정
    setShowPopup(true); // 팝업 열기
  };

  // 작업 선택 처리
  const handleJobSelection = (job) => {
    setSelectedJob(job);
  };

  // 작업 추가 처리
  const handleConfirmAddJob = () => {
    alert(`${selectedRobot.name}에 ${selectedJob} 작업이 추가되었습니다.`);
    // 실제 작업 추가 로직 구현 가능
    setShowPopup(false); // 팝업 닫기
    setSelectedJob(''); // 선택된 작업 초기화
  };

  // Teleop 팝업 열기
  const handleOpenTeleop = (robot) => {
    setSelectedRobot(robot); // 조종할 로봇 설정
    setShowTeleopPopup(true); // Teleop 팝업 열기
  };

  return (
    <div className="robot-list">
      <h3>로봇 리스트</h3>
      <ul>
        {robots.length > 0 ? (
          robots.map((robot) => (
            <li key={robot._id} className="robot-item">
              <div className="robot-item-header">
                <span><strong>이름:</strong> {robot.name}</span>
                <span><strong>상태:</strong> {robot.status?.state || 'Unknown'}</span>
                <span><strong>배터리:</strong> {robot.status?.battery || 'N/A'}%</span>
                <button
                  onClick={() => toggleExpand(robot._id)}
                  className="expand-btn"
                  aria-label={expandedRobotId === robot._id ? '접기' : '펼치기'}
                >
                  {expandedRobotId === robot._id ? <FaChevronUp /> : <FaChevronDown />}
                </button>
              </div>

              {/* 확장된 로봇의 상세 정보 표시 */}
              <div className={`robot-details ${expandedRobotId === robot._id ? 'expanded' : ''}`}>
                <p><strong>IP:</strong> {robot.ip}</p>
                <p><strong>위치:</strong> x: {robot.location.x}, y: {robot.location.y}</p>
                <p><strong>속도:</strong> {robot.status?.speed || 'N/A'} m/s</p>
                <p><strong>배터리 상태:</strong> {robot.status?.battery || 'N/A'}%</p>

                {/* Job 텍스트와 + 버튼 추가 */}
                <div className="robot-job-section">
                  <span><strong>Job:</strong></span>
                  <button 
                    className="add-job-btn" 
                    onClick={() => handleAddJob(robot)}
                  >
                    +
                  </button>
                </div>

                {/* 조종 버튼 추가 */}
                <div className="robot-control-section">
                  <button 
                    className="control-circle-btn" 
                    onClick={() => handleOpenTeleop(robot)}
                  >
                    {/* 원 모양 버튼 */}
                  </button>
                  <span>조종</span>
                </div>
              </div>
            </li>
          ))
        ) : (
          <p>연결된 로봇이 없습니다.</p>
        )}
      </ul>

      {/* 작업 추가 팝업 */}
      {showPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>{selectedRobot.name}에 작업 추가</h3>
            <ul className="job-list">
              {jobList.map((job, index) => (
                <li key={index} onClick={() => handleJobSelection(job)} className={selectedJob === job ? 'selected' : ''}>
                  {job}
                </li>
              ))}
            </ul>
            <button onClick={handleConfirmAddJob} disabled={!selectedJob}>작업 추가</button>
            <button onClick={() => setShowPopup(false)}>취소</button>
          </div>
        </div>
      )}

      {/* Teleop 팝업 */}
      {showTeleopPopup && (
        <div className="popup-overlay">
          <div className="popup">
            <h3>{selectedRobot.name} 조종</h3>
            {/* 조종 UI 구현 또는 로봇 조종 컴포넌트 추가 */}
            <button onClick={() => setShowTeleopPopup(false)}>닫기</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default RobotListComponent;
