import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import './RobotEditPage.css';
import RobotRegisterPage from './RobotRegisterPage';

function RobotEditPage() {
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [ip, setIp] = useState(''); // IP 주소 상태 추가
  const [active, setActive] = useState(1);
  const [showRegisterModal, setShowRegisterModal] = useState(false);
  const [workflowQueue, setWorkflowQueue] = useState([]); // 작업 큐 상태 추가
  const [selectedRobotId, setSelectedRobotId] = useState(null); // 선택한 로봇의 _id 상태 추가

  useEffect(() => {
    fetchRobots();
  }, []);

  const fetchRobots = async (preserveSelectedRobotId = null) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.33:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setRobots(response.data);

      if (preserveSelectedRobotId) {
        const selected = response.data.find(robot => robot._id === preserveSelectedRobotId);
        if (selected) {
          setSelectedRobot(selected);
          setSelectedRobotId(selected._id); // 선택한 로봇의 _id 저장
          setName(selected.name);
          setDescription(selected.description);
          setIp(selected.ip); // IP 주소 상태 설정
          setActive(selected.active);
          fetchWorkflowQueue(selected._id); // 작업 큐 불러오기
        }
      } else if (response.data.length > 0) {
        const firstRobot = response.data[0];
        setSelectedRobot(firstRobot);
        setSelectedRobotId(firstRobot._id); // 첫 번째 로봇의 _id 저장
        setName(firstRobot.name);
        setDescription(firstRobot.description);
        setIp(firstRobot.ip); // IP 주소 상태 설정
        setActive(firstRobot.active);
        fetchWorkflowQueue(firstRobot._id); // 작업 큐 불러오기
      }
    } catch (error) {
      console.error('Error fetching robots:', error);
      alert('Error fetching robots.');
    }
  };

  // 특정 robotId에 해당하는 작업 큐 불러오기
  const fetchWorkflowQueue = async (robotId) => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      const response = await axios.get(`http://172.30.1.33:5559/robot/robot/${robotId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setWorkflowQueue(response.data.workflows || []); // 작업 큐의 workflows 배열 저장
    } catch (error) {
      console.error('Error fetching workflow queue:', error);
      alert('Error fetching workflow queue.');
    }
  };

  const handleSelectRobot = (robot) => {
    setSelectedRobot(robot);
    setSelectedRobotId(robot._id); // 선택한 로봇의 _id 저장
    setName(robot.name);
    setDescription(robot.description);
    setIp(robot.ip); // IP 주소 상태 설정
    setActive(robot.active);
    fetchWorkflowQueue(robot._id); // 작업 큐 불러오기
  };

  const handleToggleActive = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    try {
      await axios.put(
        `http://172.30.1.33:5559/robot/toggleActive/${selectedRobot._id}`,
        {},
        {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        }
      );
      fetchRobots(selectedRobot._id);
    } catch (error) {
      console.error('Error toggling active status:', error);
      alert('Error toggling active status.');
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    // IP 주소 유효성 검사 (간단한 예제)
    const ipRegex = /^(25[0-5]|2[0-4]\d|[0-1]?\d\d?)\.(25[0-5]|2[0-4]\d|[0-1]?\d\d?)\.(25[0-5]|2[0-4]\d|[0-1]?\d\d?)\.(25[0-5]|2[0-4]\d|[0-1]?\d\d?)$/;
    if (!ipRegex.test(ip)) {
      alert('유효한 IP 주소를 입력해주세요.');
      return;
    }

    try {
      await axios.put(`http://172.30.1.33:5559/robot/update/${selectedRobot._id}`, {
        name,
        description,
        ip // IP 주소 포함
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      fetchRobots(selectedRobot._id);
      alert('Robot updated successfully.');
    } catch (error) {
      console.error('Error updating robot:', error);
      alert(`Error updating robot: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleDelete = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    if (window.confirm('Are you sure you want to delete this robot?')) {
      try {
        await axios.put(`http://172.30.1.33:5559/robot/unregister/${selectedRobot._id}`, {}, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        setSelectedRobot(null);
        setWorkflowQueue([]);
        fetchRobots();
        alert('Robot deleted successfully.');
      } catch (error) {
        console.error('Error removing robot:', error);
        alert(`Error removing robot: ${error.response?.data?.message || error.message}`);
      }
    }
  };
  const handleDeleteWorkflow = async (workflowId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }

    if (!selectedRobotId) {
      alert('No robot selected.');
      return;
    }

    if (window.confirm('Are you sure you want to delete this workflow item?')) {
      try {
        await axios.delete(`http://172.30.1.33:5559/robot/${selectedRobotId}/${workflowId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        // 삭제 후 워크플로우 큐 다시 불러오기
        fetchWorkflowQueue(selectedRobotId);
        alert('Workflow item deleted successfully.');
      } catch (error) {
        console.error('Error deleting workflow item:', error);
        alert(`Error deleting workflow item: ${error.response?.data?.message || error.message}`);
      }
    }
  };


  const totalRobots = robots.length;
  const activeRobots = robots.filter(robot => robot.active === 1).length;

  return (
    <div className="robot-edit-page">
      <header className="header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />
      <div className="main-content">
        <div className="robot-list-container">
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <h3 style={{ marginRight: '10px' }}>AMR</h3>
            <span style={{ fontSize: '18px', color: 'gray' }}>
              {activeRobots}/{totalRobots} Act
            </span>
            <button
              onClick={() => setShowRegisterModal(true)}
              style={{
                padding: '5px 10px',
                borderRadius: '50%',
                backgroundColor: '#4CAF50',
                color: '#fff',
                border: 'none',
                cursor: 'pointer',
                fontSize: '16px',
                marginLeft: 'auto'
              }}
            >
              +
            </button>
          </div>
          <ul className="robot-list">
            {robots.map((robot) => (
              <li
                key={robot._id}
                onClick={() => handleSelectRobot(robot)}
                className={selectedRobotId === robot._id ? 'selected' : ''}
                style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}
              >
                <span>
                  {robot.name} - {robot.ip}
                </span>
                <span style={{ color: 'gray', marginLeft: '10px' }}>
                  {robot.active === 1 ? 'Act' : 'Inact'}
                </span>
              </li>
            ))}
          </ul>
        </div>
        <div className="robot-details-container">
          {selectedRobot ? (
            <form className="robot-form" onSubmit={handleUpdate}>
              <div className="robot-field">
                <label htmlFor="name"><strong>Name</strong></label>
                <input
                  id="name"
                  type="text"
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                  required
                  className="input-field"
                  placeholder="Enter robot name"
                  style={{ width: '50%' }}
                />
                <span style={{ marginLeft: '20px', fontWeight: 'bold' }}>
                  배터리 {selectedRobot.status?.battery || 0}%
                </span>
              </div>
              <div className="robot-field">
                <label htmlFor="description"><strong>설명</strong></label>
                <textarea
                  id="description"
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  rows="4"
                  className="input-field"
                  placeholder="Enter robot description"
                />
              </div>
              <div className="robot-field">
                <label htmlFor="ip"><strong>IP 주소</strong></label>
                <input
                  id="ip"
                  type="text"
                  value={ip}
                  onChange={(e) => setIp(e.target.value)}
                  required
                  className="input-field"
                  placeholder="Enter robot IP address"
                  style={{ width: '10%' }}
                />
              </div>

              <div className="robot-info">
                <div>
                  <p><strong>모델</strong></p>
                  <p>{selectedRobot.model}</p>
                </div>
                <div>
                  <p><strong>상태</strong></p>
                  <p>{selectedRobot.status?.state || 'Unknown'}</p>
                </div>
                <div>
                  <p><strong>등록일</strong></p>
                  <p>{new Date(selectedRobot.createdAt).toLocaleString()}</p>
                </div>
              </div>

              <div className="robot-field">
                <label><strong>Active</strong></label>
                <div style={{ marginTop: '10px' }}>
                  <label className="switch">
                    <input
                      type="checkbox"
                      checked={active === 1}
                      onChange={handleToggleActive}
                    />
                    <span className="slider round"></span>
                  </label>
                </div>
              </div>

              {/* 작업 플로우 디자인에 작업 큐 데이터 표시 */}
              <div className="robot-flow-container">
  <div className="robot-flow-background">
    <div className="robot-flow-list">
      {workflowQueue.length > 0 ? (
        workflowQueue.map((workflow) => (
          <div key={workflow._id} className="robot-flow-item">
            <div><strong>{workflow.node}</strong></div>
            <div>{workflow.step}</div>
            <div style={{ color: 'gray' }}>{workflow.status}</div>
            <button 
              onClick={() => handleDeleteWorkflow(workflow._id)}
              style={{
                backgroundColor: '#ff4d4d',
                color: 'white',
                border: 'none',
                padding: '5px 10px',
                borderRadius: '5px',
                cursor: 'pointer',
                marginTop: '5px'
              }}
            >
              Delete
            </button>
          </div>
        ))
      ) : (
        <p>No workflow steps assigned.</p>
      )}
    </div>
  </div>
</div>

              {/* 추가적인 작업 플로우 기능을 원할 경우 여기에 구현 */}
            </form>
          ) : (
            <p>Please select a robot to view and edit details</p>
          )}
        </div>
      </div>

      {showRegisterModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button
              onClick={() => setShowRegisterModal(false)}
              style={{ position: 'absolute', top: '10px', right: '10px', background: 'transparent', border: 'none', fontSize: '18px', cursor: 'pointer' }}
            >
              &times;
            </button>
            <RobotRegisterPage />
          </div>
        </div>
      )}

      {selectedRobot && (
        <div className="floating-buttons">
          <button className="floating-delete-button" onClick={handleDelete}>
            DELETE
          </button>
          <button className="floating-save-button" onClick={handleUpdate}>
            SAVE
          </button>
        </div>
      )}
    </div>
  );
}

export default RobotEditPage;
