import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import CreateTask from './CreateTask'; // CreateTask 컴포넌트 임포트
import TaskFlow from './TaskFlow'; // TaskFlow 컴포넌트 임포트
import './TaskPage.css'; // CSS 파일을 임포트

function TaskPage() {
    const [tasks, setTasks] = useState([]); // 작업 리스트 상태
    const [selectedTask, setSelectedTask] = useState(null); // 선택한 작업 상태
    const [isPopupOpen, setIsPopupOpen] = useState(false); // 팝업 상태
    const [monitoredMap, setMonitoredMap] = useState(null); // 모니터링 중인 맵 정보

    // 작업 이름과 설명을 수정할 상태 변수
    const [taskName, setTaskName] = useState('');
    const [taskDescription, setTaskDescription] = useState('');
    const [taskType, setTaskType] = useState('A'); // 작업 유형 드롭다운 기본 값

    // JWT 토큰을 localStorage에서 가져옴
    const token = localStorage.getItem('token');

    // 작업 리스트를 가져오는 함수
    useEffect(() => {
        async function fetchTasks() {
            try {
                const response = await axios.get('http://172.30.1.30:8080/task/tasks', {
                    headers: { Authorization: `Bearer ${token}` }, // 인증 헤더 추가
                });
                setTasks(response.data);

                // 작업 리스트의 첫 번째 작업을 자동 선택
                if (response.data.length > 0) {
                    setSelectedTask(response.data[0]);
                    setTaskName(response.data[0].name);
                    setTaskDescription(response.data[0].description || '');
                    setTaskType(response.data[0].taskType || 'A');
                }
            } catch (error) {
                console.error('작업 리스트를 가져오는 중 오류가 발생했습니다.', error);
            }
        }
        fetchTasks();
    }, [token]);

    // 모니터링 중인 맵 정보를 가져오는 함수
    useEffect(() => {
        async function fetchMonitoredMap() {
            try {
                const response = await axios.get('http://172.30.1.30:5557/map/monitored', {
                    headers: { Authorization: `Bearer ${token}` }, // 인증 헤더 추가
                });
                setMonitoredMap(response.data); // 모니터링 중인 맵 정보를 상태에 저장
            } catch (error) {
                console.error('모니터링 중인 맵을 가져오는 중 오류가 발생했습니다.', error);
            }
        }

        fetchMonitoredMap();
    }, [token]);

    // 작업을 선택했을 때 호출되는 함수
    const handleTaskClick = (task) => {
        setSelectedTask(task);
        setTaskName(task.name);
        setTaskDescription(task.description || '');
        setTaskType(task.taskType || 'A');
    };

    // 팝업 열기/닫기 함수
    const togglePopup = () => {
        setIsPopupOpen(!isPopupOpen);
    };

    // 작업 생성 후 업데이트 함수
    const handleTaskCreated = (newTask) => {
        setTasks([...tasks, newTask]); // 새로운 작업을 리스트에 추가
    };

    // 작업 할당 버튼 클릭 시 실행될 함수
    const handleAssignTask = () => {
        alert('작업 할당 기능 실행');
    };

    // 작업 저장 버튼 클릭 시 실행될 함수
    const handleSaveTask = async () => {
        if (selectedTask) {
            try {
                const updatedTask = {
                    name: taskName,
                    description: taskDescription,
                    taskType: taskType,
                    workflow: selectedTask.workflow // 기존 워크플로우 유지
                };

                const response = await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, updatedTask, {
                    headers: { Authorization: `Bearer ${token}` }, // 인증 헤더 추가
                });

                setSelectedTask(response.data); // 업데이트된 작업을 선택된 작업으로 설정
                alert('작업이 성공적으로 저장되었습니다.');
            } catch (error) {
                console.error('작업 저장 중 오류가 발생했습니다.', error);
                alert('작업 저장 중 오류가 발생했습니다.');
            }
        } else {
            alert('저장할 작업을 선택하세요.');
        }
    };

    // 작업 삭제 버튼 클릭 시 실행될 함수 (이름을 '0'으로 변경)
    const handleDeleteTask = async () => {
        if (selectedTask) {
            try {
                // 작업의 이름을 '0'으로 변경
                const response = await axios.put(`http://172.30.1.30:8080/task/tasks/${selectedTask._id}`, {
                    name: '0',
                }, {
                    headers: { Authorization: `Bearer ${token}` }, // 인증 헤더 추가
                });

                // 삭제된 작업을 리스트에서 제거
                setTasks(tasks.filter(task => task._id !== selectedTask._id));
                setSelectedTask(null); // 선택된 작업 초기화
                alert('작업이 삭제되었습니다.');
            } catch (error) {
                console.error('작업 삭제 중 오류가 발생했습니다.', error);
                alert('작업 삭제 중 오류가 발생했습니다.');
            }
        } else {
            alert('삭제할 작업을 선택하세요.');
        }
    };

    return (
        <div className="page-container">
            <header className="header">
                <UserInfo />
                <LogoutButton />
            </header>
            <Navbar className="navbar" /> {/* Navbar 클래스 추가 */}
            <div className="main-content">
                {/* 모니터링 중인 맵 정보 표시 */}
                {monitoredMap && (
                    <div className="monitored-map-info">
                        <h3>현재 모니터링 중인 맵: {monitoredMap.name}</h3>
                        <p>{monitoredMap.description}</p>
                    </div>
                )}

                {/* 작업 리스트 영역 */}
                <div className="task-list">
                    <div className="task-list-header">
                        <h3>작업 리스트</h3>
                        <button className="add-task-btn" onClick={togglePopup}>+</button>
                    </div>
                    <ul>
                        {tasks.map((task) => (
                            <li
                                key={task._id}
                                onClick={() => handleTaskClick(task)}
                                className={selectedTask && selectedTask._id === task._id ? 'selected' : ''}
                            >
                                {task.name}
                            </li>
                        ))}
                    </ul>
                </div>

                {/* 선택한 작업의 상세 정보 영역 */}
                <div className="task-details">
                    {selectedTask ? (
                        <div>
                            <h3>작업 상세 정보</h3>
                            <label>
                                <strong>작업 이름:</strong>
                                <input
                                    type="text"
                                    className="input-field" // 텍스트 필드 크기 조정
                                    value={taskName}
                                    onChange={(e) => setTaskName(e.target.value)}
                                />
                            </label>
                            <br />
                            <label>
                                <strong>작업 설명:</strong>
                                <textarea
                                    className="textarea-field" // 설명 필드 크기 조정
                                    value={taskDescription}
                                    onChange={(e) => setTaskDescription(e.target.value)}
                                />
                            </label>
                            <br />
                            <div className="task-row">
                                <label>
                                    <strong>작업 유형:</strong>
                                    <select
                                        className="select-field" // 드롭다운 크기 조정
                                        value={taskType}
                                        onChange={(e) => setTaskType(e.target.value)}
                                    >
                                        <option value="A">A</option>
                                        <option value="B">B</option>
                                        <option value="C">C</option>
                                    </select>
                                </label>
                                <label className="created-date">
                                    <strong>생성일:</strong> {new Date(selectedTask.createdAt).toLocaleDateString()}
                                </label>
                            </div>

                            {/* 작업 플로우 */}
                            <h4>작업 플로우</h4>
                            <ul>
                                {selectedTask.workflow.map((flow, index) => (
                                    <li key={index}>
                                        <strong>노드:</strong> {flow.node}, <strong>단계:</strong> {flow.step}, <strong>상태:</strong> {flow.status}
                                    </li>
                                ))}
                            </ul>

                            {/* 작업 플로우를 추가할 공간 */}
                            <div className="task-flow-section">
                                <TaskFlow /> {/* TaskFlow 컴포넌트 */}
                            </div>
                        </div>
                    ) : (
                        <p>작업을 선택하세요.</p>
                    )}
                </div>

                {/* 작업 추가 팝업 */}
                {isPopupOpen && (
                    <CreateTask onClose={togglePopup} onTaskCreated={handleTaskCreated} />
                )}

                {/* 플로팅 버튼 */}
                <div className="floating-buttons">
                    <button className="assign-task-btn" onClick={handleAssignTask}>작업 할당</button>
                    <button className="save-task-btn" onClick={handleSaveTask}>작업 저장</button>
                    <button className="delete-task-btn" onClick={handleDeleteTask}>작업 삭제</button>
                </div>
            </div>
        </div>
    );
}

export default TaskPage;
