import React, { useState, useEffect, useRef } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';
import io from 'socket.io-client';
import SimplePeer from 'simple-peer';

const socket = io.connect('http://localhost:3000'); // 신호 서버 주소

function MapCreatePage() {
    const [robots, setRobots] = useState([]);
    const [selectedRobot, setSelectedRobot] = useState('');
    const [stream, setStream] = useState(null);
    const videoRef = useRef();

    useEffect(() => {
        const fetchRobots = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get('http://localhost:5559/robot/robots', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRobots(response.data);
            } catch (error) {
                console.error('Error fetching robots:', error);
            }
        };

        fetchRobots();

        navigator.mediaDevices.getUserMedia({ video: true, audio: false }).then(stream => {
            setStream(stream);
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }
        }).catch(error => console.error('Error accessing media devices.', error));

        socket.on('signal', data => {
            peer.signal(data.signal);
        });

        const peer = new SimplePeer({
            initiator: true,
            trickle: false,
            stream: stream
        });

        peer.on('signal', data => {
            socket.emit('signal', { signal: data });
        });

        peer.on('stream', stream => {
            if (videoRef.current) {
                videoRef.current.srcObject = stream;
            }
        });

        const handleKeyDown = (e) => {
            const velocityCommands = {
                w: { linear: 0.1, angular: 0 },
                a: { linear: 0, angular: 0.1 },
                s: { linear: -0.1, angular: 0 },
                d: { linear: 0, angular: -0.1 },
                ' ': { linear: 0, angular: 0 }
            };
            if (velocityCommands[e.key]) {
                peer.send(JSON.stringify({
                    robot_id: selectedRobot,
                    velocity: velocityCommands[e.key]
                }));
            }
        };

        window.addEventListener('keydown', handleKeyDown);
        return () => {
            window.removeEventListener('keydown', handleKeyDown);
            socket.disconnect();
        };
    }, [selectedRobot, stream]);

    const sendCommand = (command) => {
        const token = localStorage.getItem('token');
        fetch('http://localhost:5559/robot/send_command', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({
                robot_id: selectedRobot,
                command: command
            })
        }).then(response => response.text())
            .then(data => alert(data))
            .catch(error => console.error('Error sending command:', error));
    };

    return (
        <div>
            <header>
                <UserInfo />
                <LogoutButton />
            </header>
            <div style={{ display: 'flex' }}>
                <Navbar />
            </div>
            <h2>SLAM Control</h2>
            <div>
                <label>로봇 선택:</label>
                <select onChange={(e) => setSelectedRobot(e.target.value)} value={selectedRobot}>
                    <option value="">로봇을 선택하세요</option>
                    {robots.map(robot => (
                        <option key={robot._id} value={robot._id}>{robot.name}</option>
                    ))}
                </select>
            </div>
            <button onClick={() => sendCommand('slam')}>SLAM 시작</button>
            <div>
                <video ref={videoRef} autoPlay playsInline />
            </div>
        </div>
    );
};

export default MapCreatePage;
