  import React, { useState, useEffect } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';

function RobotEditPage() {
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState(null);
  const [name, setName] = useState('');
  const [ip, setIp] = useState('');
  const [model, setModel] = useState('');
  const [battery, setBattery] = useState(100);
  const [state, setState] = useState('Idle');
  const [taskName, setTaskName] = useState(''); 
  const [taskType, setTaskType] = useState(''); 
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchRobots();
  }, []);

  const getToken = () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      window.location.href = '/login'; // 로그인 페이지로 리다이렉트
      return null;
    }
    return token;
  };

  const fetchRobots = async () => {
    const token = getToken();
    if (!token) return;

    try {
      const response = await axios.get('http://172.30.1.28:5559/robot/robots', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setRobots(response.data);
    } catch (error) {
      console.error('Error fetching robots:', error);
      if (error.response && error.response.status === 401) {
        alert('Unauthorized: Invalid or expired token.');
        localStorage.removeItem('token'); // 토큰 삭제
        window.location.href = '/login'; // 로그인 페이지로 리다이렉트
      }
    }
  };

  const handleSelectRobot = (robot) => {
    setSelectedRobot(robot);
    setName(robot.name);
    setIp(robot.ip);
    setModel(robot.model);
    setBattery(robot.status.battery); 
    setState(robot.status.state);     
  };

  const handleUpdate = async (e) => {
    e.preventDefault();

    const token = getToken();
    if (!token || name.length < 2) {
      alert('Robot name must be at least 2 characters long');
      return;
    }

    try {
      await axios.put(`http://172.30.1.28:5559/robot/update/${selectedRobot._id}`, {
        name,
        ip,
        model
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('Robot updated successfully');
      fetchRobots();
    } catch (error) {
      console.error('Error updating robot:', error);
      alert(`Error updating robot: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleCreateTask = async (e) => {
    e.preventDefault();

    const token = getToken();  // JWT 토큰 가져오기
    if (!token) {
      setMessage('No token found. Please log in again.');
      window.location.href = '/login';  // 토큰이 없으면 로그인 페이지로 이동
      return;
    }

    const taskData = {
      name: taskName,
      taskType,
      status: 'Pending',
      startTime: new Date().toISOString()
    };

    try {
      // Task 생성 요청
      const response = await axios.post('http://172.30.1.28:6000/task/create', taskData, {
        headers: {
          'Authorization': `Bearer ${token}`,  // JWT 인증 헤더
          'Content-Type': 'application/json'
        }
      });

      // 요청 성공 시 처리
      setMessage('Task created successfully!');
      setTaskName('');
      setTaskType('');
      console.log('Task creation response:', response.data);

    } catch (error) {
      // 다양한 오류 상황 처리
      if (error.response) {
        // 서버 응답이 있는 경우 (HTTP 상태 코드 기반)
        switch (error.response.status) {
          case 400:
            setMessage('Bad request: Please check the input data.');
            break;
          case 401:
            setMessage('Unauthorized: Invalid or expired token.');
            localStorage.removeItem('token');  // 토큰 삭제 후 로그인 페이지로 이동
            window.location.href = '/login';
            break;
          case 403:
            setMessage('Forbidden: You do not have permission to create this task.');
            break;
          case 404:
            setMessage('Not Found: The server could not find the requested resource.');
            break;
          case 500:
            setMessage('Server error: Please try again later.');
            break;
          default:
            setMessage(`Unexpected error: ${error.response.status} - ${error.response.data?.message || 'Unknown error occurred'}`);
        }
        console.error('Error response data:', error.response.data);
      } else if (error.request) {
        // 요청이 만들어졌지만 응답을 받지 못한 경우 (네트워크 문제)
        setMessage('Network error: No response from server. Please check your connection or server status.');
        console.error('No response from server:', error.request);
      } else {
        // 요청 자체가 실패한 경우 (기타 에러)
        setMessage(`Request failed: ${error.message}`);
        console.error('Error message:', error.message);
      }
    }
  };



  const handleRemove = async () => {
    const token = getToken();
    if (!token) return;

    if (window.confirm('Are you sure you want to remove this robot?')) {
      try {
        await axios.put(`http://172.30.1.28:5559/robot/unregister/${selectedRobot._id}`, {}, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        alert('Robot removed successfully');
        setSelectedRobot(null);
        fetchRobots();     
      } catch (error) {
        console.error('Error removing robot:', error);
        alert(`Error removing robot: ${error.response?.data?.message || error.message}`);
      }
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <div style={{ display: 'flex' }}>
        <Navbar />
      </div>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Robot Edit</h2>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <h3>Your Robots</h3>
        <ul style={{ listStyleType: 'none', padding: 0 }}>
          {robots.map((robot) => (
            <li key={robot._id} onClick={() => handleSelectRobot(robot)} style={{ padding: '10px', cursor: 'pointer', borderBottom: '1px solid #ccc' }}>
              {robot.name} - Battery: {robot.status.battery}% - State: {robot.status.state}
            </li>
          ))}
        </ul>
        {selectedRobot && (
          <>
            <form onSubmit={handleUpdate} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <input
                type="text"
                placeholder="Robot Name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
                style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
              />
              <input
                type="text"
                placeholder="Robot IP"
                value={ip}
                onChange={(e) => setIp(e.target.value)}
                required
                style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
              />
              <input
                type="text"
                placeholder="Robot Model"
                value={model}
                onChange={(e) => setModel(e.target.value)}
                required
                style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
              />
              <div style={{ marginBottom: '20px' }}>
                <p>Battery: {battery}%</p>
                <p>State: {state}</p>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', width: '300px' }}>
                <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', cursor: 'pointer' }}>
                  Update Robot
                </button>
                <button type="button" onClick={handleRemove} style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#f44336', color: '#fff', border: 'none', cursor: 'pointer' }}>
                  Remove Robot
                </button>
              </div>
            </form>

            <h3>Create Task for {selectedRobot.name}</h3>
            <form onSubmit={handleCreateTask} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
              <input
                type="text"
                placeholder="Task Name"
                value={taskName}
                onChange={(e) => setTaskName(e.target.value)}
                required
                style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
              />
              <input
                type="text"
                placeholder="Task Type"
                value={taskType}
                onChange={(e) => setTaskType(e.target.value)}
                required
                style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
              />
              <button type="submit" style={{ padding: '10px 20px', borderRadius: '5px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', cursor: 'pointer' }}>
                Create Task
              </button>
            </form>
            {message && <p>{message}</p>}
          </>
        )}
      </div>
    </div>
  );
}

export default RobotEditPage;
