import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import Peer from 'simple-peer';

const MapCreatePage = () => {
  const [robots, setRobots] = useState([]);
  const [selectedRobot, setSelectedRobot] = useState('');
  const [stream, setStream] = useState(null);
  const videoRef = useRef();

  useEffect(() => {
    // 로봇 목록을 가져오는 함수
    const fetchRobots = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('http://172.30.1.40:5559/robot/robots', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const data = await response.json();
        setRobots(data);
      } catch (error) {
        console.error('Error fetching robots:', error);
      }
    };

    fetchRobots();
  }, []);

  useEffect(() => {
    // WebSocket 연결 설정
    const socket = io(`ws://${window.location.hostname}:7000`);
    socket.on('connect', () => {
      console.log('WebSocket connected');
    });

    const handleKeyDown = (e) => {
      const velocityCommands = {
        w: { linear: 0.1, angular: 0 },
        a: { linear: 0, angular: 0.1 },
        s: { linear: -0.1, angular: 0 },
        d: { linear: 0, angular: -0.1 },
        ' ': { linear: 0, angular: 0 }
      };
      if (velocityCommands[e.key]) {
        socket.emit('command', {
          robot_id: selectedRobot,
          velocity: velocityCommands[e.key]
        });
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      socket.close();
    };
  }, [selectedRobot]);

  useEffect(() => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      console.error('getUserMedia is not supported in this browser');
      return;
    }

    const getVideoStream = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        setStream(stream);
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error('Error accessing media devices.', error);
      }
    };

    getVideoStream();

    return () => {
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
    };
  }, []);

  const sendCommand = async (command) => {
    const token = localStorage.getItem('token');
    try {
      const response = await fetch('http://172.30.1.40:5559/robot/send_command', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          robot_id: selectedRobot,
          command: command
        })
      });
      const data = await response.text();
      alert(data);
    } catch (error) {
      console.error('Error sending command:', error);
    }
  };

  return (
    <div>
      <header>
        <h2>Map Create Page</h2>
      </header>
      <div>
        <label>로봇 선택:</label>
        <select onChange={(e) => setSelectedRobot(e.target.value)} value={selectedRobot}>
          <option value="">로봇을 선택하세요</option>
          {robots.map(robot => (
            <option key={robot._id} value={robot._id}>{robot.name}</option>
          ))}
        </select>
      </div>
      <button onClick={() => sendCommand('slam')}>SLAM 시작</button>
      <div>
        <video ref={videoRef} autoPlay playsInline />
      </div>
    </div>
  );
};

export default MapCreatePage;
