// src/utils/coordinateUtils.js

const mapResolution = 0.05; // 픽셀당 미터
const mapOrigin = [-10.0, -10.0]; // 맵의 원점 좌표 (x, y)

// 월드 좌표 -> 화면(맵) 좌표로 변환
export const worldToMap = (worldX, worldY, mapWidth, mapHeight) => {
  const mapX = (worldX - mapOrigin[0]) / mapResolution;
  const mapY = mapHeight - (worldY - mapOrigin[1]) / mapResolution; // Y 좌표 반전
  return { x: mapX, y: mapY };
};

// 화면(맵) 좌표 -> 월드 좌표로 변환
export const mapToWorld = (mapX, mapY, mapWidth, mapHeight) => {
  const worldX = mapX * mapResolution + mapOrigin[0];
  const worldY = (mapHeight - mapY) * mapResolution + mapOrigin[1];
  return { x: worldX, y: worldY };
};
