// src/components/MapManage/Mapmanage.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faPlus, faLink, faUnlink } from '@fortawesome/free-solid-svg-icons';

import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import NodeList from './NodeList'; // NodeList 컴포넌트 임포트
import NodeDisconnect from './NodeDisconnect'; // NodeDisconnect 컴포넌트 임포트
import NodeConnectionPopup from './NodeConnectionPopup'; // NodeConnectionPopup 컴포넌트 임포트
import './Mapmanage.css';
import { worldToMap, mapToWorld } from '../../utils/coordinateUtils';

function Mapmanage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null); // 선택된 맵 상태
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [isMonitored, setIsMonitored] = useState(false); // 모니터링 설정 체크박스 상태
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false); // 업로드 팝업 상태
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 수정 팝업 상태
  const [isCreatingNode, setIsCreatingNode] = useState(false); // 노드 생성 모드
  const [nodeX, setNodeX] = useState(null); // X 좌표
  const [nodeY, setNodeY] = useState(null); // Y 좌표
  const [nodeName, setNodeName] = useState(''); // 노드 이름
  const [isNodePopupOpen, setIsNodePopupOpen] = useState(false); // 노드 팝업 상태
  const canvasRef = useRef(null); // 캔버스 참조
  const mapImageRef = useRef(null); // 지도 이미지 참조
  const [nodes, setNodes] = useState([]); // 노드 목록 저장
  const [isDeletingNode, setIsDeletingNode] = useState(false);

  const [imageNaturalWidth, setImageNaturalWidth] = useState(null);
  const [imageNaturalHeight, setImageNaturalHeight] = useState(null);

  // Hover 상태 관리
  const [hoveredNode, setHoveredNode] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });

  // 금지구역 관련 상태
  const [isCreatingNoGoZone, setIsCreatingNoGoZone] = useState(false); // 금지 구역 생성 모드
  const [isDeletingNoGoZone, setIsDeletingNoGoZone] = useState(false); // 금지 구역 삭제 모드
  const [dragging, setDragging] = useState(false); // 드래그 상태
  const [dragStart, setDragStart] = useState(null); // 드래그 시작 지점
  const [currentRect, setCurrentRect] = useState(null); // 현재 그려지고 있는 사각형
  const [noGoZones, setNoGoZones] = useState([]); // 금지 구역 목록 저장

  // 맵 패치 로딩 상태 추가
  const [isPatchingMap, setIsPatchingMap] = useState(false); // 맵 패치 로딩 상태

  // 노드 연결 팝업 관리 상태
  const [isConnectionPopupOpen, setIsConnectionPopupOpen] = useState(false);

  // 노드 연결 결과 저장
  const [nodeConnectionData, setNodeConnectionData] = useState(null);

  // 노드 목록을 서버에서 가져오는 함수
  const fetchMaps = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }

    try {
      const response = await axios.get('http://172.30.1.33:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);

      // 맵이 하나 이상 있을 경우 첫 번째 맵을 자동으로 선택
      if (response.data.length > 0) {
        await handleSelectMap(response.data[0]);
      }
    } catch (error) {
      console.error('맵을 가져오는 중 오류 발생:', error);
      alert('맵을 가져오는 데 실패했습니다.');
    }
  };

  useEffect(() => {
    fetchMaps();
  }, []);

  // 노드 목록을 서버에서 가져오는 함수
  const fetchNodes = async (mapId) => {
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/nodes/${mapId}`);
      if (response.status === 200) {
        setNodes(response.data); // 노드 리스트 상태 업데이트
      } else {
        setNodes([]); // 실패 시 빈 배열로 초기화
      }
    } catch (error) {
      console.error('노드를 가져오는 중 오류 발생:', error);
      setNodes([]); // 오류 발생 시에도 빈 배열로 초기화
      alert('노드를 가져오는 데 실패했습니다.');
    }
  };

  // 금지구역 목록을 서버에서 가져오는 함수
  const fetchNoGoZones = async (mapId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/no-go-zones/map/${mapId}`, {
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });
      setNoGoZones(response.data);
    } catch (error) {
      console.error('금지구역을 가져오는 중 오류 발생:', error);
      alert('금지구역을 가져오는 데 실패했습니다.');
    }
  };

  // 맵을 선택했을 때 호출되는 함수
  const handleSelectMap = async (map) => {
    setSelectedMap(map); // 맵 선택 상태 업데이트
    setEditName(map.name);
    setEditDescription(map.description);
    setIsMonitored(map.isMonitored || false); // 모니터링 지도 상태 설정

    // 맵 이미지 가져오기
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl); // 이미지 설정
    } catch (error) {
      console.error('맵 이미지를 가져오는 중 오류 발생:', error);
      alert('맵 이미지를 가져오는 데 실패했습니다.');
    }

    // 금지구역 가져오기
    await fetchNoGoZones(map._id);
  };

  // 이미지 로드 시 호출되는 함수 (지도 이미지를 캔버스에 그리기)
  const loadMapImage = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    mapImageRef.current = new Image();
    mapImageRef.current.src = mapImage;

    mapImageRef.current.onload = () => {
      const imgWidth = mapImageRef.current.width;
      const imgHeight = mapImageRef.current.height;
      setImageNaturalWidth(imgWidth);
      setImageNaturalHeight(imgHeight);

      // 캔버스 크기 조정 및 기존 내용 클리어
      canvas.width = imgWidth;
      canvas.height = imgHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 지도 이미지를 캔버스에 그리기
      ctx.drawImage(mapImageRef.current, 0, 0, imgWidth, imgHeight);
      drawAll(); // 모든 요소 그리기 (노드, 금지구역 등)
    };
  };

  // 맵 좌표계를 캔버스 좌표계로 변환하는 함수
  const mapToMapCoordinates = (x, y) => {
    return worldToMap(x, y, imageNaturalWidth, imageNaturalHeight);
  };

  // 캔버스에 모든 요소를 그리는 함수 (지도, 노드, 연결선, 금지구역)
  const drawAll = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    if (!canvas || !imageNaturalWidth || !imageNaturalHeight) return;

    // 캔버스 클리어
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // 지도 이미지 그리기
    ctx.drawImage(mapImageRef.current, 0, 0, imageNaturalWidth, imageNaturalHeight);

    // 금지구역 그리기
    noGoZones.forEach((zone) => {
      const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
      const bottomRight = mapToMapCoordinates(zone.bottomRight.x, zone.bottomRight.y);
      const width = bottomRight.x - topLeft.x;
      const height = bottomRight.y - topLeft.y;
      ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
      ctx.fillRect(topLeft.x, topLeft.y, width, height);
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 2;
      ctx.strokeRect(topLeft.x, topLeft.y, width, height);
    });

    // 드래그 중인 사각형 그리기
    if (currentRect) {
      ctx.fillStyle = 'rgba(255, 0, 0, 0.2)'; // 반투명 붉은 배경
      ctx.fillRect(currentRect.x, currentRect.y, currentRect.width, currentRect.height);
      ctx.strokeStyle = 'red'; // 빨간색 테두리
      ctx.lineWidth = 1;
      ctx.strokeRect(currentRect.x, currentRect.y, currentRect.width, currentRect.height);
    }

    // 연결선을 먼저 그리기
    nodes.forEach((node) => {
      node.connections.forEach((connection) => {
        const connectedNode = nodes.find(n => n._id === connection.node);
        if (connectedNode) {
          const { x: startX, y: startY } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
          const { x: endX, y: endY } = worldToMap(connectedNode.x, connectedNode.y, imageNaturalWidth, imageNaturalHeight);

          ctx.strokeStyle = 'blue';
          ctx.lineWidth = 2;
          ctx.beginPath();
          ctx.moveTo(startX, startY);

          // 웨이포인트가 있는 경우
          if (connection.waypoints && connection.waypoints.length > 0) {
            connection.waypoints.forEach((wp) => {
              const { x: wpX, y: wpY } = worldToMap(wp.x, wp.y, imageNaturalWidth, imageNaturalHeight);
              ctx.lineTo(wpX, wpY);
            });
          }

          ctx.lineTo(endX, endY);
          ctx.stroke();
        }
      });
    });

    // 금지구역 삭제 모드일 때, 금지구역 위에 삭제 아이콘 표시
    if (isDeletingNoGoZone) {
      noGoZones.forEach((zone) => {
        const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
        const size = 20;

        // X 아이콘 그리기
        ctx.fillStyle = 'red';
        ctx.font = 'bold 16px Arial';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';

        // X 위치를 우측 상단에 배치
        ctx.fillText('X', topLeft.x + 8, topLeft.y + 15);
      });
    }

    // 노드 그리기
    nodes.forEach((node) => {
      const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
      const size = 10;
      ctx.fillStyle = 'red';
      ctx.fillRect(x - size / 2, y - size / 2, size, size);
    });
  };

  // 이미지 로드 후 캔버스에 지도 그리기
  useEffect(() => {
    if (mapImage) {
      loadMapImage();
    }
  }, [mapImage]);

  // 맵 이미지와 selectedMap이 모두 설정된 후 노드 패치
  useEffect(() => {
    if (mapImage && selectedMap) {
      fetchNodes(selectedMap._id);
    }
  }, [mapImage, selectedMap]);

  // 노드 또는 금지구역 변경 시마다 캔버스에 다시 그리기
  useEffect(() => {
    if (canvasRef.current && mapImageRef.current) {
      drawAll(); // 모든 요소 다시 그리기
    }
  }, [nodes, noGoZones, currentRect, isDeletingNoGoZone]);

  // 노드 삭제 시 커서 복구
  useEffect(() => {
    if (
      !isDeletingNode &&
      !isCreatingNoGoZone &&
      !isDeletingNoGoZone &&
      !dragging
    ) {
      document.body.style.cursor = 'default'; // 모든 모드가 아닐 때 커서를 기본으로 복구
    }
  }, [isDeletingNode, isCreatingNoGoZone, isDeletingNoGoZone, dragging]);

  // 마우스가 캔버스 위를 움직일 때 노드 호버 상태 업데이트
  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;

    let foundNode = null;

    for (let node of nodes) {
      const { x, y } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
      const distance = Math.sqrt((mouseX - x) ** 2 + (mouseY - y) ** 2);
      if (distance < 5) { // 노드 반경 5px 내에 있을 때
        foundNode = node;
        break;
      }
    }

    if (foundNode) {
      setHoveredNode(foundNode);
      setTooltipPosition({ x: e.clientX, y: e.clientY });
    } else {
      setHoveredNode(null);
    }

    // 금지구역 생성 모드일 때 드래그 중인 사각형 업데이트
    if (isCreatingNoGoZone && dragging && dragStart) {
      const newRect = {
        x: Math.min(mouseX, dragStart.x),
        y: Math.min(mouseY, dragStart.y),
        width: Math.abs(mouseX - dragStart.x),
        height: Math.abs(mouseY - dragStart.y),
      };
      setCurrentRect(newRect);
    }
  };

  // 마우스가 캔버스를 벗어났을 때 호버 상태 초기화 및 드래그 상태 초기화
  const handleMouseLeave = () => {
    setHoveredNode(null);
    // 드래그 중이었는데 캔버스를 벗어났을 경우 드래그 취소
    if (isCreatingNoGoZone && dragging) {
      setDragging(false);
      setDragStart(null);
      setCurrentRect(null);
      setIsCreatingNoGoZone(false);
      document.body.style.cursor = 'default';
    }
  };

  // 드래그 시작 이벤트 핸들러
  const handleMouseDown = (e) => {
    if (isCreatingNoGoZone) {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const rect = canvas.getBoundingClientRect();
      const startX = e.clientX - rect.left;
      const startY = e.clientY - rect.top;
      setDragStart({ x: startX, y: startY });
      setDragging(true);
      document.body.style.cursor = 'crosshair';
    }

    // 금지구역 삭제 모드일 때
    if (isDeletingNoGoZone) {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const rect = canvas.getBoundingClientRect();
      const clickX = e.clientX - rect.left;
      const clickY = e.clientY - rect.top;

      // 금지구역 클릭 여부 확인
      const clickedZone = noGoZones.find(zone => {
        const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
        const bottomRight = mapToMapCoordinates(zone.bottomRight.x, zone.bottomRight.y);
        return clickX >= topLeft.x && clickX <= bottomRight.x && clickY >= topLeft.y && clickY <= bottomRight.y;
      });

      if (clickedZone) {
        handleDeleteNoGoZone(clickedZone._id);
      }
    }
  };

  // 드래그 종료 이벤트 핸들러
  const handleMouseUp = async (e) => {
    if (isCreatingNoGoZone && dragging && dragStart && currentRect) {
      const canvas = canvasRef.current;
      if (!canvas) return;

      const rect = canvas.getBoundingClientRect();
      const endX = e.clientX - rect.left;
      const endY = e.clientY - rect.top;

      // 드래그 완료 후 사각형의 최종 위치와 크기 설정
      const finalRect = {
        x: Math.min(endX, dragStart.x),
        y: Math.min(endY, dragStart.y),
        width: Math.abs(endX - dragStart.x),
        height: Math.abs(endY - dragStart.y),
      };

      // 실제 월드 좌표로 변환하여 금지 구역 생성
      const topLeftWorld = mapToWorld(finalRect.x, finalRect.y, imageNaturalWidth, imageNaturalHeight);
      const bottomRightWorld = mapToWorld(finalRect.x + finalRect.width, finalRect.y + finalRect.height, imageNaturalWidth, imageNaturalHeight);

      const noGoZone = {
        topLeft: topLeftWorld,
        bottomRight: bottomRightWorld,
        mapId: selectedMap._id,
      };

      // 금지구역 서버에 저장
      const token = localStorage.getItem('token');
      try {
        const response = await axios.post('http://172.30.1.33:5557/map/no-go-zones', noGoZone, {
          headers: { Authorization: `Bearer ${token}` },
        });
        alert('금지 구역이 성공적으로 생성되었습니다.');
        setNoGoZones([...noGoZones, response.data.noGoZone]); // 생성된 금지 구역 추가
      } catch (error) {
        console.error('금지 구역 생성 중 오류 발생:', error);
        alert('금지 구역 생성에 실패했습니다.');
      }

      // 상태 초기화
      setDragging(false);
      setDragStart(null);
      setCurrentRect(null);
      setIsCreatingNoGoZone(false);
      document.body.style.cursor = 'default';
      drawAll();
    }
  };

  // 금지구역을 삭제하는 함수
  const handleDeleteNoGoZone = async (zoneId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }

    const confirmDelete = window.confirm('이 금지 구역을 삭제하시겠습니까?');
    if (!confirmDelete) return;

    try {
      await axios.delete(`http://172.30.1.33:5557/map/no-go-zones/${zoneId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('금지 구역이 성공적으로 삭제되었습니다.');
      setNoGoZones(noGoZones.filter(zone => zone._id !== zoneId));
    } catch (error) {
      console.error('금지 구역 삭제 중 오류 발생:', error);
      alert('금지 구역 삭제에 실패했습니다.');
    } finally {
      setIsDeletingNoGoZone(false);
      document.body.style.cursor = 'default';
      drawAll();
    }
  };

  // 맵 이미지 클릭 시 노드 생성, 삭제, 연결 또는 웨이포인트 추가, 연결 해제
  const handleMapClick = async (e) => {
    if (!selectedMap) return;

    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const clickX = e.clientX - rect.left;
    const clickY = e.clientY - rect.top;

    const { x, y } = mapToWorld(clickX, clickY, imageNaturalWidth, imageNaturalHeight);

    try {
      // 기존 노드 생성, 삭제 모드 처리는 별도로 유지
      if (isCreatingNode) {
        // 노드 생성 로직
        setNodeX(x);
        setNodeY(y);
        setIsCreatingNode(false);
        setIsNodePopupOpen(true);
        document.body.style.cursor = 'default';
        return;
      }

      if (isDeletingNode) {
        // 클릭한 위치 근처에 있는 노드를 찾아 삭제
        let nodeToDelete = null;
        for (let node of nodes) {
          const { x: nodeX, y: nodeY } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
          const distance = Math.sqrt((clickX - nodeX) ** 2 + (clickY - nodeY) ** 2);
          if (distance < 5) {
            nodeToDelete = node;
            break;
          }
        }

        if (nodeToDelete) {
          const confirmDelete = window.confirm(`노드 "${nodeToDelete.name}"를 삭제하시겠습니까?`);
          if (confirmDelete) {
            try {
              const token = localStorage.getItem('token');
              await axios.delete(`http://172.30.1.33:5557/map/node/${nodeToDelete._id}`, { // DELETE 요청
                headers: { Authorization: `Bearer ${token}` },
              });
              alert('노드가 성공적으로 삭제되었습니다.');

              // 노드 목록을 다시 패치하고, 노드를 다시 그리기
              await fetchNodes(selectedMap._id);
              drawAll(); // 삭제 후 모든 요소 다시 그리기

              setIsDeletingNode(false); // 삭제 후 삭제 모드 종료
            } catch (error) {
              console.error('노드 삭제 중 오류 발생:', error);
              alert('노드 삭제에 실패했습니다.');
            }
          }
        } else {
          alert('해당 위치에 노드를 찾을 수 없습니다.');
        }
        return;
      }

      // 노드 연결 팝업이 열려 있을 때만 팝업에 클릭 이벤트 전달
      if (isConnectionPopupOpen) {
        // 팝업 컴포넌트에서 맵 클릭을 처리하도록 전달
        handleOpenNodeConnectionPopup(); // 팝업에서 자체적으로 클릭 이벤트를 처리하게 설정
        return;
      }

      // 기타 맵 클릭 처리 로직 (예: 금지구역 생성)
      // ...

    } catch (error) {
      console.error('맵 클릭 처리 중 오류 발생:', error);
      alert('맵 클릭 처리 중 오류가 발생했습니다.');
    } finally {
      // handleMapClick 이 끝나면 노드 정보를 다시 fetch하도록
      await fetchNodes(selectedMap._id);
      drawAll();
    }
  };

  // 맵 수정 팝업을 여는 함수
  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsMonitored(map.isMonitored || false); // 모니터링 상태 설정
    setIsEditPopupOpen(true); // 수정 팝업 열기
  };

  // 노드 생성 모드를 활성화하는 함수
  const handleCreateNodeMode = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setIsCreatingNode(true);
    setIsDeletingNoGoZone(false); // 삭제 모드 비활성화
    document.body.style.cursor = 'crosshair'; // 마우스 커서를 십자가 모양으로 변경
  };

  // 노드 삭제 모드를 활성화하는 함수
  const handleDeleteNodeMode = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setIsDeletingNode(true);
    setIsDeletingNoGoZone(false); // 삭제 모드 비활성화
    setIsCreatingNoGoZone(false); // 생성 모드 비활성화
    document.body.style.cursor = 'not-allowed'; // 커서를 삭제 모드로 변경
  };

  // Send to Robots 버튼과 관련된 함수 추가
  const handleSendMap = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }

    try {
      await axios.post('http://172.30.1.33:5559/robot/send_map', {}, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });
      alert('맵이 로봇에게 성공적으로 전송되었습니다.');
    } catch (error) {
      console.error('맵을 로봇에게 전송하는 중 오류 발생:', error);
      alert(`맵을 로봇에게 전송하는 중 오류 발생: ${error.response?.data?.message || error.message}`);
    }
  };

  // 맵 패치를 처리하는 함수 (기존 경로 삭제 후 재계산)
  const handlePatchMap = async () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }

    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }

    const confirmPatch = window.confirm('선택한 맵의 모든 노드 간의 최단 경로를 계산하고 저장하시겠습니까?');
    if (!confirmPatch) return;

    setIsPatchingMap(true); // 로딩 상태 시작

    try {
      // Step 1: 기존 경로 데이터 삭제
      await axios.delete(`http://172.30.1.33:5557/map/shortpaths/map/${selectedMap._id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log('기존 ShortPath 데이터가 삭제되었습니다.');

      // Step 2: 새로운 경로 계산 및 저장
      const response = await axios.post('http://172.30.1.33:5557/map/calculate-paths', {
        mapId: selectedMap._id
      }, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.status === 200) {
        alert('맵 패치가 성공적으로 완료되었습니다.');
        // 필요 시 추가적인 상태 업데이트나 데이터 패칭을 여기에 추가할 수 있습니다.
        await fetchNodes(selectedMap._id); // 노드 목록 갱신
        drawAll(); // 노드 및 간선 다시 그리기
      } else {
        alert('맵 패치에 실패했습니다.');
      }
    } catch (error) {
      console.error('맵 패치 중 오류 발생:', error);
      alert(`맵 패치 중 오류 발생: ${error.response?.data?.error || error.message}`);
    } finally {
      setIsPatchingMap(false); // 로딩 상태 종료
    }
  };

  // 노드 연결 팝업 열기 함수
  const handleOpenNodeConnectionPopup = () => {
    setIsConnectionPopupOpen(true);
  };

  // 노드 연결 팝업 완료 시 처리 함수
  const handleNodeConnectionComplete = (data) => {
    setNodeConnectionData(data); // 연결 결과 데이터 저장
    setIsConnectionPopupOpen(false);
    // 추가적인 처리 로직 (예: 서버에 연결 요청)
    // 여기서는 이미 팝업에서 서버에 연결 요청을 했으므로 별도의 추가 로직이 필요 없을 수 있습니다.
  };

  // 노드 연결 팝업 취소 시 처리 함수
  const handleNodeConnectionCancel = () => {
    setIsConnectionPopupOpen(false);
  };

  return (
    <div className="map-management-page">
      {/* 상단 네비게이션 바 */}
      <header className="map-management-header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      {/* 툴바: 네비게이션 아래에 위치 */}
      <div className="toolbar">
        <button onClick={handleCreateNodeMode} className="create-node-button">
          <FontAwesomeIcon icon={faPlus} /> 노드 생성
        </button>
        <button onClick={handleDeleteNodeMode} className="delete-node-button">
          <FontAwesomeIcon icon={faEdit} /> 노드 삭제
        </button>
        <button onClick={handleOpenNodeConnectionPopup} className="connect-node-button">
          <FontAwesomeIcon icon={faLink} /> 노드 연결
        </button>
        <button onClick={() => setIsConnectionPopupOpen(true)} className="disconnect-node-button">
          <FontAwesomeIcon icon={faUnlink} /> 연결 해제
        </button>
        {/* '금지 구역 생성' 버튼 추가 */}
        <button
          onClick={() => {
            setIsCreatingNoGoZone(true);
            setIsDeletingNoGoZone(false); // 삭제 모드 비활성화
            setDragging(false);
            setDragStart(null);
            setCurrentRect(null);
            document.body.style.cursor = 'crosshair'; // 금지 구역 생성 모드 커서로 변경
          }}
          className="create-nogozone-button"
        >
          금지 구역 생성
        </button>
        {/* '금지 구역 해제' 버튼 추가 */}
        <button
          onClick={() => {
            setIsDeletingNoGoZone(true);
            setIsCreatingNoGoZone(false); // 생성 모드 비활성화
            setDragging(false);
            setDragStart(null);
            setCurrentRect(null);
            document.body.style.cursor = 'not-allowed'; // 삭제 모드 커서로 변경
          }}
          className="delete-nogozone-button"
        >
          금지 구역 해제
        </button>
      </div>

      {/* 맵 리스트와 이미지 컨텐츠 */}
      <div className="map-management-content">
        {/* 맵 리스트 컨테이너 */}
        <div className="map-list-container">
          <h2 className="map-list-header">
            맵 리스트
            <button
              onClick={() => setIsUploadPopupOpen(true)}
              className="add-map-button"
            >
              <FontAwesomeIcon icon={faPlus} />
            </button>
          </h2>
          <ul className="map-list">
            {maps.map((map) => (
              <li
                key={map._id}
                className={`map-list-item ${selectedMap?._id === map._id ? 'selected-map' : ''}`}
              >
                <span
                  onClick={() => handleSelectMap(map)}
                  className="map-list-item-name"
                >
                  {map.name}
                </span>
                <button
                  onClick={() => openEditPopup(map)}
                  className="edit-map-button"
                >
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button
                  onClick={() => handleDeleteMap(map._id)}
                  className="delete-map-button"
                >
                  &times;
                </button>
              </li>
            ))}
          </ul>

          {selectedMap && (
            <div className="map-description">
              <h3>맵 설명</h3>
              <p>{selectedMap.description}</p>
            </div>
          )}
        </div>

        {/* 지도 이미지와 노드 렌더링 및 노드 리스트 영역 */}
        <div className="map-image-and-node-list">
          {/* 지도 이미지와 노드 렌더링 영역 */}
          <div
            className="map-image-container"
            style={{ position: 'relative' }}
          >
            {mapImage ? (
              <canvas
                ref={canvasRef}
                className="map-canvas"
                onMouseDown={handleMouseDown}
                onMouseMove={handleMouseMove}
                onMouseUp={handleMouseUp}
                onMouseLeave={handleMouseLeave}
                onClick={handleMapClick} // 맵 클릭 이벤트 추가
                style={{
                  cursor:
                    isCreatingNode
                      ? 'crosshair'
                      : isCreatingNoGoZone
                      ? 'crosshair'
                      : isDeletingNoGoZone
                      ? 'not-allowed'
                      : isConnectionPopupOpen
                      ? 'pointer'
                      : 'default'
                }} // 커서 스타일 수정
              />
            ) : (
              <p>맵을 선택하여 이미지를 확인하세요.</p>
            )}

            {/* 플로팅 Send to Robots 버튼과 Map Patch 버튼 추가 */}
            {selectedMap && (
              <div className="floating-buttons">
                <button
                  className="send-to-robots-button"
                  onClick={handleSendMap}
                >
                  로봇에게 전송
                </button>
                <button
                  className="map-patch-button"
                  onClick={handlePatchMap}
                  disabled={isPatchingMap} // 로딩 상태에 따라 버튼 비활성화
                >
                  {isPatchingMap ? '맵 패치 중...' : '맵 패치'}
                </button>
              </div>
            )}
          </div>

          {/* 오른쪽에 노드 목록 표시 */}
          <NodeList mapId={selectedMap?._id} /> {/* NodeList 컴포넌트 사용 */}
        </div>
      </div>

      {/* Node 생성 팝업 */}
      {isNodePopupOpen && (
        <div className="popup-container">
          <h3>노드 생성</h3>
          <p>X: {nodeX.toFixed(2)}, Y: {nodeY.toFixed(2)}</p>
          <input
            type="text"
            value={nodeName}
            onChange={(e) => setNodeName(e.target.value)}
            placeholder="노드 이름 입력"
            className="popup-input"
          />
          <div className="popup-button-container">
            <button onClick={handleSubmitNode} className="popup-submit-button">
              생성
            </button>
            <button
              onClick={() => setIsNodePopupOpen(false)}
              className="popup-cancel-button"
            >
              취소
            </button>
          </div>
        </div>
      )}

      {/* Node 연결 팝업 */}
      {isConnectionPopupOpen && (
        <NodeConnectionPopup
          onComplete={handleNodeConnectionComplete}
          onCancel={handleNodeConnectionCancel}
        />
      )}

      {/* Map Upload Popup */}
      {isUploadPopupOpen && (
        <div className="popup-container">
          <h3>맵 업로드</h3>
          <form onSubmit={handleUploadMap}>
            <input
              type="text"
              value={uploadName}
              onChange={(e) => setUploadName(e.target.value)}
              placeholder="맵 이름"
              required
              className="popup-input"
            />
            <textarea
              value={uploadDescription}
              onChange={(e) => setUploadDescription(e.target.value)}
              placeholder="맵 설명"
              required
              className="popup-textarea"
            />
            <label>맵 파일 (.pgm, .png, .jpg)</label>
            <input
              type="file"
              accept=".pgm,.png,.jpg"
              onChange={handleFileChange}
              required
              className="popup-file-input"
            />
            <label>YAML 파일 (.yaml)</label>
            <input
              type="file"
              accept=".yaml"
              onChange={handleYamlFileChange}
              required
              className="popup-file-input"
            />
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                업로드
              </button>
              <button
                type="button"
                onClick={() => setIsUploadPopupOpen(false)}
                className="popup-cancel-button"
              >
                취소
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Edit Map Popup */}
      {isEditPopupOpen && (
        <div className="popup-container">
          <h3>맵 수정</h3>
          <form onSubmit={handleUpdateMap}>
            <input
              type="text"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              placeholder="맵 이름"
              required
              className="popup-input"
            />
            <textarea
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              placeholder="맵 설명"
              required
              className="popup-textarea"
            />
            {/* 모니터링 지도 설정 체크박스 추가 */}
            <label style={{ marginTop: '10px', color: '#333' }}>
              <input
                type="checkbox"
                checked={isMonitored}
                onChange={(e) => setIsMonitored(e.target.checked)}
                style={{ marginRight: '10px' }}
              />
              모니터링 맵으로 설정
            </label>
            <div className="popup-button-container">
              <button type="submit" className="popup-submit-button">
                업데이트
              </button>
              <button
                type="button"
                onClick={() => setIsEditPopupOpen(false)}
                className="popup-cancel-button"
              >
                취소
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Hover Tooltip */}
      {hoveredNode && (
        <div
          className="tooltip"
          style={{
            position: 'fixed',
            top: `${tooltipPosition.y + 10}px`,
            left: `${tooltipPosition.x + 10}px`,
            backgroundColor: 'rgba(0, 0, 0, 0.7)',
            color: 'white',
            padding: '5px 10px',
            borderRadius: '4px',
            pointerEvents: 'none',
            zIndex: 1000,
            fontSize: '12px',
          }}
        >
          {hoveredNode.name}
        </div>
      )}

      {/* NodeDisconnect 컴포넌트에 클릭한 노드 정보 전달 */}
      <NodeDisconnect
        firstNodeId={null} // 연결 해제 기능은 별도로 구현 필요
        secondNodeId={null}
        onDisconnectSuccess={async (isDeleted) => {
          if (isDeleted) {  // 삭제가 완료된 경우에만 실행
            await fetchNodes(selectedMap._id); // 노드 정보 갱신
            drawAll(); // 갱신된 정보로 노드 및 간선 다시 그리기
          }
        }}
      />

      {/* 로딩 인디케이터 추가 */}
      {isPatchingMap && (
        <div className="loading-overlay">
          <div className="spinner"></div>
        </div>
      )}
    </div>
  );
}

export default Mapmanage;
