import React, { useState } from 'react';
import './CreateTaskFlow.css'; // 팝업 스타일 임포트

function CreateTaskFlow({ onClose, onTaskFlowAdd }) {
    const [nodeName, setNodeName] = useState(''); // 노드 이름
    const [stepName, setStepName] = useState(''); // 단계 이름

    const handleSubmit = () => {
        if (nodeName.trim() && stepName.trim()) {
            onTaskFlowAdd({ node: nodeName, step: stepName }); // 새로운 작업 플로우 추가
            onClose(); // 팝업 닫기
        } else {
            alert('노드 이름과 단계를 입력해주세요.');
        }
    };

    return (
        <div className="create-task-flow">
            <h3>작업 플로우 추가</h3>
            <input
                type="text"
                value={nodeName}
                onChange={(e) => setNodeName(e.target.value)}
                placeholder="노드 이름 입력"
            />
            <input
                type="text"
                value={stepName}
                onChange={(e) => setStepName(e.target.value)}
                placeholder="단계 이름 입력"
            />
            <div className="buttons">
                <button onClick={handleSubmit}>추가</button>
                <button onClick={onClose}>취소</button>
            </div>
        </div>
    );
}

export default CreateTaskFlow;
