import React, { useState } from 'react';
import axios from 'axios';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';

function TaskPage() {
    const [taskName, setTaskName] = useState(''); // 작업 이름 상태
    const [tasks, setTasks] = useState([]); // 작업 목록 상태
    const [error, setError] = useState(''); // 오류 상태

    // 작업 추가 함수
    const addTask = async () => {
        if (taskName.trim() === '') {
            setError('작업 이름을 입력하세요.');
            return;
        }

        try {
            const response = await axios.post('http://localhost:5557/steps', { name: taskName });
            setTasks([...tasks, response.data.step]); // 새로 추가된 작업을 상태에 추가
            setTaskName(''); // 입력 필드 초기화
            setError(''); // 오류 메시지 초기화
        } catch (error) {
            setError('작업 추가 중 오류가 발생했습니다.');
            console.error(error);
        }
    };

    return (
        <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
            <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
                <UserInfo />
                <LogoutButton />
            </header>
            <div style={{ display: 'flex' }}>
                <Navbar />
                <div style={{ marginLeft: '20px', flex: 1 }}>
                    <h2>작업 추가</h2>
                    <input 
                        type="text" 
                        value={taskName} 
                        onChange={(e) => setTaskName(e.target.value)} 
                        placeholder="작업 이름을 입력하세요" 
                        style={{ marginRight: '10px' }}
                    />
                    <button onClick={addTask}>추가</button>

                    {error && <p style={{ color: 'red' }}>{error}</p>}

                    <h3>작업 목록</h3>
                    <ul>
                        {tasks.map((task) => (
                            <li key={task._id}>{task.name}</li>
                        ))}
                    </ul>
                </div>
            </div>
        </div>
    );
}

export default TaskPage;
