import React, { useEffect, useRef } from 'react';
import gsap from 'gsap';

function MapComponent({ robots, nodes, mapUrl }) {
  const canvasRef = useRef(null);
  const mapImageRef = useRef(null);
  const robotRefs = useRef([]);
  const nodeRefs = useRef([]);

  const mapResolution = 0.05; // 해상도 값 (픽셀당 미터)
  const mapOrigin = [-10.0, -10.0]; // 원점 좌표 (x, y)

  // 지도 이미지를 로드하고 그리는 함수
  const loadMapImage = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    mapImageRef.current = new Image();
    mapImageRef.current.src = mapUrl;

    mapImageRef.current.onload = () => {
      const containerWidth = canvas.parentElement.clientWidth;
      const containerHeight = canvas.parentElement.clientHeight;
      const imgWidth = mapImageRef.current.width;
      const imgHeight = mapImageRef.current.height;
      const imgAspectRatio = imgWidth / imgHeight;
      const containerAspectRatio = containerWidth / containerHeight;

      let drawWidth, drawHeight;

      if (imgAspectRatio > containerAspectRatio) {
        drawWidth = containerWidth;
        drawHeight = containerWidth / imgAspectRatio;
      } else {
        drawHeight = containerHeight;
        drawWidth = containerHeight * imgAspectRatio;
      }

      canvas.width = drawWidth;
      canvas.height = drawHeight;

      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.drawImage(mapImageRef.current, 0, 0, drawWidth, drawHeight);

      drawNodes(ctx);
      drawRobots(ctx);
    };

    mapImageRef.current.onerror = (err) => {
      console.error('지도 이미지를 로드하는 중 오류 발생:', err);
    };
  };

  // 로봇들을 그리는 함수
  const drawRobots = (ctx) => {
    if (!mapImageRef.current || !robots) return;

    robots.forEach((robot, index) => {
      const { x, y } = robot?.location || {};
      if (x === undefined || y === undefined) return;

      const { mapX, mapY } = worldToMap(x, y);

      let robotColor = 'gray';
      if (robot.status?.state === 'Tasking') {
        robotColor = 'blue';
      } else if (robot.status?.state === 'Waiting') {
        robotColor = 'yellow';
      } else if (robot.status?.state === 'Error') {
        robotColor = 'red';
      }

      if (!robotRefs.current[index]) {
        robotRefs.current[index] = { mapX, mapY, robotColor };
      }

      gsap.to(robotRefs.current[index], {
        mapX,
        mapY,
        duration: 1.5,
        ease: 'power2.out',
        onUpdate: () => {
          ctx.fillStyle = robotRefs.current[index].robotColor;
          ctx.beginPath();
          ctx.arc(robotRefs.current[index].mapX, robotRefs.current[index].mapY, 5, 0, 2 * Math.PI);
          ctx.fill();
        },
      });
    });
  };

  // 노드들을 그리는 함수
  const drawNodes = (ctx) => {
    if (!mapImageRef.current || !nodes || nodes.length === 0) {
      console.log('노드 데이터가 없습니다.');
      return;
    }

    nodes.forEach((node, index) => {
      const { location } = node;
      if (!location || location.x === undefined || location.y === undefined) {
        console.error(`노드 위치 정보가 없습니다: ${node}`);
        return;
      }

      const { x, y } = location;
      const { mapX, mapY } = worldToMap(x, y);

      let nodeColor = 'red';

      if (!nodeRefs.current[index]) {
        nodeRefs.current[index] = { mapX, mapY, nodeColor };
      }

      gsap.to(nodeRefs.current[index], {
        mapX,
        mapY,
        duration: 1.5,
        ease: 'power2.out',
        onUpdate: () => {
          ctx.fillStyle = nodeRefs.current[index].nodeColor;
          ctx.beginPath();
          ctx.arc(nodeRefs.current[index].mapX, nodeRefs.current[index].mapY, 5, 0, 2 * Math.PI);
          ctx.fill();
        },
      });
    });
  };

  // 월드 좌표를 맵 좌표로 변환하는 함수
  const worldToMap = (x, y) => {
    const mapImage = mapImageRef.current;
    const mapWidth = mapImage.width;
    const mapHeight = mapImage.height;

    const mapX = (x - mapOrigin[0]) / mapResolution;
    const mapY = mapHeight - (y - mapOrigin[1]) / mapResolution;
    return { mapX, mapY };
  };

  useEffect(() => {
    if (mapUrl) {
      loadMapImage();
    }
  }, [mapUrl]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (mapImageRef.current && canvas) {
      const ctx = canvas.getContext('2d');
      drawRobots(ctx);

      if (nodes && nodes.length > 0) {
        drawNodes(ctx);
      }
    }
  }, [robots, nodes]);

  return (
    <div className="map-canvas-container">
      {mapUrl ? (
        <canvas ref={canvasRef} className="canvas"></canvas>
      ) : (
        <p>지도를 불러오는 중...</p>
      )}
    </div>
  );
}

export default MapComponent;
