import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css'; // CSS 파일 임포트

function MapManagementPage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null);
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false);
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false);

  useEffect(() => {
    fetchMaps();
  }, []);

  const fetchMaps = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    try {
      const response = await axios.get('http://172.30.1.15:5557/map/maps', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMaps(response.data);
    } catch (error) {
      console.error('Error fetching maps:', error);
    }
  };

  const handleSelectMap = async (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);

    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.15:5557/map/file/${map._id}`, {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob',
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl);
    } catch (error) {
      console.error('Error fetching map image:', error);
    }
  };

  const handleDeleteMap = async (mapId) => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    const confirmDelete = window.confirm('Are you sure you want to delete this map?');
    if (!confirmDelete) return;
    try {
      await axios.put(`http://172.30.1.15:5557/map/delete/${mapId}`, {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Map deleted successfully');
      fetchMaps();
      if (selectedMap && selectedMap._id === mapId) setSelectedMap(null);
    } catch (error) {
      console.error('Error deleting map:', error);
      alert('Error deleting map');
    }
  };

  const handleFileChange = (e) => setMapFile(e.target.files[0]);
  const handleYamlFileChange = (e) => setYamlFile(e.target.files[0]);

  const handleUploadMap = async (e) => {
    e.preventDefault();
    if (uploadName.length < 2) {
      alert('Map name must be at least 2 characters long.');
      return;
    }
    const formData = new FormData();
    formData.append('name', uploadName);
    formData.append('description', uploadDescription);
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);

    const token = localStorage.getItem('token');
    if (!token) {
      alert('No token found, please log in again.');
      return;
    }
    try {
      await axios.post('http://172.30.1.15:5557/map/upload', formData, {
        headers: { Authorization: `Bearer ${token}`, 'Content-Type': 'multipart/form-data' },
      });
      alert('Map uploaded successfully');
      setIsUploadPopupOpen(false);
      fetchMaps();
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  const openEditPopup = (map) => {
    setSelectedMap(map);
    setEditName(map.name);
    setEditDescription(map.description);
    setIsEditPopupOpen(true);
  };

  return (
    <div className="map-management-container">
      <header className="map-management-header">
        <UserInfo />
        <LogoutButton />
      </header>
      <Navbar />

      {/* 새로운 버튼 컨테이너 */}
      <div className="action-buttons-container">
        <button className="node-add-button" onClick={() => alert('노드 추가 버튼 클릭')}>
          Add Node
        </button>
      </div>

      <div className="map-management-content">
        <div className="map-list-container">
          <h2 className="map-list-header">
            Map List
            <button onClick={() => setIsUploadPopupOpen(true)} className="upload-button">+</button>
          </h2>
          <ul className="map-list">
            {maps.map((map) => (
              <li key={map._id} className={`map-list-item ${selectedMap && selectedMap._id === map._id ? 'selected' : ''}`}>
                <span onClick={() => handleSelectMap(map)}>{map.name}</span>
                <button onClick={() => openEditPopup(map)} className="edit-button">
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button onClick={() => handleDeleteMap(map._id)} className="delete-button">&times;</button>
              </li>
            ))}
          </ul>

          {selectedMap && (
            <div className="map-description">
              <h3>Map Description</h3>
              <p>{selectedMap.description}</p>
            </div>
          )}
        </div>

        <div className="map-image-container">
          {mapImage ? (
            <img src={mapImage} alt="Selected Map" className="map-image" />
          ) : (
            <p>Select a map to view its image</p>
          )}
        </div>
      </div>

      {isUploadPopupOpen && (
        <div className="popup">
          <h3>Upload Map</h3>
          <form onSubmit={handleUploadMap}>
            <input type="text" value={uploadName} onChange={(e) => setUploadName(e.target.value)} placeholder="Map Name" required />
            <textarea value={uploadDescription} onChange={(e) => setUploadDescription(e.target.value)} placeholder="Map Description" required />
            <label>Map File (.pgm, .png, .jpg)</label>
            <input type="file" accept=".pgm,.png,.jpg" onChange={handleFileChange} required />
            <label>YAML File (.yaml)</label>
            <input type="file" accept=".yaml" onChange={handleYamlFileChange} required />
            <div className="popup-buttons">
              <button type="submit">Upload</button>
              <button type="button" onClick={() => setIsUploadPopupOpen(false)}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

export default MapManagementPage;
