// src/components/MapManage/NodeConnectionPopup.js

import React from 'react';
import './NodeConnectionPopup.css';

function NodeConnectionPopup({ onClose, onCreate, connectionData, startSelectingNode1, startSelectingNode2, startSelectingWaypoint }) {
  const handleCreate = () => {
    onCreate(connectionData);
  };

  return (
    <div className="node-connection-slide-in open">
      <div className="slide-header">
        <h3>노드 연결 설정</h3>
        <button className="close-btn" onClick={onClose}>
          &times;
        </button>
      </div>

      <div className="input-group">
        <label>노드 1</label>
        <div className="coordinate-input">
          <input type="text" readOnly value={connectionData.node1 ? connectionData.node1.name : '선택하세요'} />
          <button onClick={startSelectingNode1}>셋</button>
        </div>
      </div>

      <div className="input-group">
        <label>노드 2</label>
        <div className="coordinate-input">
          <input type="text" readOnly value={connectionData.node2 ? connectionData.node2.name : '선택하세요'} />
          <button onClick={startSelectingNode2}>셋</button>
        </div>
      </div>

      <h4>경유지 (웨이포인트)</h4>
      {connectionData.waypoints.map((wp, index) => (
        <div key={index} className="waypoint-input-group">
          <label>웨이포인트 {index + 1}</label>
          <div className="coordinate-input">
            <input
              type="text"
              readOnly
              value={`X: ${wp.x || ''}, Y: ${wp.y || ''}`}
            />
            <button onClick={() => alert(`웨이포인트 ${index + 1}은 이미 설정되었습니다.`)}>
              셋
            </button>
          </div>
          <button className="remove-btn" onClick={() => {
            // Remove waypoint logic can be implemented here if needed
            alert('웨이포인트 삭제 기능은 아직 구현되지 않았습니다.');
          }}>
            -
          </button>
        </div>
      ))}
      <button className="add-btn" onClick={startSelectingWaypoint}>+</button>

      <div className="button-group">
        <button className="create-btn" onClick={handleCreate}>생성</button>
        <button className="cancel-btn" onClick={onClose}>취소</button>
      </div>
    </div>
  );
}

export default NodeConnectionPopup;
