// src/components/MapManage/NodeConnectionPopup.js

import React, { useState } from 'react';
import './NodeConnectionPopup.css';

function NodeConnectionPopup({ onClose, onCreate }) {
  const [node1, setNode1] = useState({ x: '', y: '', id: '' });
  const [node2, setNode2] = useState({ x: '', y: '', id: '' });
  const [waypoints, setWaypoints] = useState([{ x: '', y: '' }]);

  const handleAddWaypoint = () => {
    setWaypoints([...waypoints, { x: '', y: '' }]);
  };

  const handleRemoveWaypoint = (index) => {
    setWaypoints(waypoints.filter((_, i) => i !== index));
  };

  const handleWaypointChange = (index, field, value) => {
    const newWaypoints = [...waypoints];
    newWaypoints[index][field] = value;
    setWaypoints(newWaypoints);
  };

  const handleCreate = () => {
    onCreate({ node1, node2, waypoints });
    onClose();
  };

  return (
    <div className="popup-container" onClick={onClose}>
      <div className="node-connection-popup" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>
          &times;
        </button>
        <h3>노드 연결 설정</h3>

        <div className="input-group">
          <label>노드 1</label>
          <div className="coordinate-input">
            <input type="text" readOnly value={node1.id || '선택하세요'} />
            <button onClick={() => alert('맵에서 노드 1을 선택하세요.')}>셋</button>
          </div>
        </div>

        <div className="input-group">
          <label>노드 2</label>
          <div className="coordinate-input">
            <input type="text" readOnly value={node2.id || '선택하세요'} />
            <button onClick={() => alert('맵에서 노드 2를 선택하세요.')}>셋</button>
          </div>
        </div>

        <h4>경유지 (웨이포인트)</h4>
        {waypoints.map((wp, index) => (
          <div key={index} className="waypoint-input-group">
            <label>웨이포인트 {index + 1}</label>
            <div className="coordinate-input">
              <input
                type="text"
                readOnly
                value={`X: ${wp.x || ''}, Y: ${wp.y || ''}`}
              />
              <button onClick={() => alert(`맵에서 웨이포인트 ${index + 1} 선택하세요.`)}>
                셋
              </button>
            </div>
            <button className="remove-btn" onClick={() => handleRemoveWaypoint(index)}>
              -
            </button>
          </div>
        ))}
        <button className="add-btn" onClick={handleAddWaypoint}>+</button>

        <div className="button-group">
          <button className="create-btn" onClick={handleCreate}>생성</button>
          <button className="cancel-btn" onClick={onClose}>취소</button>
        </div>
      </div>
    </div>
  );
}

export default NodeConnectionPopup;
