// src/components/MapManage/Mapmanage.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faPlus, faLink, faUnlink } from '@fortawesome/free-solid-svg-icons';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import './Mapmanage.css';
import { worldToMap, mapToWorld } from '../../utils/coordinateUtils';
import NodeList from './NodeList'; // NodeList 컴포넌트 임포트
import NodeDisconnect from './NodeDisconnect'; // NodeDisconnect 컴포넌트 임포트
import NodeConnectPopup from './NodeConnectPopup'; // NodeConnectPopup 컴포넌트 임포트

function Mapmanage() {
  const [maps, setMaps] = useState([]);
  const [selectedMap, setSelectedMap] = useState(null); // 선택된 맵 상태
  const [editName, setEditName] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [isMonitored, setIsMonitored] = useState(false); // 모니터링 설정 체크박스 상태
  const [uploadName, setUploadName] = useState('');
  const [uploadDescription, setUploadDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);
  const [mapImage, setMapImage] = useState(null);
  const [isUploadPopupOpen, setIsUploadPopupOpen] = useState(false); // 업로드 팝업 상태
  const [isEditPopupOpen, setIsEditPopupOpen] = useState(false); // 수정 팝업 상태
  const [isCreatingNode, setIsCreatingNode] = useState(false); // 노드 생성 모드
  const [nodeX, setNodeX] = useState(null); // X 좌표
  const [nodeY, setNodeY] = useState(null); // Y 좌표
  const [nodeName, setNodeName] = useState(''); // 노드 이름
  const [isNodePopupOpen, setIsNodePopupOpen] = useState(false); // 노드 팝업 상태
  const canvasRef = useRef(null); // 캔버스 참조
  const mapImageRef = useRef(null); // 지도 이미지 참조
  const [nodes, setNodes] = useState([]); // 노드 목록 저장
  const [isDeletingNode, setIsDeletingNode] = useState(false);
  const [imageNaturalWidth, setImageNaturalWidth] = useState(null);
  const [imageNaturalHeight, setImageNaturalHeight] = useState(null);

  // Hover 상태 관리
  const [hoveredNode, setHoveredNode] = useState(null);
  const [tooltipPosition, setTooltipPosition] = useState({
    x: 0,
    y: 0
  });

  // 연결 팝업 상태 관리
  const [isConnectPopupOpen, setIsConnectPopupOpen] = useState(false);
  const [connectionData, setConnectionData] = useState({
    firstNode: {
      x: '',
      y: ''
    },
    secondNode: {
      x: '',
      y: ''
    },
    waypoints: []
  });
  const [activeField, setActiveField] = useState(null); // 'firstNode', 'secondNode', 'waypoint-0', etc.

  // 연결 해제 단계 관리
  const [disconnectStep, setDisconnectStep] = useState('idle'); // 'idle', 'selectingFirstNode', 'selectingSecondNode'
  const [disconnectFirstNodeId, setDisconnectFirstNodeId] = useState(null);
  const [disconnectSecondNodeId, setDisconnectSecondNodeId] = useState(null);

  // 금지구역 관련 상태
  const [isCreatingNoGoZone, setIsCreatingNoGoZone] = useState(false); // 금지 구역 생성 모드
  const [isDeletingNoGoZone, setIsDeletingNoGoZone] = useState(false); // 금지 구역 삭제 모드
  const [dragging, setDragging] = useState(false); // 드래그 상태
  const [dragStart, setDragStart] = useState(null); // 드래그 시작 지점
  const [currentRect, setCurrentRect] = useState(null); // 현재 그려지고 있는 사각형
  const [noGoZones, setNoGoZones] = useState([]); // 금지구역 목록 저장

  // 맵 패치 로딩 상태 추가
  const [isPatchingMap, setIsPatchingMap] = useState(false); // 맵 패치 로딩 상태

  // 맵 목록을 서버에서 가져오는 함수
  const fetchMaps = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }
    try {
      const response = await axios.get('http://172.30.1.33:5557/map/maps', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setMaps(response.data);

      // 맵이 하나 이상 있을 경우 첫 번째 맵을 자동으로 선택
      if (response.data.length > 0) {
        await handleSelectMap(response.data[0]);
      }
    } catch (error) {
      console.error('맵을 가져오는 중 오류 발생:', error);
      alert('맵을 가져오는 데 실패했습니다.');
    }
  };
  useEffect(() => {
    fetchMaps();
  }, []);

  // 노드 목록을 서버에서 가져오는 함수
  const fetchNodes = async mapId => {
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/nodes/${mapId}`);
      if (response.status === 200) {
        setNodes(response.data); // 노드 리스트 상태 업데이트
      } else {
        setNodes([]); // 실패 시 빈 배열로 초기화
      }
    } catch (error) {
      console.error('노드를 가져오는 중 오류 발생:', error);
      setNodes([]); // 오류 발생 시에도 빈 배열로 초기화
      alert('노드를 가져오는 데 실패했습니다.');
    }
  };

  // 금지구역 목록을 서버에서 가져오는 함수
  const fetchNoGoZones = async mapId => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('토큰이 없습니다. 다시 로그인해주세요.');
      return;
    }
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/no-go-zones/map/${mapId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      setNoGoZones(response.data);
    } catch (error) {
      console.error('금지구역을 가져오는 중 오류 발생:', error);
      alert('금지구역을 가져오는 데 실패했습니다.');
    }
  };

  // 맵을 선택했을 때 호출되는 함수
  const handleSelectMap = async map => {
    setSelectedMap(map); // 맵 선택 상태 업데이트
    setEditName(map.name);
    setEditDescription(map.description);
    setIsMonitored(map.isMonitored || false); // 모니터링 지도 상태 설정

    // 맵 이미지 가져오기
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get(`http://172.30.1.33:5557/map/file/${map._id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        },
        responseType: 'blob'
      });
      const imageUrl = URL.createObjectURL(response.data);
      setMapImage(imageUrl); // 이미지 설정
    } catch (error) {
      console.error('맵 이미지를 가져오는 중 오류 발생:', error);
      alert('맵 이미지를 가져오는 데 실패했습니다.');
    }

    // 금지구역 가져오기
    await fetchNoGoZones(map._id);
  };

  // 이미지 로드 시 호출되는 함수 (지도 이미지를 캔버스에 그리기)
  const loadMapImage = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    mapImageRef.current = new Image();
    mapImageRef.current.src = mapImage;
    mapImageRef.current.onload = () => {
      const imgWidth = mapImageRef.current.width;
      const imgHeight = mapImageRef.current.height;
      setImageNaturalWidth(imgWidth);
      setImageNaturalHeight(imgHeight);

      // 캔버스 크기 조정 및 기존 내용 클리어
      canvas.width = imgWidth;
      canvas.height = imgHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 지도 이미지를 캔버스에 그리기
      ctx.drawImage(mapImageRef.current, 0, 0, imgWidth, imgHeight);
      drawAll(); // 모든 요소 그리기 (노드, 금지구역 등)
    };
  };

  // 맵 좌표계를 캔버스 좌표계로 변환하는 함수
  const mapToMapCoordinates = (x, y) => {
    return worldToMap(x, y, imageNaturalWidth, imageNaturalHeight);
  };

  // 캔버스에 모든 요소를 그리는 함수 (지도, 노드, 연결선, 금지구역)
  const drawAll = () => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    if (!canvas || !imageNaturalWidth || !imageNaturalHeight) return;

    // 캔버스 클리어
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // 지도 이미지 그리기
    ctx.drawImage(mapImageRef.current, 0, 0, imageNaturalWidth, imageNaturalHeight);

    // 금지구역 그리기
    noGoZones.forEach(zone => {
      const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
      const bottomRight = mapToMapCoordinates(zone.bottomRight.x, zone.bottomRight.y);
      const width = bottomRight.x - topLeft.x;
      const height = bottomRight.y - topLeft.y;
      ctx.fillStyle = 'rgba(255, 0, 0, 0.3)';
      ctx.fillRect(topLeft.x, topLeft.y, width, height);
      ctx.strokeStyle = 'red';
      ctx.lineWidth = 2;
      ctx.strokeRect(topLeft.x, topLeft.y, width, height);
    });

    // 드래그 중인 사각형 그리기
    if (currentRect) {
      ctx.fillStyle = 'rgba(255, 0, 0, 0.2)'; // 반투명 붉은 배경
      ctx.fillRect(currentRect.x, currentRect.y, currentRect.width, currentRect.height);
      ctx.strokeStyle = 'red'; // 빨간색 테두리
      ctx.lineWidth = 1;
      ctx.strokeRect(currentRect.x, currentRect.y, currentRect.width, currentRect.height);
    }

    // 연결선을 먼저 그리기
    nodes.forEach(node => {
      node.connections.forEach(connection => {
        const connectedNode = nodes.find(n => n._id === connection.node);
        if (connectedNode) {
          const {
            x: startX,
            y: startY
          } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
          const {
            x: endX,
            y: endY
          } = worldToMap(connectedNode.x, connectedNode.y, imageNaturalWidth, imageNaturalHeight);
          ctx.strokeStyle = 'blue';
          ctx.lineWidth = 2;
          ctx.beginPath();
          ctx.moveTo(startX, startY);

          // 웨이포인트가 있는 경우
          if (connection.waypoints && connection.waypoints.length > 0) {
            connection.waypoints.forEach(wp => {
              const {
                x: wpX,
                y: wpY
              } = worldToMap(wp.x, wp.y, imageNaturalWidth, imageNaturalHeight);
              ctx.lineTo(wpX, wpY);
            });
          }
          ctx.lineTo(endX, endY);
          ctx.stroke();
        }
      });
    });

    // 금지구역 삭제 모드일 때, 금지구역 위에 삭제 아이콘 표시
    if (isDeletingNoGoZone) {
      noGoZones.forEach(zone => {
        const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
        const size = 20;

        // X 아이콘 그리기
        ctx.fillStyle = 'red';
        ctx.font = 'bold 16px Arial';
        ctx.textAlign = 'center';
        ctx.textBaseline = 'middle';

        // X 위치를 우측 상단에 배치
        ctx.fillText('X', topLeft.x + 8, topLeft.y + 15);
      });
    }

    // 노드 그리기
    nodes.forEach(node => {
      const {
        x,
        y
      } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
      const size = 10;
      ctx.fillStyle = 'red';
      ctx.fillRect(x - size / 2, y - size / 2, size, size);
    });
  };

  // 이미지 로드 후 캔버스에 지도 그리기
  useEffect(() => {
    if (mapImage) {
      loadMapImage();
    }
  }, [mapImage]);

  // 맵 이미지와 selectedMap이 모두 설정된 후 노드 패치
  useEffect(() => {
    if (mapImage && selectedMap) {
      fetchNodes(selectedMap._id);
    }
  }, [mapImage, selectedMap]);

  // 노드 또는 금지구역 변경 시마다 캔버스에 다시 그리기
  useEffect(() => {
    if (canvasRef.current && mapImageRef.current) {
      drawAll(); // 모든 요소 다시 그리기
    }
  }, [nodes, noGoZones, currentRect, isDeletingNoGoZone, connectionData]);

  // 노드 삭제 시 커서 복구
  useEffect(() => {
    if (!isDeletingNode && disconnectStep === 'idle' && !isCreatingNoGoZone && !isDeletingNoGoZone && !isConnectPopupOpen && !dragging) {
      document.body.style.cursor = 'default'; // 모든 모드가 아닐 때 커서를 기본으로 복구
    }
  }, [isDeletingNode, disconnectStep, isCreatingNoGoZone, isDeletingNoGoZone, isConnectPopupOpen, dragging]);

  // 마우스가 캔버스 위를 움직일 때 노드 호버 상태 업데이트
  const handleMouseMove = e => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const rect = canvas.getBoundingClientRect();
    const mouseX = e.clientX - rect.left;
    const mouseY = e.clientY - rect.top;
    let foundNode = null;
    for (let node of nodes) {
      const {
        x,
        y
      } = worldToMap(node.x, node.y, imageNaturalWidth, imageNaturalHeight);
      const distance = Math.sqrt((mouseX - x) ** 2 + (mouseY - y) ** 2);
      if (distance < 5) {
        // 노드 반경 5px 내에 있을 때
        foundNode = node;
        break;
      }
    }
    if (foundNode) {
      setHoveredNode(foundNode);
      setTooltipPosition({
        x: e.clientX,
        y: e.clientY
      });
    } else {
      setHoveredNode(null);
    }

    // 금지구역 생성 모드일 때 드래그 중인 사각형 업데이트
    if (isCreatingNoGoZone && dragging && dragStart) {
      const newRect = {
        x: Math.min(mouseX, dragStart.x),
        y: Math.min(mouseY, dragStart.y),
        width: Math.abs(mouseX - dragStart.x),
        height: Math.abs(mouseY - dragStart.y)
      };
      setCurrentRect(newRect);
    }
  };

  // 마우스가 캔버스를 벗어났을 때 호버 상태 초기화 및 드래그 상태 초기화
  const handleMouseLeave = () => {
    setHoveredNode(null);
    // 드래그 중이었는데 캔버스를 벗어났을 경우 드래그 취소
    if (isCreatingNoGoZone && dragging) {
      setDragging(false);
      setDragStart(null);
      setCurrentRect(null);
      setIsCreatingNoGoZone(false);
      document.body.style.cursor = 'default';
    }
  };

  // 드래그 시작 이벤트 핸들러
  const handleMouseDown = e => {
    if (isCreatingNoGoZone) {
      const canvas = canvasRef.current;
      if (!canvas) return;
      const rect = canvas.getBoundingClientRect();
      const startX = e.clientX - rect.left;
      const startY = e.clientY - rect.top;
      setDragStart({
        x: startX,
        y: startY
      });
      setDragging(true);
      document.body.style.cursor = 'crosshair';
    }

    // 금지구역 삭제 모드일 때
    if (isDeletingNoGoZone) {
      const canvas = canvasRef.current;
      if (!canvas) return;
      const rect = canvas.getBoundingClientRect();
      const clickX = e.clientX - rect.left;
      const clickY = e.clientY - rect.top;

      // 금지구역 클릭 여부 확인
      const clickedZone = noGoZones.find(zone => {
        const topLeft = mapToMapCoordinates(zone.topLeft.x, zone.topLeft.y);
        const bottomRight = mapToMapCoordinates(zone.bottomRight.x, zone.bottomRight.y);
        return clickX >= topLeft.x && clickX <= bottomRight.x && clickY >= topLeft.y && clickY <= bottomRight.y;
      });
      if (clickedZone) {
        handleDeleteNoGoZone(clickedZone._id);
      }
    }
  };

  // 드래그 종료 이벤트 핸들러
  const handleMouseUp = async e => {
    if (isCreatingNoGoZone && dragging && dragStart && currentRect) {
      const canvas = canvasRef.current;
      if (!canvas) return;
      const rect = canvas.getBoundingClientRect();
      const endX = e.clientX - rect.left;
      const endY = e.clientY - rect.top;

      // 드래그 완료 후 사각형의 최종 위치와 크기 설정
      const finalRect = {
        x: Math.min(endX, dragStart.x),
        y: Math.min(endY, dragStart.y),
        width: Math.abs(endX - dragStart.x),
        height: Math.abs(endY - dragStart.y)
      };

      // 실제 월드 좌표로 변환하여 금지 구역 생성
      const topLeftWorld = mapToWorld(finalRect.x, finalRect.y, imageNaturalWidth, imageNaturalHeight);
      const bottomRightWorld = mapToWorld(finalRect.x + finalRect.width, finalRect.y + finalRect.height, imageNaturalWidth, imageNaturalHeight);
      const noGoZone = {
        topLeft: topLeftWorld,
        bottomRight: bottomRightWorld,
        mapId: selectedMap._id
      };

      // 금지구역 서버에 저장
      const token = localStorage.getItem('token');
      try {
        const response = await axios.post('http://172.30.1.33:5557/map/no-go-zones', noGoZone, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        alert('금지 구역이 성공적으로 생성되었습니다.');
        setNoGoZones([...noGoZones, response.data.noGoZone]); // 생성된 금지 구역 추가
      } catch (error) {
        console.error('금지 구역 생성 중 오류 발생:', error);
        alert('금지 구역 생성에 실패했습니다.');
      }

      // 상태 초기화
      setDragging(false);
      setDragStart(null);
      setCurrentRect(null);
      setIsCreatingNoGoZone(false);
      document.body.style.cursor = 'default';
      drawAll();
    }
  };

  // 노드 연결 모드 활성화 함수 (팝업 열기)
  const handleConnectNodeMode = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setIsConnectPopupOpen(true);
    setConnectionData({
      firstNode: {
        x: '',
        y: ''
      },
      secondNode: {
        x: '',
        y: ''
      },
      waypoints: []
    });
    setActiveField(null);
  };

  // 연결 해제 모드 활성화 함수
  const handleDisconnectNodeMode = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setDisconnectStep('selectingFirstNode');
    setDisconnectFirstNodeId(null);
    setDisconnectSecondNodeId(null);
    alert('연결을 해제할 첫 번째 노드를 선택하세요.');
    document.body.style.cursor = 'pointer'; // 연결 해제 모드 커서로 변경
  };

  // 금지구역 생성 모드 활성화 함수
  const handleCreateNoGoZone = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setIsCreatingNoGoZone(true);
    setIsDeletingNoGoZone(false); // 삭제 모드 비활성화
    setDragging(false);
    setDragStart(null);
    setCurrentRect(null);
    document.body.style.cursor = 'crosshair'; // 금지 구역 생성 모드 커서로 변경
  };

  // 금지구역 삭제 모드를 활성화하는 함수
  const handleDeleteNoGoZoneMode = () => {
    if (!selectedMap) {
      alert('먼저 맵을 선택해주세요.');
      return;
    }
    setIsDeletingNoGoZone(true);
    setIsCreatingNoGoZone(false); // 생성 모드 비활성화
    setDragging(false);
    setDragStart(null);
    setCurrentRect(null);
    alert('삭제할 금지 구역을 클릭하세요.');
    document.body.style.cursor = 'not-allowed'; // 삭제 모드 커서로 변경
  };

  // 금지구역을 삭제하는 함수
  const handleDeleteNoGoZone = async zoneId => {
