import React, { useState } from 'react';
import './CreateTaskFlow.css'; // 스타일 파일 임포트

function CreateTaskFlow({ onClose, onTaskFlowAdd }) {
    const [nodeName, setNodeName] = useState('');
    const [stepName, setStepName] = useState('');

    const handleSubmit = () => {
        onTaskFlowAdd(nodeName, stepName); // 부모 컴포넌트에 작업 플로우 추가 요청
    };

    return (
        <div className="create-task-flow">
            <h3>작업 플로우 추가</h3>
            <input 
                type="text" 
                value={nodeName} 
                onChange={(e) => setNodeName(e.target.value)} 
                placeholder="노드 이름 입력"
            />
            <input 
                type="text" 
                value={stepName} 
                onChange={(e) => setStepName(e.target.value)} 
                placeholder="단계 이름 입력"
            />
            <div className="buttons">
                <button onClick={handleSubmit}>추가</button>
                <button onClick={onClose}>취소</button>
            </div>
        </div>
    );
}

export default CreateTaskFlow;
