import React, { useState } from 'react';
import Navbar from '../../components/Common/Navbar';
import LogoutButton from '../../components/Common/LogoutButton';
import UserInfo from '../../components/Common/UserInfo';
import axios from 'axios';

function MapUploadPage() {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [mapFile, setMapFile] = useState(null);
  const [yamlFile, setYamlFile] = useState(null);

  const handleMapFile = (e) => {
    setMapFile(e.target.files[0]);
  };

  const handleYamlFile = (e) => {
    setYamlFile(e.target.files[0]);
  };

  const handleUpload = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('file', mapFile);
    formData.append('yaml', yamlFile);
    formData.append('name', name);
    formData.append('description', description);

    const token = localStorage.getItem('token');

    if (!token) {
        alert('No token found, please log in again.');
        return;
    }

    try {
      await axios.post('http://172.30.1.40:5557/map/upload', formData, {
        headers: { 
            'Authorization': `Bearer ${token}` 
        }
      });
      alert('Map uploaded successfully');
    } catch (error) {
      console.error('Error uploading map:', error);
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', margin: '20px' }}>
      <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '20px' }}>
        <UserInfo />
        <LogoutButton />
      </header>
      <div style={{ display: 'flex' }}>
        <Navbar />
      </div>
      <h2 style={{ textAlign: 'center', margin: '20px 0' }}>Map Create</h2>
      <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        <input
          type="text"
          placeholder="Map Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <textarea
          placeholder="Map Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
          style={{ width: '300px', padding: '10px', margin: '10px 0', borderRadius: '5px', border: '1px solid #ccc' }}
        />
        <label htmlFor="map-file-upload" style={{ margin: '10px 0', color: '#333' }}>맵 파일(.pgm)을 올려주세요</label>
        <input
          id="map-file-upload"
          type="file"
          accept=".pgm"
          onChange={handleMapFile}
          required
          style={{ margin: '10px 0' }}
        />
        <label htmlFor="yaml-file-upload" style={{ margin: '10px 0', color: '#333' }}>메타데이터 파일(.yaml)을 올려주세요</label>
        <input
          id="yaml-file-upload"
          type="file"
          accept=".yaml"
          onChange={handleYamlFile}
          required
          style={{ margin: '10px 0' }}
        />
        <button type="submit" style={{ padding: '10px 20px', margin: '20px 0', borderRadius: '5px', backgroundColor: '#4CAF50', color: '#fff', border: 'none', cursor: 'pointer' }}>
          Upload Map
        </button>
      </form>
    </div>
  );
}

export default MapUploadPage;
