// src/components/MapManage/NodeConnectPopup.js

import React from 'react';
import './NodeConnectPopup.css';

function NodeConnectPopup({
  onClose,
  connectionData,
  setConnectionData,
  onSetActiveField,
  addWaypoint,
  removeWaypoint,
  handleCreateConnection,
}) {
  const { firstNode, secondNode, waypoints } = connectionData;

  // 'Set' 버튼 클릭 시 활성 필드 설정
  const handleSetClick = (field) => {
    onSetActiveField(field);
  };

  // 특정 웨이포인트 인덱스에 대한 'Set' 버튼 클릭 처리
  const handleWaypointSetClick = (index) => {
    onSetActiveField(`waypoint-${index}`);
  };

  return (
    <div className="node-connect-popup">
      <h2>노드 연결</h2>

      {/* 첫 번째 노드 설정 */}
      <div className="popup-field">
        <label>첫 번째 노드</label>
        <div className="popup-input-group">
          <input
            type="text"
            readOnly
            value={firstNode.x !== '' && firstNode.y !== '' 
              ? `X: ${firstNode.x.toFixed(2)}, Y: ${firstNode.y.toFixed(2)}`
              : ''}
            placeholder="좌표 미설정"
          />
          <button onClick={() => handleSetClick('firstNode')}>Set</button>
        </div>
      </div>

      {/* 두 번째 노드 설정 */}
      <div className="popup-field">
        <label>두 번째 노드</label>
        <div className="popup-input-group">
          <input
            type="text"
            readOnly
            value={secondNode.x !== '' && secondNode.y !== '' 
              ? `X: ${secondNode.x.toFixed(2)}, Y: ${secondNode.y.toFixed(2)}`
              : ''}
            placeholder="좌표 미설정"
          />
          <button onClick={() => handleSetClick('secondNode')}>Set</button>
        </div>
      </div>

      {/* 경유지 설정 */}
      <div className="popup-field">
        <label>경유지</label>
        <div className="waypoint-controls">
          <button onClick={addWaypoint} className="add-waypoint-button">+</button>
          <button onClick={removeWaypoint} className="remove-waypoint-button" disabled={waypoints.length === 0}>-</button>
        </div>

        {waypoints.map((wp, index) => (
          <div className="popup-input-group" key={index}>
            <input
              type="text"
              readOnly
              value={wp.x !== '' && wp.y !== '' 
                ? `X: ${wp.x.toFixed(2)}, Y: ${wp.y.toFixed(2)}`
                : ''}
              placeholder={`웨이포인트 ${index + 1} 미설정`}
            />
            <button onClick={() => handleWaypointSetClick(index)}>Set</button>
          </div>
        ))}
      </div>

      {/* 생성 및 취소 버튼 */}
      <div className="popup-buttons">
        <button onClick={handleCreateConnection} className="create-button">생성</button>
        <button onClick={onClose} className="cancel-button">취소</button>
      </div>
    </div>
  );
}

export default NodeConnectPopup;
